const canvas = document.getElementById('gameCanvas');
const ctx = canvas.getContext('2d');

// Game variables
let score = 0;
let lives = 3;

// Set canvas dimensions
canvas.width = 800;
canvas.height = 600;

// Brick properties
const brickInfo = {
    width: 75,
    height: 20,
    padding: 10,
    offsetX: 45,
    offsetY: 60,
    rows: 5,
    cols: 9
};

// Create bricks
const bricks = [];
for (let c = 0; c < brickInfo.cols; c++) {
    bricks[c] = [];
    for (let r = 0; r < brickInfo.rows; r++) {
        const x = c * (brickInfo.width + brickInfo.padding) + brickInfo.offsetX;
        const y = r * (brickInfo.height + brickInfo.padding) + brickInfo.offsetY;
        bricks[c][r] = { x, y, visible: true };
    }
}

// Draw bricks
function drawBricks() {
    bricks.forEach(col => {
        col.forEach(brick => {
            if (brick.visible) {
                ctx.beginPath();
                ctx.rect(brick.x, brick.y, brickInfo.width, brickInfo.height);
                ctx.fillStyle = '#0ff';
                ctx.shadowBlur = 10;
                ctx.shadowColor = '#0ff';
                ctx.fill();
                ctx.closePath();
            }
        });
    });
}

// Ball properties
const ball = {
    x: canvas.width / 2,
    y: canvas.height - 30,
    radius: 10,
    speed: 4,
    dx: 4,
    dy: -4
};

// Draw ball
function drawBall() {
    ctx.beginPath();
    ctx.arc(ball.x, ball.y, ball.radius, 0, Math.PI * 2);
    ctx.fillStyle = '#ff00ff';
    ctx.shadowBlur = 20;
    ctx.shadowColor = '#ff00ff';
    ctx.fill();
    ctx.closePath();
}

function increaseScore() {
    score++;

    if (score === brickInfo.rows * brickInfo.cols) {
        alert('YOU WIN, CONGRATULATIONS!');
        document.location.reload();
    }
}

// Move ball
function moveBall() {
    ball.x += ball.dx;
    ball.y += ball.dy;

    // Wall collision (right/left)
    if (ball.x + ball.radius > canvas.width || ball.x - ball.radius < 0) {
        ball.dx *= -1;
    }

    // Wall collision (top)
    if (ball.y - ball.radius < 0) {
        ball.dy *= -1;
    }

    // Wall collision (bottom)
    if (ball.y + ball.radius > canvas.height) {
        lives--;
        if (lives === 0) {
            alert('GAME OVER');
            document.location.reload();
        } else {
            ball.x = canvas.width / 2;
            ball.y = canvas.height - 30;
            ball.dx = 4;
            ball.dy = -4;
            paddle.x = canvas.width / 2 - 50;
        }
    }

    // Paddle collision
    if (
        ball.y + ball.radius > paddle.y &&
        ball.x > paddle.x &&
        ball.x < paddle.x + paddle.width
    ) {
        ball.dy = -ball.speed;
    }

    // Brick collision
    bricks.forEach(col => {
        col.forEach(brick => {
            if (brick.visible) {
                if (
                    ball.x > brick.x &&
                    ball.x < brick.x + brickInfo.width &&
                    ball.y > brick.y &&
                    ball.y < brick.y + brickInfo.height
                ) {
                    ball.dy *= -1;
                    brick.visible = false;
                    increaseScore();
                }
            }
        });
    });
}

// Paddle properties
const paddle = {
    x: canvas.width / 2 - 50,
    y: canvas.height - 20,
    width: 100,
    height: 10,
    speed: 8,
    dx: 0
};

// Draw paddle
function drawPaddle() {
    ctx.beginPath();
    ctx.rect(paddle.x, paddle.y, paddle.width, paddle.height);
    ctx.fillStyle = '#0ff';
    ctx.shadowBlur = 10;
    ctx.shadowColor = '#0ff';
    ctx.fill();
    ctx.closePath();
}

// Move paddle
function movePaddle() {
    paddle.x += paddle.dx;

    // Wall detection
    if (paddle.x + paddle.width > canvas.width) {
        paddle.x = canvas.width - paddle.width;
    }

    if (paddle.x < 0) {
        paddle.x = 0;
    }
}

// Keyboard event handlers
document.addEventListener('keydown', (e) => {
    if (e.key === 'Right' || e.key === 'ArrowRight') {
        paddle.dx = paddle.speed;
    } else if (e.key === 'Left' || e.key === 'ArrowLeft') {
        paddle.dx = -paddle.speed;
    }
});

document.addEventListener('keyup', (e) => {
    if (
        e.key === 'Right' ||
        e.key === 'ArrowRight' ||
        e.key === 'Left' ||
        e.key === 'ArrowLeft'
    ) {
        paddle.dx = 0;
    }
});

function drawScore() {
    ctx.font = '20px Arial';
    ctx.fillStyle = '#fff';
    ctx.fillText(`Score: ${score}`, canvas.width - 100, 30);
}

function drawLives() {
    ctx.font = '20px Arial';
    ctx.fillStyle = '#fff';
    ctx.fillText(`Lives: ${lives}`, 20, 30);
}

function draw() {
    // Clear canvas
    ctx.clearRect(0, 0, canvas.width, canvas.height);

    drawPaddle();
    drawBall();
    drawBricks();
    drawScore();
    drawLives();
}

function update() {
    movePaddle();
    moveBall();

    draw();
    requestAnimationFrame(update);
}

update();

