const canvas = document.getElementById('gameCanvas');
const ctx = canvas.getContext('2d');

// --- Game State and Elements ---
let score;
let lives;
let particles = [];
let brickInfo;
let bricks;
let ball;
let paddle;

// --- Game Initialization and Reset ---
function resetGame() {
    score = 0;
    lives = 3;
    particles = [];

    // Paddle properties
    paddle = {
        width: canvas.width / 7,
        height: 12,
        speed: 8,
        dx: 0
    };
    paddle.x = (canvas.width / 2) - (paddle.width / 2);
    paddle.y = canvas.height - 25;

    // Ball properties
    ball = {
        radius: 8,
        speed: canvas.width / 200,
    };
    ball.x = canvas.width / 2;
    ball.y = paddle.y - ball.radius - 5;
    ball.dx = ball.speed;
    ball.dy = -ball.speed;

    // Brick properties
    brickInfo = {
        rows: 5,
        cols: 9,
        padding: 5,
        offsetY: canvas.height / 12,
    };
    const totalPaddingWidth = brickInfo.padding * (brickInfo.cols - 1);
    const totalBrickWidth = canvas.width - totalPaddingWidth;
    brickInfo.width = totalBrickWidth / brickInfo.cols;
    brickInfo.height = canvas.height / 25;
    brickInfo.offsetX = 0;

    // Create bricks
    bricks = [];
    for (let c = 0; c < brickInfo.cols; c++) {
        bricks[c] = [];
        for (let r = 0; r < brickInfo.rows; r++) {
            const x = c * (brickInfo.width + brickInfo.padding) + brickInfo.offsetX;
            const y = r * (brickInfo.height + brickInfo.padding) + brickInfo.offsetY;
            bricks[c][r] = { x, y, visible: true };
        }
    }
}

// --- Canvas and Responsiveness ---
function resizeCanvas() {
    canvas.width = Math.min(window.innerWidth * 0.9, 1000);
    canvas.height = canvas.width * (9 / 16);
    resetGame();
}
window.addEventListener('resize', resizeCanvas);


// --- Particle System ---
function createParticles(x, y) {
    for (let i = 0; i < 15; i++) {
        particles.push({
            x, y,
            radius: Math.random() * 3 + 1,
            color: '#0ff',
            velocityX: (Math.random() - 0.5) * 6,
            velocityY: (Math.random() - 0.5) * 6,
            alpha: 1
        });
    }
}

function drawParticles() {
    particles.forEach(p => {
        ctx.save();
        ctx.globalAlpha = p.alpha;
        ctx.beginPath();
        ctx.arc(p.x, p.y, p.radius, 0, Math.PI * 2);
        ctx.fillStyle = p.color;
        ctx.fill();
        ctx.restore();
    });
}

function updateParticles() {
    particles.forEach((p, index) => {
        p.x += p.velocityX;
        p.y += p.velocityY;
        p.alpha -= 0.02;
        if (p.alpha <= 0) particles.splice(index, 1);
    });
}


// --- Game Logic ---
function increaseScore() {
    score++;
    if (score === brickInfo.rows * brickInfo.cols) {
        alert('YOU WIN, CONGRATULATIONS!');
        resetGame();
    }
}

function moveBall() {
    ball.x += ball.dx;
    ball.y += ball.dy;

    // Wall collision
    if (ball.x + ball.radius > canvas.width || ball.x - ball.radius < 0) ball.dx *= -1;
    if (ball.y - ball.radius < 0) ball.dy *= -1;
    if (ball.y + ball.radius > canvas.height) {
        lives--;
        if (lives === 0) {
            alert('GAME OVER');
            resetGame();
        } else {
            ball.x = canvas.width / 2;
            ball.y = paddle.y - 30;
            ball.dx = ball.speed;
            ball.dy = -ball.speed;
        }
    }

    // Paddle collision
    if (ball.y + ball.radius > paddle.y && ball.x > paddle.x && ball.x < paddle.x + paddle.width) {
        ball.dy = -ball.speed;
    }

    // Brick collision
    bricks.forEach(col => col.forEach(brick => {
        if (brick.visible && ball.x > brick.x && ball.x < brick.x + brickInfo.width && ball.y > brick.y && ball.y < brick.y + brickInfo.height) {
            ball.dy *= -1;
            brick.visible = false;
            increaseScore();
            createParticles(ball.x, ball.y);
        }
    }));
}

function movePaddle() {
    paddle.x += paddle.dx;
    if (paddle.x < 0) paddle.x = 0;
    if (paddle.x + paddle.width > canvas.width) paddle.x = canvas.width - paddle.width;
}


// --- Drawing Functions ---
function drawPaddle() {
    ctx.beginPath();
    ctx.rect(paddle.x, paddle.y, paddle.width, paddle.height);
    ctx.fillStyle = '#0ff';
    ctx.shadowBlur = 15;
    ctx.shadowColor = '#0ff';
    ctx.fill();
    ctx.closePath();
}

function drawBall() {
    ctx.beginPath();
    ctx.arc(ball.x, ball.y, ball.radius, 0, Math.PI * 2);
    ctx.fillStyle = '#ff00ff';
    ctx.shadowBlur = 20;
    ctx.shadowColor = '#ff00ff';
    ctx.fill();
    ctx.closePath();
}

function drawBricks() {
    bricks.forEach(col => col.forEach(brick => {
        if (brick.visible) {
            ctx.beginPath();
            ctx.rect(brick.x, brick.y, brickInfo.width, brickInfo.height);
            ctx.fillStyle = '#0ff';
            ctx.shadowBlur = 10;
            ctx.shadowColor = '#0ff';
            ctx.fill();
            ctx.closePath();
        }
    }));
}

function drawUI() {
    const fontSize = canvas.width / 40;
    ctx.font = `${fontSize}px Orbitron`;
    ctx.fillStyle = '#fff';
    ctx.fillText(`Score: ${score}`, canvas.width - (fontSize * 7), fontSize * 1.5);
    ctx.fillText(`Lives: ${lives}`, fontSize, fontSize * 1.5);
}

// --- Main Loop ---
function draw() {
    ctx.fillStyle = 'rgba(0, 0, 0, 0.25)';
    ctx.fillRect(0, 0, canvas.width, canvas.height);

    drawPaddle();
    drawBall();
    drawBricks();
    drawUI();
    drawParticles();
}

function update() {
    movePaddle();
    moveBall();
    updateParticles();
    draw();
    requestAnimationFrame(update);
}


// --- Event Handlers ---
document.addEventListener('keydown', e => {
    if (e.key === 'Right' || e.key === 'ArrowRight') paddle.dx = paddle.speed;
    else if (e.key === 'Left' || e.key === 'ArrowLeft') paddle.dx = -paddle.speed;
});

document.addEventListener('keyup', e => {
    if (['Right', 'ArrowRight', 'Left', 'ArrowLeft'].includes(e.key)) paddle.dx = 0;
});


// --- Start Game ---
resizeCanvas();
update();

