const { ObjectId } = require('mongodb');
const { getDB } = require('../config/mongodb');

class Todo {
  static async create(userId, title, description = '') {
    const db = getDB();
    const todo = {
      userId: parseInt(userId),
      title,
      description,
      completed: false,
      createdAt: new Date(),
      updatedAt: new Date()
    };

    const result = await db.collection('todos').insertOne(todo);
    return { _id: result.insertedId, ...todo };
  }

  static async findByUserId(userId) {
    const db = getDB();
    const todos = await db.collection('todos')
      .find({ userId: parseInt(userId) })
      .sort({ createdAt: -1 })
      .toArray();

    return todos;
  }

  static async findById(id, userId) {
    const db = getDB();
    const todo = await db.collection('todos').findOne({
      _id: new ObjectId(id),
      userId: parseInt(userId)
    });

    return todo;
  }

  static async update(id, userId, updates) {
    const db = getDB();
    const updateData = {
      ...updates,
      updatedAt: new Date()
    };

    const result = await db.collection('todos').findOneAndUpdate(
      { _id: new ObjectId(id), userId: parseInt(userId) },
      { $set: updateData },
      { returnDocument: 'after' }
    );

    return result.value;
  }

  static async delete(id, userId) {
    const db = getDB();
    const result = await db.collection('todos').deleteOne({
      _id: new ObjectId(id),
      userId: parseInt(userId)
    });

    return result.deletedCount > 0;
  }

  static async toggleComplete(id, userId) {
    const db = getDB();
    const todo = await this.findById(id, userId);

    if (!todo) return null;

    return await this.update(id, userId, { completed: !todo.completed });
  }
}

module.exports = Todo;

