const express = require('express');
const Todo = require('../models/Todo');
const { authenticateToken } = require('../middleware/auth');

const router = express.Router();

router.use(authenticateToken);

router.get('/', async (req, res) => {
  try {
    const todos = await Todo.findByUserId(req.user.id);
    res.json({ todos });
  } catch (error) {
    console.error('Get todos error:', error);
    res.status(500).json({ message: 'Failed to fetch todos' });
  }
});

router.post('/', async (req, res) => {
  try {
    const { title, description } = req.body;

    if (!title || title.trim().length === 0) {
      return res.status(400).json({ message: 'Title is required' });
    }

    const todo = await Todo.create(req.user.id, title.trim(), description?.trim() || '');

    res.status(201).json({
      message: 'Todo created successfully',
      todo
    });
  } catch (error) {
    console.error('Create todo error:', error);
    res.status(500).json({ message: 'Failed to create todo' });
  }
});

router.get('/:id', async (req, res) => {
  try {
    const todo = await Todo.findById(req.params.id, req.user.id);

    if (!todo) {
      return res.status(404).json({ message: 'Todo not found' });
    }

    res.json({ todo });
  } catch (error) {
    console.error('Get todo error:', error);
    res.status(500).json({ message: 'Failed to fetch todo' });
  }
});

router.put('/:id', async (req, res) => {
  try {
    const { title, description, completed } = req.body;

    const updates = {};
    if (title !== undefined) updates.title = title.trim();
    if (description !== undefined) updates.description = description.trim();
    if (completed !== undefined) updates.completed = Boolean(completed);

    if (Object.keys(updates).length === 0) {
      return res.status(400).json({ message: 'No valid updates provided' });
    }

    const todo = await Todo.update(req.params.id, req.user.id, updates);

    if (!todo) {
      return res.status(404).json({ message: 'Todo not found' });
    }

    res.json({
      message: 'Todo updated successfully',
      todo
    });
  } catch (error) {
    console.error('Update todo error:', error);
    res.status(500).json({ message: 'Failed to update todo' });
  }
});

router.patch('/:id/toggle', async (req, res) => {
  try {
    const todo = await Todo.toggleComplete(req.params.id, req.user.id);

    if (!todo) {
      return res.status(404).json({ message: 'Todo not found' });
    }

    res.json({
      message: 'Todo status updated successfully',
      todo
    });
  } catch (error) {
    console.error('Toggle todo error:', error);
    res.status(500).json({ message: 'Failed to toggle todo status' });
  }
});

router.delete('/:id', async (req, res) => {
  try {
    const deleted = await Todo.delete(req.params.id, req.user.id);

    if (!deleted) {
      return res.status(404).json({ message: 'Todo not found' });
    }

    res.json({ message: 'Todo deleted successfully' });
  } catch (error) {
    console.error('Delete todo error:', error);
    res.status(500).json({ message: 'Failed to delete todo' });
  }
});

module.exports = router;

