import React, { useState } from 'react';
import { motion } from 'framer-motion';
import { Check, Edit2, Trash2, Save, X } from 'lucide-react';

const TodoCard = ({ todo, onToggle, onUpdate, onDelete }) => {
  const [isEditing, setIsEditing] = useState(false);
  const [editData, setEditData] = useState({
    title: todo.title,
    description: todo.description || '',
  });

  const handleSave = async () => {
    const result = await onUpdate(todo._id, editData);
    if (result.success) {
      setIsEditing(false);
    }
  };

  const handleCancel = () => {
    setEditData({
      title: todo.title,
      description: todo.description || '',
    });
    setIsEditing(false);
  };

  const handleToggle = () => {
    onToggle(todo._id);
  };

  const handleDelete = () => {
    if (window.confirm('Are you sure you want to delete this todo?')) {
      onDelete(todo._id);
    }
  };

  return (
    <motion.div
      layout
      initial={{ opacity: 0, y: 20, scale: 0.9 }}
      animate={{ opacity: 1, y: 0, scale: 1 }}
      exit={{ opacity: 0, y: -20, scale: 0.9 }}
      whileHover={{ y: -4 }}
      className={`glass card relative overflow-hidden ${
        todo.completed ? 'opacity-75' : ''
      }`}
    >
      <div className="absolute inset-0 bg-gradient-to-r from-purple-500/10 to-pink-500/10 opacity-0 hover:opacity-100 transition-opacity duration-300" />

      <div className="relative">
        {isEditing ? (
          <div className="space-y-4">
            <input
              type="text"
              value={editData.title}
              onChange={(e) => setEditData(prev => ({ ...prev, title: e.target.value }))}
              className="form-input w-full"
              placeholder="Todo title"
            />
            <textarea
              value={editData.description}
              onChange={(e) => setEditData(prev => ({ ...prev, description: e.target.value }))}
              className="form-input w-full resize-none"
              rows="3"
              placeholder="Description (optional)"
            />
            <div className="flex justify-end gap-2">
              <motion.button
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
                onClick={handleCancel}
                className="btn bg-gray-600 hover:bg-gray-700 text-white px-3 py-2"
              >
                <X size={16} />
              </motion.button>
              <motion.button
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
                onClick={handleSave}
                className="btn btn-primary px-3 py-2"
              >
                <Save size={16} />
              </motion.button>
            </div>
          </div>
        ) : (
          <>
            <div className="flex items-start gap-3 mb-4">
              <motion.button
                whileHover={{ scale: 1.1 }}
                whileTap={{ scale: 0.9 }}
                onClick={handleToggle}
                className={`flex-shrink-0 w-6 h-6 rounded-full border-2 flex items-center justify-center transition-all duration-200 ${
                  todo.completed
                    ? 'bg-green-500 border-green-500'
                    : 'border-gray-400 hover:border-green-400'
                }`}
              >
                {todo.completed && (
                  <motion.div
                    initial={{ scale: 0 }}
                    animate={{ scale: 1 }}
                    transition={{ type: "spring", stiffness: 300 }}
                  >
                    <Check size={14} className="text-white" />
                  </motion.div>
                )}
              </motion.button>

              <div className="flex-1 min-w-0">
                <h3
                  className={`text-lg font-semibold mb-1 transition-all duration-200 ${
                    todo.completed
                      ? 'line-through text-gray-400'
                      : 'text-white'
                  }`}
                >
                  {todo.title}
                </h3>
                {todo.description && (
                  <p
                    className={`text-sm transition-all duration-200 ${
                      todo.completed
                        ? 'line-through text-gray-500'
                        : 'text-gray-300'
                    }`}
                  >
                    {todo.description}
                  </p>
                )}
              </div>
            </div>

            <div className="flex items-center justify-between text-xs text-gray-400">
              <span>
                Created: {new Date(todo.createdAt).toLocaleDateString()}
              </span>

              <div className="flex gap-2">
                <motion.button
                  whileHover={{ scale: 1.1, color: '#60a5fa' }}
                  whileTap={{ scale: 0.9 }}
                  onClick={() => setIsEditing(true)}
                  className="p-1.5 rounded-lg hover:bg-blue-500/20 transition-colors"
                >
                  <Edit2 size={14} />
                </motion.button>

                <motion.button
                  whileHover={{ scale: 1.1, color: '#f87171' }}
                  whileTap={{ scale: 0.9 }}
                  onClick={handleDelete}
                  className="p-1.5 rounded-lg hover:bg-red-500/20 transition-colors"
                >
                  <Trash2 size={14} />
                </motion.button>
              </div>
            </div>
          </>
        )}
      </div>
    </motion.div>
  );
};

export default TodoCard;

