import React from 'react';

const AnimeBackground = ({ className = '' }) => {
  return (
    <div 
      className={`fixed inset-0 -z-10 ${className}`}
      style={{ 
        background: 'linear-gradient(135deg, #0c0c0c 0%, #1a1a2e 50%, #16213e 100%)',
        overflow: 'hidden'
      }}
    >
      {/* Animated background elements using CSS */}
      <div className="absolute inset-0">
        {/* Floating orbs */}
        <div 
          className="absolute w-32 h-32 rounded-full opacity-20 floating"
          style={{
            background: 'radial-gradient(circle, #667eea 0%, transparent 70%)',
            top: '20%',
            left: '10%',
            animationDelay: '0s'
          }}
        />
        <div 
          className="absolute w-24 h-24 rounded-full opacity-30 floating"
          style={{
            background: 'radial-gradient(circle, #f093fb 0%, transparent 70%)',
            top: '60%',
            right: '15%',
            animationDelay: '2s'
          }}
        />
        <div 
          className="absolute w-40 h-40 rounded-full opacity-15 floating"
          style={{
            background: 'radial-gradient(circle, #4facfe 0%, transparent 70%)',
            top: '10%',
            right: '20%',
            animationDelay: '4s'
          }}
        />
        <div 
          className="absolute w-28 h-28 rounded-full opacity-25 floating"
          style={{
            background: 'radial-gradient(circle, #43e97b 0%, transparent 70%)',
            bottom: '20%',
            left: '20%',
            animationDelay: '1s'
          }}
        />

        {/* Particle effect with CSS */}
        <div className="absolute inset-0">
          {Array.from({ length: 50 }).map((_, i) => (
            <div
              key={i}
              className="absolute w-1 h-1 bg-white rounded-full opacity-40 pulse"
              style={{
                left: `${Math.random() * 100}%`,
                top: `${Math.random() * 100}%`,
                animationDelay: `${Math.random() * 3}s`,
                animationDuration: `${2 + Math.random() * 2}s`
              }}
            />
          ))}
        </div>
      </div>

      <div className="absolute inset-0 bg-gradient-to-br from-purple-900/20 via-transparent to-blue-900/20" />
    </div>
  );
};

export default AnimeBackground;

