import { useState, useEffect } from 'react';
import { todoAPI } from '../utils/api';
import toast from 'react-hot-toast';

export const useTodos = () => {
  const [todos, setTodos] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const fetchTodos = async () => {
    try {
      setLoading(true);
      setError(null);
      const response = await todoAPI.getTodos();
      setTodos(response.data.todos || []);
    } catch (error) {
      console.error('Failed to fetch todos:', error);
      setError('Failed to load todos');
      toast.error('Failed to load todos');
    } finally {
      setLoading(false);
    }
  };

  const createTodo = async (todoData) => {
    try {
      const response = await todoAPI.createTodo(todoData);
      const newTodo = response.data.todo;
      setTodos(prev => [newTodo, ...prev]);
      toast.success('Todo created! ✨');
      return { success: true, todo: newTodo };
    } catch (error) {
      const message = error.response?.data?.message || 'Failed to create todo';
      toast.error(message);
      return { success: false, message };
    }
  };

  const updateTodo = async (id, updates) => {
    try {
      const response = await todoAPI.updateTodo(id, updates);
      const updatedTodo = response.data.todo;
      setTodos(prev => prev.map(todo => 
        todo._id === id ? updatedTodo : todo
      ));
      toast.success('Todo updated! 📝');
      return { success: true, todo: updatedTodo };
    } catch (error) {
      const message = error.response?.data?.message || 'Failed to update todo';
      toast.error(message);
      return { success: false, message };
    }
  };

  const toggleTodo = async (id) => {
    try {
      const response = await todoAPI.toggleTodo(id);
      const updatedTodo = response.data.todo;
      setTodos(prev => prev.map(todo => 
        todo._id === id ? updatedTodo : todo
      ));
      toast.success(updatedTodo.completed ? 'Todo completed! 🎉' : 'Todo reopened! 📝');
      return { success: true, todo: updatedTodo };
    } catch (error) {
      const message = error.response?.data?.message || 'Failed to toggle todo';
      toast.error(message);
      return { success: false, message };
    }
  };

  const deleteTodo = async (id) => {
    try {
      await todoAPI.deleteTodo(id);
      setTodos(prev => prev.filter(todo => todo._id !== id));
      toast.success('Todo deleted! 🗑️');
      return { success: true };
    } catch (error) {
      const message = error.response?.data?.message || 'Failed to delete todo';
      toast.error(message);
      return { success: false, message };
    }
  };

  useEffect(() => {
    fetchTodos();
  }, []);

  const stats = {
    total: todos.length,
    completed: todos.filter(todo => todo.completed).length,
    pending: todos.filter(todo => !todo.completed).length,
  };

  return {
    todos,
    loading,
    error,
    stats,
    fetchTodos,
    createTodo,
    updateTodo,
    toggleTodo,
    deleteTodo,
  };
};

