        note._id === id ? updatedNote : note
      ));
      toast.success(updatedNote.completed ? 'Note completed! 🎉' : 'Note reopened! 📝');
      return { success: true, note: updatedNote };
    } catch (error) {
      const message = error.response?.data?.message || 'Failed to toggle note';
      toast.error(message);
      return { success: false, message };
    }
  };

  const deleteNote = async (id) => {
    try {
      await notesAPI.deleteNote(id);
      setNotes(prev => prev.filter(note => note._id !== id));
      toast.success('Note deleted! 🗑️');
      return { success: true };
    } catch (error) {
      const message = error.response?.data?.message || 'Failed to delete note';
      toast.error(message);
      return { success: false, message };
    }
  };

  useEffect(() => {
    fetchNotes();
  }, []);

  const stats = {
    total: notes.length,
    completed: notes.filter(note => note.completed).length,
    pending: notes.filter(note => !note.completed).length,
  };

  return {
    notes,
    loading,
    error,
    stats,
    fetchNotes,
    createNote,
    updateNote,
    toggleNote,
    deleteNote,
  };
};

