                  {filteredNotes.map((note) => (
                    <NoteCard
                      key={note._id}
                      note={note}
                      onUpdate={updateNote}
                      onDelete={deleteNote}
                      onToggle={toggleNote}
                    />
                  ))}
                </AnimatePresence>
              </div>
            )}
          </div>
        </main>
      </div>

      {/* Mobile Sidebar Overlay */}
      {sidebarOpen && (
        <motion.div
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          exit={{ opacity: 0 }}
          onClick={() => setSidebarOpen(false)}
          className="fixed inset-0 bg-black/50 z-30 lg:hidden"
        />
      )}
    </div>
  );
};

export default Dashboard;

