import React, { useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { LogOut, User, CheckCircle, Circle, ListTodo, Menu, X } from 'lucide-react';
import { useAuth } from '../hooks/useAuth';
import { useTodos } from '../hooks/useTodos';
import AnimeBackground from '../components/AnimeBackground';
import TodoCard from '../components/TodoCard';
import AddTodoForm from '../components/AddTodoForm';

const Dashboard = () => {
  const { user, signout } = useAuth();
  const { todos, loading, stats, createTodo, updateTodo, toggleTodo, deleteTodo } = useTodos();
  const [filter, setFilter] = useState('all');
  const [sidebarOpen, setSidebarOpen] = useState(false);

  const filteredTodos = todos.filter(todo => {
    switch (filter) {
      case 'pending':
        return !todo.completed;
      case 'completed':
        return todo.completed;
      default:
        return true;
    }
  });

  const FilterButton = ({ type, label, icon: Icon, count }) => (
    <motion.button
      whileHover={{ scale: 1.05 }}
      whileTap={{ scale: 0.95 }}
      onClick={() => setFilter(type)}
      className={`flex items-center justify-between w-full p-3 rounded-lg transition-all duration-200 ${
        filter === type
          ? 'bg-gradient-to-r from-purple-500 to-pink-500 text-white'
          : 'text-gray-300 hover:bg-white/10'
      }`}
    >
      <div className="flex items-center gap-3">
        <Icon size={18} />
        <span className="font-medium">{label}</span>
      </div>
      <span className={`px-2 py-1 rounded-full text-xs ${
        filter === type ? 'bg-white/20' : 'bg-gray-700'
      }`}>
        {count}
      </span>
    </motion.button>
  );

  return (
    <div className="min-h-screen">
      <AnimeBackground />

      <div className="relative z-10">
        <header className="glass border-b border-white/10 sticky top-0 z-20">
