      whileHover={{ y: -2 }}
      className={`note-card ${note.completed ? 'opacity-75' : ''}`}
      onClick={!isEditing ? handleEdit : undefined}
    >
      {isEditing ? (
        <div className="space-y-3">
          <input
            ref={titleRef}
            type="text"
            value={editData.title}
            onChange={(e) => setEditData(prev => ({ ...prev, title: e.target.value }))}
            onKeyDown={handleKeyDown}
            className="w-full bg-transparent border-none outline-none text-lg font-semibold text-white placeholder-gray-400 resize-none"
            placeholder="Title"
            maxLength={100}
          />

          <textarea
            ref={contentRef}
            value={editData.description}
            onChange={(e) => setEditData(prev => ({ ...prev, description: e.target.value }))}
            onKeyDown={handleKeyDown}
            className="w-full bg-transparent border-none outline-none text-sm text-gray-300 placeholder-gray-400 resize-none min-h-[60px]"
            placeholder="Take a note..."
            rows={3}
            style={{ 
              height: 'auto',
              minHeight: '60px',
              maxHeight: '200px',
              overflow: 'auto'
            }}
            onInput={(e) => {
              e.target.style.height = 'auto';
              e.target.style.height = e.target.scrollHeight + 'px';
            }}
          />

          <div className="flex justify-between items-center mt-4">
            <div className="flex gap-2">
              <motion.button
                whileHover={{ scale: 1.1 }}
                whileTap={{ scale: 0.9 }}
                onClick={(e) => {
                  e.stopPropagation();
                  handleSave();
                }}
                className="p-2 rounded-full hover:bg-green-500/20 text-green-400 transition-colors"
                title="Save (Ctrl+Enter)"
              >
                <Check size={16} />
              </motion.button>

              <motion.button
                whileHover={{ scale: 1.1 }}
                whileTap={{ scale: 0.9 }}
                onClick={(e) => {
                  e.stopPropagation();
                  handleCancel();
                }}
                className="p-2 rounded-full hover:bg-red-500/20 text-red-400 transition-colors"
                title="Cancel (Esc)"
              >
                <X size={16} />
              </motion.button>
            </div>

            <span className="text-xs text-gray-500">
              Ctrl+Enter to save, Esc to cancel
            </span>
          </div>
        </div>
      ) : (
        <div>
          {(note.title || note.description) ? (
            <>
              {note.title && (
                <h3 className={`note-card-title ${note.completed ? 'line-through' : ''}`}>
                  {note.title}
                </h3>
              )}

              {note.description && (
                <p className={`note-card-content ${note.completed ? 'line-through' : ''}`}>
                  {note.description}
                </p>
              )}

              <div className="note-card-date">
                {formatDate(note.updatedAt || note.createdAt)}
              </div>

              <div className="note-card-actions">
                <motion.button
                  whileHover={{ scale: 1.1 }}
                  whileTap={{ scale: 0.9 }}
                  onClick={(e) => {
                    e.stopPropagation();
                    handleToggleComplete();
                  }}
                  className={`p-2 rounded-full transition-colors ${
                    note.completed 
                      ? 'hover:bg-yellow-500/20 text-yellow-400' 
                      : 'hover:bg-green-500/20 text-green-400'
                  }`}
                  title={note.completed ? 'Mark as incomplete' : 'Mark as complete'}
                >
                  <Check size={16} />
                </motion.button>

                <motion.button
                  whileHover={{ scale: 1.1 }}
                  whileTap={{ scale: 0.9 }}
                  onClick={(e) => {
                    e.stopPropagation();
                    handleEdit();
                  }}
                  className="p-2 rounded-full hover:bg-blue-500/20 text-blue-400 transition-colors"
                  title="Edit note"
                >
                  <Edit2 size={16} />
                </motion.button>

                <motion.button
                  whileHover={{ scale: 1.1 }}
                  whileTap={{ scale: 0.9 }}
                  onClick={(e) => {
                    e.stopPropagation();
                    handleDelete();
                  }}
                  className="p-2 rounded-full hover:bg-red-500/20 text-red-400 transition-colors"
                  title="Delete note"
                >
                  <Trash2 size={16} />
                </motion.button>
              </div>
            </>
          ) : (
            <div className="flex items-center justify-center h-20 text-gray-500">
              <span>Empty note</span>
            </div>
          )}
        </div>
      )}
    </motion.div>
  );
};

export default NoteCard;

