import React, { useState, useRef } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { Plus, Check, X } from 'lucide-react';

const AddNoteForm = ({ onAdd }) => {
  const [isExpanded, setIsExpanded] = useState(false);
  const [formData, setFormData] = useState({
    title: '',
    description: '',
  });
  const [loading, setLoading] = useState(false);
  const titleRef = useRef(null);
  const contentRef = useRef(null);

  const handleExpand = () => {
    setIsExpanded(true);
    setTimeout(() => {
      if (contentRef.current) {
        contentRef.current.focus();
      }
    }, 100);
  };

  const handleCollapse = () => {
    if (!formData.title.trim() && !formData.description.trim()) {
      setIsExpanded(false);
      return;
    }

    handleSave();
  };

  const handleSave = async () => {
    if (!formData.title.trim() && !formData.description.trim()) {
      setIsExpanded(false);
      return;
    }

    setLoading(true);
    const result = await onAdd({
      title: formData.title.trim(),
      description: formData.description.trim(),
    });

    if (result.success) {
      setFormData({ title: '', description: '' });
      setIsExpanded(false);
    }

    setLoading(false);
  };

  const handleCancel = () => {
    setFormData({ title: '', description: '' });
    setIsExpanded(false);
  };

  const handleKeyDown = (e) => {
    if (e.key === 'Enter' && e.ctrlKey) {
      handleSave();
    } else if (e.key === 'Escape') {
      handleCancel();
    }
  };

  const handleClickOutside = (e) => {
    if (e.target.closest('.add-note-form')) return;
    handleCollapse();
  };

  React.useEffect(() => {
    if (isExpanded) {
      document.addEventListener('click', handleClickOutside);
      return () => document.removeEventListener('click', handleClickOutside);
    }
  }, [isExpanded]);

  return (
    <div className="w-full max-w-2xl mx-auto mb-8">
      <motion.div
        initial={false}
        animate={{
          height: isExpanded ? 'auto' : '48px',
        }}
        transition={{ duration: 0.2, ease: 'easeInOut' }}
        className="add-note-form card cursor-text overflow-hidden"
        onClick={!isExpanded ? handleExpand : undefined}
      >
        <AnimatePresence mode="wait">
          {!isExpanded ? (
            <motion.div
              key="collapsed"
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              exit={{ opacity: 0 }}
              className="flex items-center gap-4 p-4"
            >
              <Plus className="w-5 h-5 text-gray-400" />
              <span className="text-gray-400 text-lg">Take a note...</span>
            </motion.div>
          ) : (
            <motion.div
              key="expanded"
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              exit={{ opacity: 0 }}
              className="p-4 space-y-4"
            >
              <input
                ref={titleRef}
                type="text"
                value={formData.title}
                onChange={(e) => setFormData(prev => ({ ...prev, title: e.target.value }))}
                onKeyDown={handleKeyDown}
                className="w-full bg-transparent border-none outline-none text-lg font-semibold text-white placeholder-gray-400"
                placeholder="Title"
                maxLength={100}
              />

              <textarea
                ref={contentRef}
                value={formData.description}
                onChange={(e) => setFormData(prev => ({ ...prev, description: e.target.value }))}
                onKeyDown={handleKeyDown}
                className="w-full bg-transparent border-none outline-none text-sm text-gray-300 placeholder-gray-400 resize-none min-h-[80px]"
                placeholder="Take a note..."
                rows={4}
                style={{ 
                  height: 'auto',
                  minHeight: '80px',
                  maxHeight: '200px',
                  overflow: 'auto'
                }}
                onInput={(e) => {
                  e.target.style.height = 'auto';
                  e.target.style.height = e.target.scrollHeight + 'px';
                }}
              />

              <div className="flex justify-between items-center">
                <div className="flex gap-2">
                  <motion.button
                    whileHover={{ scale: 1.05 }}
                    whileTap={{ scale: 0.95 }}
                    onClick={handleSave}
                    disabled={loading}
                    className="btn btn-primary btn-sm"
                  >
                    {loading ? (
                      <motion.div
                        animate={{ rotate: 360 }}
                        transition={{ duration: 1, repeat: Infinity, ease: "linear" }}
                        className="w-4 h-4 border-2 border-white border-t-transparent rounded-full"
                      />
                    ) : (
                      <>
                        <Check size={14} />
                        Save
                      </>
                    )}
                  </motion.button>

                  <motion.button
                    whileHover={{ scale: 1.05 }}
                    whileTap={{ scale: 0.95 }}
                    onClick={handleCancel}
                    className="btn btn-ghost btn-sm"
                  >
                    <X size={14} />
                    Cancel
                  </motion.button>
                </div>

                <span className="text-xs text-gray-500">
                  Ctrl+Enter to save
                </span>
              </div>
            </motion.div>
          )}
        </AnimatePresence>
      </motion.div>
    </div>
  );
};

export default AddNoteForm;

