{
  "success": true,
  "data": {
    "name": "LLM Chat with Download",
    "nodes": [
      {
        "id": "chat-trigger",
        "name": "Chat Trigger",
        "type": "@n8n/n8n-nodes-langchain.chatTrigger",
        "typeVersion": 1,
        "position": [
          260,
          300
        ],
        "parameters": {
          "public": true,
          "mode": "hostedChat"
        }
      },
      {
        "id": "check-download",
        "name": "Check Download Request",
        "type": "n8n-nodes-base.if",
        "typeVersion": 2,
        "position": [
          480,
          300
        ],
        "parameters": {
          "conditions": {
            "options": {
              "caseSensitive": true,
              "leftValue": "",
              "typeValidation": "strict"
            },
            "conditions": [
              {
                "leftValue": "={{ $json.chatInput }}",
                "rightValue": "/download",
                "operator": {
                  "type": "string",
                  "operation": "contains"
                }
              }
            ],
            "combinator": "and"
          }
        }
      },
      {
        "id": "load-memory",
        "name": "Load Conversation History",
        "type": "@n8n/n8n-nodes-langchain.memoryManager",
        "typeVersion": 1,
        "position": [
          700,
          180
        ],
        "parameters": {
          "mode": "load",
          "simplifyOutput": true
        }
      },
      {
        "id": "create-download",
        "name": "Create Download File",
        "type": "n8n-nodes-base.convertToFile",
        "typeVersion": 1,
        "position": [
          920,
          180
        ],
        "parameters": {
          "operation": "toJson",
          "mode": "allItemsToOneFile",
          "options": {
            "fileName": "chat-conversation.json",
            "format": true
          }
        }
      },
      {
        "id": "respond-download",
        "name": "Respond with Download",
        "type": "n8n-nodes-base.respondToWebhook",
        "typeVersion": 1,
        "position": [
          1140,
          180
        ],
        "parameters": {
          "options": {
            "responseHeaders": {
              "entries": [
                {
                  "name": "Content-Type",
                  "value": "application/json"
                },
                {
                  "name": "Content-Disposition",
                  "value": "attachment; filename=chat-conversation.json"
                }
              ]
            }
          }
        }
      },
      {
        "id": "load-chat-memory",
        "name": "Load Chat Memory",
        "type": "@n8n/n8n-nodes-langchain.memoryManager",
        "typeVersion": 1,
        "position": [
          700,
          420
        ],
        "parameters": {
          "mode": "load",
          "simplifyOutput": true
        }
      },
      {
        "id": "openai-chat",
        "name": "OpenAI Chat",
        "type": "n8n-nodes-base.openAi",
        "typeVersion": 1,
        "position": [
          920,
          420
        ],
        "parameters": {
          "resource": "text",
          "operation": "completion",
          "model": "gpt-3.5-turbo",
          "prompt": "={{ $json.chatInput }}",
          "maxTokens": 500,
          "options": {
            "systemMessage": "You are a helpful AI assistant. Respond naturally to user questions and conversations. If a user wants to download their conversation, tell them to type '/download' to get their chat history."
          }
        }
      },
      {
        "id": "save-user-message",
        "name": "Save User Message",
        "type": "@n8n/n8n-nodes-langchain.memoryManager",
        "typeVersion": 1,
        "position": [
          1140,
          300
        ],
        "parameters": {
          "mode": "insert",
          "insertMode": "insert",
          "messages": {
            "messageValues": [
              {
                "sender": "user",
                "message": "={{ $('Chat Trigger').item.json.chatInput }}"
              }
            ]
          }
        }
      },
      {
        "id": "save-ai-response",
        "name": "Save AI Response",
        "type": "@n8n/n8n-nodes-langchain.memoryManager",
        "typeVersion": 1,
        "position": [
          1360,
          420
        ],
        "parameters": {
          "mode": "insert",
          "insertMode": "insert",
          "messages": {
            "messageValues": [
              {
                "sender": "assistant",
                "message": "={{ $json.choices[0].message.content }}"
              }
            ]
          }
        }
      },
      {
        "id": "respond-chat",
        "name": "Respond to Chat",
        "type": "n8n-nodes-base.respondToWebhook",
        "typeVersion": 1,
        "position": [
          1580,
          420
        ],
        "parameters": {
          "respondWith": "text",
          "responseBody": "={{ $('OpenAI Chat').item.json.choices[0].message.content }}"
        }
      }
    ],
    "connections": {
      "Chat Trigger": {
        "main": [
          [
            {
              "node": "Check Download Request",
              "type": "main",
              "index": 0
            }
          ]
        ]
      },
      "Check Download Request": {
        "main": [
          [
            {
              "node": "Load Conversation History",
              "type": "main",
              "index": 0
            }
          ],
          [
            {
              "node": "Load Chat Memory",
              "type": "main",
              "index": 0
            }
          ]
        ]
      },
      "Load Conversation History": {
        "main": [
          [
            {
              "node": "Create Download File",
              "type": "main",
              "index": 0
            }
          ]
        ]
      },
      "Create Download File": {
        "main": [
          [
            {
              "node": "Respond with Download",
              "type": "main",
              "index": 0
            }
          ]
        ]
      },
      "Load Chat Memory": {
        "main": [
          [
            {
              "node": "OpenAI Chat",
              "type": "main",
              "index": 0
            }
          ]
        ]
      },
      "OpenAI Chat": {
        "main": [
          [
            {
              "node": "Save User Message",
              "type": "main",
              "index": 0
            }
          ]
        ]
      },
      "Save User Message": {
        "main": [
          [
            {
              "node": "Save AI Response",
              "type": "main",
              "index": 0
            }
          ]
        ]
      },
      "Save AI Response": {
        "main": [
          [
            {
              "node": "Respond to Chat",
              "type": "main",
              "index": 0
            }
          ]
        ]
      }
    },
    "settings": {
      "executionOrder": "v1",
      "saveDataErrorExecution": "all",
      "saveDataSuccessExecution": "all",
      "saveManualExecutions": true,
      "saveExecutionProgress": true
    },
    "active": false,
    "versionId": "dbaf7e68-fb36-46b9-8bcb-32d2dcfd9ab6",
    "id": "fPBxqptgUwm9Zuvv",
