    69→const columnNamesCache = new Map();
    70→const getColumnName = (index) => {
    71→  if (index === undefined || index < 0) return '';
    72→
    73→  if (columnNamesCache.has(index)) {
    74→    return columnNamesCache.get(index);
    75→  }
    76→  
    77→  let name = "";
    78→  let tempIndex = index;
    79→  while (tempIndex >= 0) {
    80→    name = String.fromCharCode((tempIndex % 26) + 65) + name;
    81→    tempIndex = Math.floor(tempIndex / 26) - 1;
    82→  }
    83→  columnNamesCache.set(index, name);
    84→  return name;
    85→};
