   204→    }, [windowDimension]);
   205→  
   206→      // Dynamic CSS for jss_content jss_content_overflow class
   207→    useEffect(() => {
   208→      const calculateDynamicDimensions = () => {
   209→        const screenWidth = windowDimension.innerWidth;
   210→        const screenHeight = windowDimension.innerHeight;
   211→        
   212→        // Calculate dynamic height based on screen size and UI elements
   213→        let dynamicHeight = screenHeight;
   214→        
   215→        // Account for common UI elements (header, footer, margins, etc.)
   216→        const reservedHeight = 20; // Adjust this value based on your header/footer height
   217→        
   218→        // Define responsive height breakpoints
   219→        if (screenHeight <= 600) { // Small screens
   220→          dynamicHeight = screenHeight - 120;
   221→        } else if (screenHeight <= 800) { // Medium screens
   222→          dynamicHeight = screenHeight - 160;
   223→        } else if (screenHeight <= 1080) { // Standard screens
   224→          dynamicHeight = screenHeight - reservedHeight;
   225→        } else { // Large screens
   226→          dynamicHeight = screenHeight - 250;
   227→        }
   228→        
   229→        // Ensure minimum height
   230→        dynamicHeight = Math.max(dynamicHeight, 400);
   231→        
   232→        return { 
   233→          width: screenWidth, 
   234→          height: screenHeight - 120, 
   235→          dynamicHeight: dynamicHeight - 120
   236→        };
   237→      };
   238→  
   239→      const updateJSSContentStyles = () => {
   240→        const { width, height, dynamicHeight } = calculateDynamicDimensions();
   241→        
   242→        // Remove existing style if it exists
