   327→            background-color: var(--jss-hover-background) !important;
   328→          }
   329→  
   330→          .jss_modern .jss_cell.jss_selected {
   331→            background-color: var(--jss-selected-background) !important;
   332→            border-color: #2196f3 !important;
   333→          }
   334→  
   335→          .jss_modern .jss_nested {
   336→            background: #E3F1FF !important;
   337→            border: 1px solid #f0f0f0 !important;
   338→            border-color: #f0f0f0 !important;
   339→            font-weight: 600 !important;
   340→            color: #495057 !important;
   341→            position: sticky !important;
   342→            top: 0 !important;
   343→            z-index: 10 !important;
   344→          }
   345→  
   346→          /* Fix for content not overlapping with sticky headers */
   347→          .jss_modern .jss_content {
   348→            position: relative !important;
   349→            z-index: 1 !important;
   350→          }
   351→  
   352→          .jss_modern .jss_cell {
   353→            position: relative !important;
   354→            z-index: 1 !important;
   355→          }
   356→  
   357→          @media (min-width: 1680px) {
   358→            .jss_content.jss_content_overflow,
   359→            .jss_content.jss_content_overflow.jss_modern {
   360→              max-width: 92vw !important;
   361→            }
   362→          }
   363→          @media (max-width: 1366px) {
   364→            .jss_content.jss_content_overflow,
   365→            .jss_content.jss_content_overflow.jss_modern {
   366→              max-width: 88.9vw ;
   367→            }
   368→          }
   369→          
   370→          @media (max-width: 768px) {
   371→            .jss_content.jss_content_overflow,
   372→            .jss_content.jss_content_overflow.jss_modern {
   373→              width: 88.9vw !important;
   374→              height: ${Math.max(dynamicHeight * 0.8, 350)}px !important;
   375→              max-height: ${height * 0.85}px !important;
   376→            }
   377→          }
   378→          
   379→          @media (max-width: 480px) {
   380→            .jss_content.jss_content_overflow,
   381→            .jss_content.jss_content_overflow.jss_modern {
   382→              width: 98vw !important;
   383→              height: ${Math.max(dynamicHeight * 0.75, 300)}px !important;
   384→              max-height: ${height * 0.8}px !important;
   385→            }
   386→          }
   387→          
   388→          @media (min-height: 1200px) {
   389→            .jss_content.jss_content_overflow,
   390→            .jss_content.jss_content_overflow.jss_modern {
