   401→        `;
   402→        
   403→        document.head.appendChild(style);
   404→      };
   405→      
   406→      // Update styles when component mounts and when window dimension changes
   407→      updateJSSContentStyles();
   408→      
   409→      // Cleanup function
   410→      return () => {
   411→        const existingStyle = document.getElementById('jss-content-dynamic-styles');
   412→        if (existingStyle) {
   413→          existingStyle.remove();
   414→        }
