   416→    }, [windowDimension]);
   417→    
   418→
   419→  const baseColumns = useMemo(() => {
   420→    const columnWidths = calculateColumnWidths();
   421→    return [
   422→      { title: "Emp ID", width: columnWidths.empId, readOnly: true, filter: true, wrap: true },
   423→      { title: "Name", width: columnWidths.name, readOnly: true, filter: true, wrap: true },
   424→      { title: "Department", width: columnWidths.department, readOnly: true, filter: true, wrap: true },
   425→      { title: "Designation", width: columnWidths.designation, readOnly: true, filter: true, wrap: true },
   426→      { title: "Professional Tax", width: columnWidths.dayColumn, type: COLUMN_TYPES.DROPDOWN, source: TOGGLE_OPTIONS, wrap: true },
   427→      { title: "PT Amount", width: columnWidths.dayColumn, type: COLUMN_TYPES.NUMERIC, decimal: '.', mask: '#,##0.00', align: 'right' , wrap: true },
   428→      { title: "Labour Welfare Fund", width: columnWidths.dayColumn, type: COLUMN_TYPES.DROPDOWN, source: TOGGLE_OPTIONS, wrap: true },
   429→      { title: "LWF Amount", width: columnWidths.dayColumn, type: COLUMN_TYPES.NUMERIC, decimal: '.', mask: '#,##0.00', align: 'right', wrap: true },
   430→    ];
   431→  }, [calculateColumnWidths]);
   432→
   433→  const columnIndexMap = useMemo(() => ({
   434→    "Emp ID": 0,
   435→    "Name": 1,
   436→    "Department": 2,
   437→    "Designation": 3,
   438→    "Professional Tax": 4,
   439→    "PT Amount": 5,
   440→    "Labour Welfare Fund": 6,
   441→    "LWF Amount": 7,
   442→  }), []);
   443→
   444→  const { ptIndex, ptAmountIndex, lwfIndex, lwfAmountIndex } = useMemo(() => ({
   445→    ptIndex: columnIndexMap["Professional Tax"],
   446→    ptAmountIndex: columnIndexMap["PT Amount"],
   447→    lwfIndex: columnIndexMap["Labour Welfare Fund"],
   448→    lwfAmountIndex: columnIndexMap["LWF Amount"]
   449→  }), [columnIndexMap]);
   450→  
   451→  const { ptRelatedCells, lwfRelatedCells } = useMemo(() => ({
   452→    ptRelatedCells: [ptIndex, ptAmountIndex],
   453→    lwfRelatedCells: [lwfIndex, lwfAmountIndex]
   454→  }), [ptIndex, ptAmountIndex, lwfIndex, lwfAmountIndex]);
   455→
   456→  const setCellStyle = useCallback((worksheet, cellIndexes, rowIndex, styleProperty, styleValue) => {
   457→    if (!worksheet || !Array.isArray(cellIndexes) || rowIndex === undefined) return;
   458→    try {
   459→      const existingStyles = worksheet.getStyle() || {};
   460→      const newStyles = { ...existingStyles };
   461→      
   462→      for (let i = 0; i < cellIndexes.length; i++) {
   463→        const index = cellIndexes[i];
   464→        if (index === undefined || index < 0) continue;
   465→        
   466→        const cellPosition = `${getColumnName(index)}${parseInt(rowIndex) + 1}`;
   467→        
   468→        if (styleValue) {
   469→          newStyles[cellPosition] = `${styleProperty}: ${styleValue}`;
   470→        } else {
   471→          delete newStyles[cellPosition];
   472→        }
   473→      }
   474→
   475→      worksheet.setStyle(newStyles);
   476→    } catch (error) {
   477→      console.error("Error setting cell styles:", error);
   478→    }
   479→  }, []);
   480→
   481→  const setCellsReadOnly = useCallback((worksheet, indexes, rowIndex, isReadOnly) => {
   482→    if (!worksheet || !Array.isArray(indexes) || rowIndex === undefined) return;
   483→
   484→    try {
   485→      for (let i = 0; i < indexes.length; i++) {
   486→        const index = indexes[i];
   487→        if (index < 0 || !baseColumns[index]) continue;
   488→        
   489→        worksheet.setProperty(index, rowIndex, {
   490→          ...baseColumns[index],
   491→          readOnly: isReadOnly
   492→        });
   493→      }
   494→    } catch (error) {
   495→      console.error("Error setting readOnly property:", error);
   496→    }
   497→  }, [baseColumns]);
   498→
   499→  const transformRowDataToObject = useCallback((rowData, columns) => {
   500→    if (!rowData || !columns || !Array.isArray(rowData) || !Array.isArray(columns)) {
   501→      return {};
   502→    }
   503→    
   504→    try {
   505→      const values = rowData.map((cell) => cell?.v);
   506→      const keys = columns.map((col) => col?.title);
   507→      const result = _.zipObject(keys, values);
   508→      return result;
   509→    } catch (error) {
   510→      console.error("Error transforming row data:", error);
   511→      return {};
   512→    }
   513→  }, []);
   514→
   515→  const processApiDataForSpreadsheet = useCallback((configsData) => {
   516→    if (!configsData) return { data: [], columns: [] };
   517→    
   518→    try {
   519→      const orgConfig = configsData[API_KEYS.ORG_CONFIG] || {};
   520→      const employeeKeys = Object.keys(configsData).filter(key => key !== API_KEYS.ORG_CONFIG);
   521→      const dataRows = new Array(employeeKeys.length);
   522→
   523→      for (let i = 0; i < employeeKeys.length; i++) {
   524→        const empKey = employeeKeys[i];
   525→        const emp = configsData[empKey];
   526→        if (!emp) continue;
   527→
   528→        const isEntryExistInDb = emp?.[API_KEYS.CONFIG_ID] && parseInt(emp[API_KEYS.CONFIG_ID]) > 0;
   529→        
   530→        dataRows[i] = {
   531→          "Emp ID": emp[API_KEYS.CODE] || "",
   532→          "Name": emp[API_KEYS.EMPLOYEE_NAME] || "",
   533→          "Department": emp[API_KEYS.DEPARTMENT] || "",
   534→          "Designation": emp[API_KEYS.DESIGNATION] || "",
   535→          "Professional Tax": isEntryExistInDb ? (emp?.[API_KEYS.PT] === 1 ? "On" : "Off") : (orgConfig?.[API_KEYS.PT] === 1 ? "On" : "Off"),
   536→          "PT Amount": isEntryExistInDb ? (emp?.[API_KEYS.PT_AMOUNT] || 0) : (orgConfig?.[API_KEYS.PT_AMOUNT] || 0),
   537→          "Labour Welfare Fund": isEntryExistInDb ? (emp?.[API_KEYS.LWF] === 1 ? "On" : "Off") : (orgConfig?.[API_KEYS.LWF] === 1 ? "On" : "Off"),
   538→          "LWF Amount": isEntryExistInDb ? (emp?.[API_KEYS.LWF_AMOUNT] || 0) : (orgConfig?.[API_KEYS.LWF_AMOUNT] || 0),
   539→        };
   540→      }
   541→        
   542→      const filteredRows = dataRows.filter(Boolean);
   543→
   544→      return { 
   545→        data: filteredRows, 
   546→        columns: baseColumns
   547→      };
   548→    } catch (error) {
   549→      console.error("Error processing API data:", error);
   550→      return { data: [], columns: [] };
   551→    }
   552→  }, [baseColumns]);
   553→
   554→  const updateRelatedFieldsData = useCallback((rowIndex, worksheet, toggleData) => {
   555→    if (!worksheet || rowIndex === undefined) return;
   556→    
   557→    try {
