  1177→                      {dateRange.start && dateRange.end && (
  1178→                        <div className="date-range-display me-3">
  1179→                          <small>
  1180→                            Showing data from{" "}
  1181→                            <strong>
  1182→                              {new Date(dateRange.start).toLocaleDateString()}
  1183→                            </strong>{" "}
  1184→                            to{" "}
  1185→                            <strong>
  1186→                              {new Date(dateRange.end).toLocaleDateString()}
  1187→                            </strong>
  1188→                          </small>
  1189→                        </div>
  1190→                      )}
  1191→                    </div>
  1192→                    <div className="ms-auto d-flex align-items-center">
  1193→                      {/* Save Button */}
