      }
      setIsSaving(false);
    },
    onError: (error) => {
      console.error("API Error:", error);
      toast.error(error?.message || MESSAGES.SAVE_ERROR);
      setIsSaving(false);
    },
  });

   //  dynamic window dimension detection start
     const [windowDimension, detectHW] = useState({
       innerWidth: window.innerWidth,
       innerHeight: window.innerHeight
     })
     const detectSize = () => {
       detectHW({
         innerWidth: window.innerWidth,
         innerHeight: window.innerHeight
       })
     }
     useEffect(() => {
       window.addEventListener('resize', detectSize)
       return () => {
         window.removeEventListener('resize', detectSize)
       }
     }, [])
    //  dynamic window dimension detection end

    // Calculate dynamic column widths based on window dimension
    const calculateColumnWidths = useCallback(() => {
      const screenWidth = windowDimension.innerWidth;

      // Define breakpoints and corresponding widths
      if (screenWidth <= 768) { // Mobile
        return {
          empId: 50,
          empCode: 80,
          name: 120,
          department: 100,
          designation: 100,
          dayColumn: 120
        };
      } else if (screenWidth <= 1024) { // Tablet
        return {
          empId: 50,
          empCode: 90,
          name: 140,
          department: 120,
          designation: 120,
          dayColumn: 140
        };
      } else if (screenWidth <= 1366) { // Small desktop
        return {
          empId: 50,
          empCode: 100,
          name: 130,
          department: 130,
          designation: 130,
          dayColumn: 150
        };
      } else if (screenWidth <= 1620) { // Standard desktop
        return {
          empId: 50,
          empCode: 100,
          name: 130,
          department: 130,
          designation: 130,
          dayColumn: 150
        };
      } else { // Large desktop/4K
        return {
          empId: 50,
          empCode: 100,
          name: 130,
          department: 130,
          designation: 130,
          dayColumn: 150
        };
      }
    }, [windowDimension]);

      // Dynamic CSS for jss_content jss_content_overflow class
    useEffect(() => {
      const calculateDynamicDimensions = () => {
        const screenWidth = windowDimension.innerWidth;
        const screenHeight = windowDimension.innerHeight;

        // Calculate dynamic height based on screen size and UI elements
        let dynamicHeight = screenHeight;

        // Account for common UI elements (header, footer, margins, etc.)
        const reservedHeight = 20; // Adjust this value based on your header/footer height

        // Define responsive height breakpoints
        if (screenHeight <= 600) { // Small screens
          dynamicHeight = screenHeight - 120;
        } else if (screenHeight <= 800) { // Medium screens
          dynamicHeight = screenHeight - 160;
        } else if (screenHeight <= 1080) { // Standard screens
          dynamicHeight = screenHeight - reservedHeight;
        } else { // Large screens
          dynamicHeight = screenHeight - 250;
        }

        // Ensure minimum height
        dynamicHeight = Math.max(dynamicHeight, 400);

        return { 
          width: screenWidth, 
          height: screenHeight - 120, 
          dynamicHeight: dynamicHeight - 120
        };
      };

      const updateJSSContentStyles = () => {
        const { width, height, dynamicHeight } = calculateDynamicDimensions();

        // Remove existing style if it exists
        const existingStyle = document.getElementById('jss-content-dynamic-styles');
        if (existingStyle) {
          existingStyle.remove();
        }

        // Create new style element
        const style = document.createElement('style');
        style.id = 'jss-content-dynamic-styles';
        style.textContent = `
          .jss_content.jss_content_overflow {
            width: ${width + 200}px !important;
            height: ${dynamicHeight}px !important;
            max-height: ${height * 0.9}px !important;
            max-width: 90vw ;
            overflow-x: auto !important;
            overflow-y: auto !important;
            border: 1px solid #e0e0e0 !important;
            border-radius: 4px !important;
