const mysql = require("mysql");
const AWS = require("aws-sdk");
const publishLogLayer = require('/opt/nodejs/publish_activity_log.js');

AWS.config.update({
  region: "ap-south-1",
  maxRetries: 2,
  httpOptions: {
    timeout: 3000,
    connectTimeout: 3000,
  },
});

const SecretsManager = require("./secretsManager.js");
const jwtTokenLayer = require("/opt/nodejs/app.js");
const remoteNotificationLayer = require('/opt/nodejs/firebase_notification_send.js');
var pool;

async function getConnection() {
  return new Promise((resolve, reject) => {
    pool.getConnection(function (err, connection) {
      if (err) {
        //console.log('Error while getting connection');
        reject();
      }
      resolve(connection);
    });
  });
}

async function beginTransaction(connection) {
  return new Promise((resolve, reject) => {
    connection.beginTransaction(function (err) {
      if (err) {
        reject(err);
      }
      resolve();
    });
  });
}

async function commitTransaction(connection) {
  return new Promise((resolve, reject) => {
    connection.commit(function (err) {
      if (err) {
        reject(err);
      }
      resolve();
    });
  });
}

async function rollbackTransaction(connection) {
  return new Promise((resolve, reject) => {
    connection.rollback(function () {
      resolve();
    });
  });
}

async function transactionRelease(connection){
  return new Promise((resolve, reject) => {
      connection.destroy();

      if (connection.state === 'disconnected') {
          console.log('Connection destroyed successfully.');
      } else {
          console.log('Connection released back to pool successfully.');
      }
      resolve();
  });
}

function execQuery(query, connection) {
  return new Promise((resolve, reject) => {
    connection.query(query, function (err, result, fields) {
      if (err) {
        console.log("Error IN query : ", query);
        reject(err);
      } else {
        resolve(result);
      }
    });
  });
}

function send_error(msg) {
  return new Promise(async (resolve, reject) => {
    console.log(msg);
    resolve();
  });
}

exports.handler = async (event, context) => {
  //use secret manager

  var secretName = process.env.payroll_secret_name;
  var region = process.env.payroll_secret_region;
  var secret_manager_value = await SecretsManager.getSecret(secretName, region);

