  const insertQuery = `INSERT INTO payrolls (ho_id, date_from, date_to, is_locked, payroll_type, modified, created) VALUES (?, ?, ?, ?, ?, ?, ?)`;
  const formattedInsertQuery = mysql.format(insertQuery, 
    [requestedData.ho_id, requestedData.from_date, requestedData.to_date, 1, payroll_type, currentDateTime, currentDateTime]);
  let insertPayrollData = await execQuery(formattedInsertQuery, db);
