export const getWorkingStatus = (data, emp_id, date, status, salary_type, tag, reportName) => {

    const legends = {
        "FullDay": reportName === "MusterReport" ? "FD" : "Full Day",
        "HalfDay": reportName === "MusterReport" ? "HD" : "Half Day",
        "Absent": reportName === "MusterReport" ? "A" : "Absent",
        "Leave": reportName === "MusterReport" ? "L" : "Leave",
        "WeekOff": reportName === "MusterReport" ? "WO" : "Week Off",
        "Holiday": reportName === "MusterReport" ? "H" : "Holiday",
        "OverTime": reportName === "MusterReport" ? "OT" : "Over Time",
        "FirstHalfLeave": reportName === "MusterReport" ? "HD" : "First Half Leave",
        "SecondHalfLeave": reportName === "MusterReport" ? "HD" : "Second Half Leave"
    };

    if(reportName === "MusterReport"){
        if (tag === "Normal" || tag === "Overwrite") {
            if (status === "Holiday" || status === "Week Off" || status === "Leave") {
                if(status === "Holiday"){
                    return legends.Holiday;
                }else if(status === "Week Off"){
                    return legends.WeekOff;
                }else if(status === "Leave"){
                    return legends.Leave;                  
                }else if(status === "First Half Leave"){
                    return legends.FirstHalfLeave;                  
                }else if(status === "Second Half Leave"){
                    return legends.SecondHalfLeave;              
                }else{
                    return legends.Absent;
                }
            } else {
                return getStatus(data, emp_id, date, status, salary_type, legends);
            }
        } else {
            if(status === "Full Day"){
                return legends.FullDay;
            }else if(status === "Half Day"){
                return legends.HalfDay;
            }else if(status === "Absent"){
                return legends.Absent;                    
            }else{
                return legends.Absent;
            }
        }
    } else if (reportName == "ShiftWiseAttendanceReport" || reportName === "WorkingHourReport"){
        // if report is ShiftWiseAttendanceReport or WorkingHourReport , then we will check for only normal and overwrite days
        if (tag === "Normal" || tag === "Overwrite") {
            return getStatus(data, emp_id, date, status, salary_type, legends);
        }else{
            return "";
        }
    }
    else{
        if (tag === "Normal" || tag === "Overwrite") {
            if(status === "Holiday"){
                return legends.Holiday;
            }else if(status === "Week Off"){
                return legends.WeekOff;
            }else if(status === "Leave"){
                return legends.Leave;                  
            }else if(status === "Half Day"){
                return legends.HalfDay;       
            }else if(status === "First Half Leave"){
                return legends.FirstHalfLeave;                  
            }else if(status === "Second Half Leave"){
                return legends.SecondHalfLeave;              
            } else {
                return getStatus(data, emp_id, date, status, salary_type, legends);
            }
        } else {
            return status;
        }
    }

};

const getStatus = (data, emp_id, date, working_hour, salary_type, legends) => {

    // Convert the working hour to seconds
    const workingSeconds = typeof working_hour === "string" ? parseTimeToSeconds(working_hour) : working_hour;
    let config;

    if (salary_type === 1) {
        config = getFreezeConfig(data, emp_id, date, salary_type) || getCurrentConfig(data, emp_id, salary_type);
    } else if (salary_type === 2) {
        config = getFreezeConfig(data, emp_id, date, salary_type) || getCurrentConfig(data, emp_id, salary_type);
    }


    if (!config) {
        config = {
            "full_day_hrs": "08:00:00",
            "half_day_hrs": "04:00:00"
        };
    }

    const fullDaySeconds = parseTimeToSeconds(config?.full_day_hrs);
    const halfDaySeconds = parseTimeToSeconds(config?.half_day_hrs);

    if (workingSeconds < halfDaySeconds) {
        return legends.Absent;
    } else if (workingSeconds >= fullDaySeconds) {
        return legends.FullDay;
    } else if (workingSeconds >= halfDaySeconds && workingSeconds < fullDaySeconds) {
        return legends.HalfDay;
    } else {
        return legends.Absent;
    }
};

// Helper function to convert HH:MM:SS to seconds
const parseTimeToSeconds = (time) => {
    const [hours, minutes, seconds] = time.split(':').map(Number);
    return hours * 3600 + minutes * 60 + seconds;
};

// Helper function to check if a date is within a range
const isDateInRange = (date, range) => {
    const [start, end] = range.split(' - ');
    const targetDate = new Date(date);
    return targetDate >= new Date(start) && targetDate <= new Date(end);
};

// Check for matching configuration in the freeze_config array
const getFreezeConfig = (data, emp_id, date, salary_type) => {
    const freezeEntries = data?.freeze_config;

    if(salary_type === 1){
        // Check for specific emp_id match in freeze_config
        for (const entry of freezeEntries) {
            if (entry.emp_id === emp_id && entry.salary_type === salary_type) {
                for (const range in entry) {
                    if (range.includes(' - ') && isDateInRange(date, range)) {
                        return entry[range];
                    }
                }
            }
        }
    }

    // Check for emp_id === null match in freeze_config
    for (const entry of freezeEntries) {
        if (entry.emp_id === null && entry.salary_type === salary_type) {
            for (const range in entry) {
                if (range.includes(' - ') && isDateInRange(date, range)) {
                    return entry[range];
                }
            }
        }
    }

    return null; // No match found
}

// Check for configuration in current_config
const getCurrentConfig = (data, emp_id, salary_type) => {
    const currentEntries = data.current_config;

    if(salary_type === 1){
        // Check for specific emp_id match in current_config
        for (const entry of currentEntries) {
            if (entry.emp_id === emp_id) {
                return entry.config;
            }
        }
    }

    // Check for emp_id === null match in current_config
    for (const entry of currentEntries) {
        if (entry.emp_id === null) {
            return entry.config;
        }
    }

    return null; // No match found
}

