import React, { useEffect, useState, useCallback, useMemo, useRef, lazy, Suspense } from "react";
import { useDispatch, useSelector } from "react-redux";
import { Container, Card, Row, Col, CardHeader, Spinner, Offcanvas, CardBody, } from "reactstrap";
import Header from "../../Layouts/Header";
import Flatpickr from "react-flatpickr";
import { Dropdown as Library1Dropdown } from "primereact/dropdown";
import { MultiSelect } from "primereact/multiselect";
import { getMusterSpreadsheetReports as fetchMusterReports } from "../../store/actions";
import dayjs from "dayjs";
import _, { debounce, groupBy } from "lodash";
import AttendanveSidebar from "../Reports/AttendanveSidebar";
import { licenceKey } from "../../Components/constants/licence_key";
import Skeleton from 'react-loading-skeleton'
import 'react-loading-skeleton/dist/skeleton.css';
import { handleExportExcel } from "../../Components/Common/SpreadsheetWrapper";
import { toast } from "react-toastify";
import "primereact/resources/themes/lara-light-indigo/theme.css";
import "primereact/resources/primereact.min.css";
import { bulkAttendanceUpdateApi, getPayrollFreezeListing, } from "../../helpers/awsapibackend_helper";
import { useMutation, useQuery } from "@tanstack/react-query";
import { PAYROLL_FREEZE_LISTING, } from "../../reactQueryKeys/PayrollQueryKeys";
import { useLocation, useNavigate, } from "react-router-dom";
import ConfirmationPopup from "../../Components/Common/ConfirmationPopup";
import CommonTooltip from '../../Components/Common/CommonTooltip';

const SpreadsheetContainer = lazy(() => import('../../Components/Common/SpreadsheetContainer'));

const ATTENDANCE_CODES = {
  FULL_DAY: "FD",
  HALF_DAY: "HD",
  ABSENT: "A",
  WEEK_OFF: "WO",
  HOLIDAY: "H"
};

const TAG_VALUES_MAP = {
  [ATTENDANCE_CODES.HALF_DAY]: 3,
  [ATTENDANCE_CODES.FULL_DAY]: 2,
  [ATTENDANCE_CODES.ABSENT]: 4,
  [ATTENDANCE_CODES.HOLIDAY]: 1,
  [ATTENDANCE_CODES.WEEK_OFF]: 6
};

const ALLOWED_VALUES = [
  ATTENDANCE_CODES.ABSENT, 
  ATTENDANCE_CODES.HALF_DAY, 
  ATTENDANCE_CODES.FULL_DAY,
  ATTENDANCE_CODES.WEEK_OFF
];
const LWP_LEAVE = 'LWP';

const DEFAULT_VALUES = [
  ATTENDANCE_CODES.HALF_DAY, 
  ATTENDANCE_CODES.FULL_DAY, 
  ATTENDANCE_CODES.ABSENT, 
  ATTENDANCE_CODES.WEEK_OFF, 
  ATTENDANCE_CODES.HOLIDAY
];

const SPECIAL_CODES = [
  ATTENDANCE_CODES.WEEK_OFF, 
  ATTENDANCE_CODES.HOLIDAY
];

// Check if a value represents an attendance code
const isAttendance = (val) => [
  ATTENDANCE_CODES.ABSENT, 
  ATTENDANCE_CODES.FULL_DAY, 
  ATTENDANCE_CODES.HALF_DAY
].includes(val);

// Check if a value represents a leave code (not attendance and not special)
// const isLeave = (val) => val && !isAttendance(val) && !SPECIAL_CODES.includes(val);
// Update the isLeave function to explicitly recognize LWP as a leave
const isLeave = (val) => val === LWP_LEAVE || (val && !isAttendance(val) && !SPECIAL_CODES.includes(val));

const DEFAULT_HEADERS = ["Emp ID", "Name", "Dept", "Designation"];

const DEFAULT_HEADERS_WIDTHS = { 
  "Designation": 150, 
  "Dept": 150, 
  "Name": 200, 
  "default": 100 
};
const DEFAULT_HEADERS_PROPS = { 
  type: "text", 
  readOnly: true, 
  filter: true, 
  locked: true 
};

// Check if a given string is a valid day (e.g. "DD-MMM")
const isValidDay = (day) => dayjs(day, "DD-MMM", true).isValid();

const WORKSHEET_TITLE  = 'Bulk Attendance Adjustments'

// Generate Excel-like column names (A, B, C, ..., Z, AA, AB, etc.)
const getColumnName = (index) => {
  let name = "";
  while (index >= 0) {
    name = String.fromCharCode((index % 26) + 65) + name;
    index = Math.floor(index / 26) - 1;
  }
  return name;
};

function groupByTagAndCombineJeIds(array) {
  const groupedByTag = _.groupBy(array, 'tag');

  return _.map(groupedByTag, (items, tag) => {
    const allJeIds = _.flatMap(items, item => {
      const jeIdsStr = String(item.je_ids);
      const matches = jeIdsStr.match(/\d+/g) || [];
      return matches;
    });

    return {
      tag: parseInt(tag),
      je_ids: _.uniq(allJeIds).join(',')
    };
  });
}

function findEmployeeByCode(employeeMap, code) {
  return employeeMap[
    Object.keys(employeeMap)?.find(
      (key) =>
        parseInt(key) === parseInt(code) ||
        String(key)?.toLowerCase() === String(code)?.toLowerCase()
    )
  ];
}

const BulkAttendanceUpdate = () => {
  const dispatch = useDispatch();

  const {
    musterReports,
    musterReportSuccess,
    organizations,
    reportStartDate,
    reportEndDate,
  } = useSelector((state) => ({
    musterReports: state.Report.musterSpreadsheetReports,
    musterReportSuccess: state.Report.musterSpreadsheetReportSuccess,
    organizations: state.Organizations.organizations,
    reportStartDate: state.Report.reportStartDate,
    reportEndDate: state.Report.reportEndDate,
    isBulkTagsUpdate: state.Report.isBulkTagsUpdate,
    bulkTagsUpdateError: state.Report.bulkTagsUpdateError
  }));
  const [nestedHeaders, setNestedHeaders] = useState([]);
  const [initialRenderCount, setInitialRenderCount] = useState(0)
  const [selectedFilterBranch, setSelectedFilterBranch] = useState("");
  const [selectedFilterStartDate, setSelectedFilterStartDate] = useState(null);
  const [selectedFilterEndDate, setSelectedFilterEndDate] = useState(null);
  const [isSearchLoading, setIsSearchLoading] = useState(false);
  const [organizationsList, setOrganizationsList] = useState([]);
  const [spreadsheetData, setSpreadsheetData] = useState([]);
  const [originalSpreadsheetData, setOriginalSpreadsheetData] = useState([]);
  const [daysColumns, setDaysColumns] = useState([]);
  const [spreadsheetKey, setSpreadsheetKey] = useState(0);
  const [cellStyles, setCellStyles] = useState({});
  const [modifiedData, setModifiedData] = useState([]);
  const [regsidebardata, setRegSidebarData] = useState({});
  const [attendanceLog, setAttendanceLog] = useState(false);
  const [yesterdayDate] = useState(dayjs(new Date()).subtract(1, "day").toDate());
  const [maxToDate, setMaxToDate] = useState(yesterdayDate);
  const [tagLoader, setTagLoader] = useState(false);
  const [showSpreadsheet, setShowSpreadsheet] = useState(false);
  const [selectedColumns, setSelectedColumns] = useState([]);
  const [isAllSelected, setIsAllSelected] = useState(false);
  const [selectedDays, setSelectedDays] = useState(0);
  const [resultFromDate, setResultFromDate] = useState(dayjs(selectedFilterStartDate).format("YYYY-MM-DD"));
  const [resultToDate, setResultToDate] = useState(dayjs(selectedFilterEndDate).format("YYYY-MM-DD"));
  const [unlockStartDate, setUnlockStartDate] = useState(null);
  const [unlockEndDate, setUnlockEndDate] = useState(null);
  const [isUnlocked, setIsUnlocked] = useState(false);
  const [isConfirmationPopupOpen, setIsConfirmationPopupOpen] = useState(false);
  const [isInitializing, setIsInitializing] = useState(true);

  const navigate = useNavigate();
  const location = useLocation();

  const isRestoringDropdownValueRef = useRef(false);

  const fullScreenRef = useRef(null);
  const bulkUpdateFlagRef = useRef(false);
  const isRevertingRef = useRef(false);

  const getDateWithinRange = useCallback((startStr, endStr, displayDay) => {
      const start = dayjs(startStr, "YYYY-MM-DD");
      const end = dayjs(endStr, "YYYY-MM-DD");
      let result = dayjs(displayDay, "DD-MMM").year(start.year());

      if (result.isBefore(start)) result = result.add(1, "year");
      if (result.isAfter(end)) result = result.subtract(1, "year");
      return result.format("YYYY-MM-DD");
    }, []);

  const getDayNameFromDate = useCallback((dateStr) => {
    if (!isValidDay(dateStr)) return "";

    const fullDate = getDateWithinRange(
      dayjs(selectedFilterStartDate).format("YYYY-MM-DD"),
      dayjs(selectedFilterEndDate).format("YYYY-MM-DD"),
      dateStr
    );
    return dayjs(fullDate).format('ddd');
  }, [selectedFilterStartDate, selectedFilterEndDate, getDateWithinRange]);

  const generateNestedHeaders = useCallback((dayArray) => {
    const row = [
      { title: "Employees", colspan: 2, frozen: true, align: "center" },
      { title: "Hierarchy", colspan: 2, frozen: false, align: "center" },
      ...dayArray.map(day => ({ title: day, colspan: 1, frozen: false, align: "center" })),
      { title: "", colspan: "11", frozen: false, align: "center"}
    ];
    return [row];
  }, []);

  const previousSearchCriteria = useMemo(() => ({
    startDate: null,
    endDate: null,
    branch: ""
  }), []);

  const rowBalanceMapsObj = useMemo(() => ({}), []);

  const { mutate: submitBulkTagUpdates, isPending: isTagUpdatePending } = useMutation({

    mutationFn: bulkAttendanceUpdateApi,
    onSuccess: (data) => {
      if (data?.statusCode === 200) {
        toast.success(data?.message || "Changes saved successfully");
        // Reset state
        setIsSearchLoading(true);
        setSpreadsheetData([]);
        setSpreadsheetKey((prev) => prev + 1);
        setModifiedData([]);

        if (reportStartDate && reportEndDate) {
          dispatch(
            fetchMusterReports({
              filter_start_date: reportStartDate,
              filter_end_date: reportEndDate,
              filter_branch: selectedFilterBranch,
            })
          );
        }
      } else {
        toast.error(data?.message || "Failed to save changes");
      }
    },
    onError: (error) => {
      console.error("API Error:", error);
      toast.error(error?.message || "An error occurred while saving changes");
    },
  });

  // Generate an array of day strings (e.g., ["01-Jan", "02-Jan", ...]) between two dates
  const generateDayArray = useCallback((fromStr, toStr) => {
    const fromDate = dayjs(fromStr, "YYYY-MM-DD");
    const toDate = dayjs(toStr, "YYYY-MM-DD");
    const difference = toDate.diff(fromDate, "day");

    return _.times(difference + 1, (i) =>
      fromDate.add(i, "day").format("DD-MMM")
    );
  }, []);

  // Generate prefixed keys (used to extract leave/holiday values from API data)
  const generatePrefixedKeys = useCallback((day) => {
    const prefixes = [
      "holiday_name_day_",
      "leave_name_day_",
      "err_day_",
      "tag_day_",
      "day_",
    ];
    const dateObj = dayjs(day, "DD-MMM");
    const dayNum = dateObj.date();
    const monthNum = dateObj.month() + 1;

    return _.map(prefixes, (prefix) => `${prefix}${monthNum}_${dayNum}`);
  }, []);

  // Determine the cell status and corresponding tag
  const getStatus = useCallback((tagCol, dayCol, leaveType) => {
    const classList = {
      "Full Day": "ps-Ptag ps-unline",
      Absent: "ps-Atag ps-unline",
      "Over Time": "ps-ottag ps-unline",
      "Half Day": "ps-Atag ps-unline",
      Leave: "ps-Pwotag ps-unline",
      "Week Off": "ps-WOtag ps-unline",
      "First Half Leave": "ps-Pwotag ps-unline",
      "Second Half Leave": "ps-Pwotag ps-unline",
      Holiday: "ps-WOtag ps-unline",
    };

    const tag = {
      "Full Day": ATTENDANCE_CODES.FULL_DAY,
      Absent: ATTENDANCE_CODES.ABSENT,
      "Over Time": "OT",
      "Half Day": ATTENDANCE_CODES.HALF_DAY,
      Leave: "L",
      "Week Off": ATTENDANCE_CODES.WEEK_OFF,
      "First Half Leave": ATTENDANCE_CODES.HALF_DAY,
      "Second Half Leave": ATTENDANCE_CODES.HALF_DAY,
      Holiday: ATTENDANCE_CODES.HOLIDAY,
    };

    if (dayCol === "Week Off") {
      return { class: classList[dayCol], tag: tag[dayCol] };
    }

    if (leaveType === "leave") {
      return { class: classList["Leave"], tag: dayCol };
    }


    const finalCol =
      tagCol && tagCol !== "Normal" && tagCol !== "Overwrite" ? tagCol : dayCol;

    return {
      class: classList[finalCol] ?? "ps-Atag ps-unline",
      tag: tag[finalCol] ?? ATTENDANCE_CODES.ABSENT,
    };
  }, []);

  // Generate custom cell styles based on muster report data
  const generateCellStyles = useCallback(
    (rows, cols, modifiedCells = {}) => {
      if (_.isEmpty(rows) || _.isEmpty(cols)) return {};

      const styles = {};
      const musterMap = _.keyBy(musterReports, (mrow) => mrow.code?.toString());

      const validColumns = _.compact(
        cols.map((col, colIndex) => {
          if (!isValidDay(col.title)) return null;

          const dayObj = dayjs(col.title, "DD-MMM");
          if (!dayObj.isValid()) return null;

          return {
            ...col,
            colLetter: getColumnName(colIndex),
            tagKey: `tag_day_${dayObj.month() + 1}_${dayObj.date()}`,
            errKey: `err_day_${dayObj.month() + 1}_${dayObj.date()}`,
            leaveKey: `leave_name_day_${dayObj.month() + 1}_${dayObj.date()}`,
            dayKey: `day_${dayObj.month() + 1}_${dayObj.date()}`
          };
        })
      );

      rows.forEach((row, rowIndex) => {
        const musterRow = musterMap[row["Emp ID"]?.toString()];

        validColumns.forEach((validCol) => {
          const cellKey = `${validCol.colLetter}${rowIndex + 1}`;
          const tagValue = musterRow?.[validCol.tagKey];
          const errValue = musterRow?.[validCol.errKey];
          const leaveValue = musterRow?.[validCol.leaveKey];
          const dayValue = musterRow?.[validCol.dayKey];
          const isModified = modifiedCells[cellKey] !== undefined;

          // Check if cell contains both a half day and a leave
          // const isHalfDayWithLeave = dayValue === 'Half Day' && leaveValue;

        if (isModified) {
          styles[cellKey] = `font-weight: 550; color: #000000;background-color: #EFF2E9`;
        // } else if (isHalfDayWithLeave) {
        //   // Special styling for half-day + leave combination
        //   styles[cellKey] = `font-weight: 550; color: #8558df`;
        } else if (
          (errValue && errValue === "Problem") && 
          ((tagValue && tagValue === "Absent") || (dayValue && (dayValue === 'Absent' || (dayValue === 'Half Day' && tagValue !== 'Half Day'))))) {
          styles[cellKey] = `font-weight: 550; color: #C52031`;
        } else if (tagValue && tagValue !== "Normal") {
          styles[cellKey] = `font-weight: 550; color:rgb(15, 94, 158)`;
        } else {
          styles[cellKey] = `font-weight: 550; color: #000;`;
        }
        });
      });

      return styles;
    },
    [musterReports]
  );

  // Get available balance for a leave
  const getAvailableBalance = useCallback((mrow, leave, rowBalanceObj, baseBalanceMap) => {
    return rowBalanceObj[mrow.code] && rowBalanceObj[mrow.code][leave] !== undefined
      ? rowBalanceObj[mrow.code][leave]
      : baseBalanceMap[leave];
  }, []);

  // Build dropdown source based on cell value
  const buildDropdownSource = useCallback((currentValue, cellSourceExcludingWOH, distinct_leave_names, mrow, rowBalanceMapsObj, baseBalanceMap) => {
    // if (currentValue === ATTENDANCE_CODES.WEEK_OFF || currentValue === ATTENDANCE_CODES.HOLIDAY) {
    if (currentValue === ATTENDANCE_CODES.HOLIDAY) {
      // If the cell is set to "WO" or "H", show all default values and all leave options
      return [...DEFAULT_VALUES, ...distinct_leave_names, LWP_LEAVE];
    } else if ([ATTENDANCE_CODES.ABSENT, ATTENDANCE_CODES.FULL_DAY, ATTENDANCE_CODES.HALF_DAY].includes(currentValue)) {
      // If the cell holds an attendance code (A, FD, or HD):
      // For HD, the required balance is 0.5; for A/FD, it is 1
      const requiredBalance = currentValue === ATTENDANCE_CODES.HALF_DAY ? 0.5 : 1;

      return [
        ...cellSourceExcludingWOH,
        // Only include leave options with sufficient available balance
        ...distinct_leave_names.filter((leave) => {
          const available = getAvailableBalance(mrow, leave, rowBalanceMapsObj, baseBalanceMap);
          if (leave === 'LWP') return true
          return available >= requiredBalance && available > 0;
        }),
      ];
    } else {
      // Otherwise, the cell already holds a leave value
      // Always include the leave that is currently applied (even if balance is 0 or negative),
      // but only include other leaves if they have available balance > 0
      return [
        ...cellSourceExcludingWOH,
        // LWP_LEAVE,
        ...distinct_leave_names.filter((leave) => {
          const available = getAvailableBalance(mrow, leave, rowBalanceMapsObj, baseBalanceMap);
          if (leave === currentValue) return true;
          if (leave === 'LWP') return true
          return available >= 1;
        }),
      ];
    }
  }, [getAvailableBalance]);

  // Compute leave aliases (assign alias numbers for duplicate leave names)
  const computeLeaveAliases = useCallback((musterRow) => {
    let leaveData = [];
    try {
      leaveData = JSON.parse(musterRow?.leave_data) || [];
    } catch (e) {
      leaveData = [];
    }

    const nameCount = {};
    leaveData.forEach((leave) => {
      if (leave.leave_name) {
        nameCount[leave.leave_name] = (nameCount[leave.leave_name] || 0) + 1;
      }
    });

    const aliasCount = {};
    const aliasMapping = {};

    leaveData.forEach((leave) => {
      if (leave.leave_name) {
        let alias = leave.leave_name;
        if (nameCount[leave.leave_name] > 1) {
          aliasCount[leave.leave_name] = (aliasCount[leave.leave_name] || 0) + 1;
          alias = leave.leave_name + aliasCount[leave.leave_name];
        }
        aliasMapping[alias] = { 
          leaveId: leave.leave_id || null, 
          originalName: leave.leave_name 
        };
      }
    });

    return aliasMapping;
  }, []);

  // Compute balance map using distinct leave names (with aliases for duplicates)
  const computeBalanceMap = useCallback((musterRow) => {
    let leaveData = [];
    try {
      leaveData = JSON.parse(musterRow?.leave_data) || [];
    } catch (e) {
      leaveData = [];
    }

    const nameCount = {};
    leaveData.forEach((leave) => {
      if (leave.leave_name) {
        nameCount[leave.leave_name] = (nameCount[leave.leave_name] || 0) + 1;
      }
    });

    const aliasCount = {};
    const balanceMap = {};

    leaveData.forEach((leave) => {
      if (leave.leave_name) {
        let alias = leave.leave_name;
        if (nameCount[leave.leave_name] > 1) {
          aliasCount[leave.leave_name] = (aliasCount[leave.leave_name] || 0) + 1;
          alias = leave.leave_name + aliasCount[leave.leave_name];
        }
        balanceMap[alias] = (balanceMap[alias] || 0) + parseFloat(leave.leave_balance);
      }
    });

    return balanceMap;
  }, []);

  // Get all default values except WO and H
  const getCellSourceExcludingWOH = useCallback(() => 
    // DEFAULT_VALUES.filter(item => item !== ATTENDANCE_CODES.WEEK_OFF && item !== ATTENDANCE_CODES.HOLIDAY),
  DEFAULT_VALUES.filter(item => item !== ATTENDANCE_CODES.HOLIDAY),
  []);

  // Create cell properties for dropdown cells
  const createCellProperties = useCallback((dropdownSource, validateFn, currentValue) => {
    const cellProps = {
      type: "dropdown",
      width: 80,
      autocomplete: true,
      source: dropdownSource,
      options: {
        newOptions: false,
        autofocus: true,
      },
      validate: validateFn,
    };

    // Make the cell read-only if it's "H" (Holiday) or "WO" (Week Off)
    if (currentValue === ATTENDANCE_CODES.HOLIDAY) {
    // if (currentValue === ATTENDANCE_CODES.HOLIDAY || currentValue === ATTENDANCE_CODES.WEEK_OFF) {

      cellProps.readOnly = true;
    }

    return cellProps;
  }, []);

  // Create a validation function for leave balance checks
  const createValidateFunction = useCallback((currentWorkSheet, col, rowIndex, currentValue, mrow, distinct_leave_names, rowBalanceMapsObj, baseBalanceMap) => {
    return function() {
      const cellVal = currentWorkSheet.getValueFromCoords(col, rowIndex);

      if (cellVal === LWP_LEAVE) {
        return true;
      }

      if (distinct_leave_names.includes(cellVal)) {
        const available = getAvailableBalance(mrow, cellVal, rowBalanceMapsObj, baseBalanceMap);
        let required = 1;

        if ([ATTENDANCE_CODES.ABSENT, ATTENDANCE_CODES.FULL_DAY].includes(currentValue)) {
          required = 1;
        } else if (currentValue === ATTENDANCE_CODES.HALF_DAY) {
          required = 0.5;
        }

        if (available < required) {
          console.error(`Validation failed: Not enough balance for ${cellVal} for employee ${mrow.code}. Required: ${required}, Available: ${available}`);
          toast.error(`Not enough balance for ${cellVal} for employee ${mrow.code}`);
          return false;
        }
      }

      return true;
    };
  }, [getAvailableBalance]);

  // Updates the dropdown options for all editable cells in a given row
  const updateRowDropdownOptions = useCallback((worksheet, rowIndex) => {
    const currentWorkSheet = worksheet;
    const columnsLength = daysColumns.length;

    // Get the muster report row data for this row
    const mrow = musterReports[rowIndex];
    // Compute the base leave balances and the leave aliases for this employee
    const baseBalanceMap = computeBalanceMap(mrow);
    const leaveAliasesMapping = computeLeaveAliases(mrow);

    // Extract the distinct leave names (with aliases if duplicates exist)
    const distinct_leave_names = Object.keys(leaveAliasesMapping);
    // For cells not already "WO" or "H", remove these from the default list
    const cellSourceExcludingWOH = getCellSourceExcludingWOH();

    // Loop through columns starting from index 6 (where leave cells begin)
    for (let col = DEFAULT_HEADERS.length; col < columnsLength; col++) {
      // Get the current cell value (e.g. "A", "HD", or a leave alias)
      const currentValue = currentWorkSheet.getValueFromCoords(col, rowIndex);

      // Build dropdown source based on current value
      const dropdownSource = buildDropdownSource(
        currentValue, 
        cellSourceExcludingWOH, 
        distinct_leave_names, 
        mrow, 
        rowBalanceMapsObj, 
        baseBalanceMap
      );

      // Create validation function
      const validateFn = createValidateFunction(
        currentWorkSheet, 
        col, 
        rowIndex, 
        currentValue, 
        mrow, 
        distinct_leave_names, 
        rowBalanceMapsObj, 
        baseBalanceMap
      );

      // Create cell properties
      const cellProps = createCellProperties(dropdownSource, validateFn, currentValue);

      // Finally, update the cell's property with the new dropdown settings
      currentWorkSheet.setProperty(col, rowIndex, cellProps);
    }
  }, [musterReports, daysColumns, buildDropdownSource, computeBalanceMap, computeLeaveAliases, getCellSourceExcludingWOH, createValidateFunction, createCellProperties, rowBalanceMapsObj]);

  // Get the original cell tag from the initial spreadsheet data
  const getOriginalCellTag = useCallback((code, day) => {
    const key = "Emp ID";
    const originalRow = originalSpreadsheetData.find(
      (r) => r[key]?.toString() === code?.toString()
    );
    return originalRow ? originalRow[day] : "N/A";
  }, [originalSpreadsheetData]);

  // Calculate selection details from coordinates
  const calculateSelectionDetails = useCallback((pxVal, pyVal, uxVal, uyVal) => {
    // Check if all coordinates are numbers
    if (typeof pxVal === "number" && typeof pyVal === "number" && 
        typeof uxVal === "number" && typeof uyVal === "number") {
      // Calculate selection range
      const startCol = Math.min(pxVal, uxVal);
      const endCol = Math.max(pxVal, uxVal);
      const startRow = Math.min(pyVal, uyVal);
      const endRow = Math.max(pyVal, uyVal);

      // Calculate selection count
      const selectionCount = (endCol - startCol + 1) * (endRow - startRow + 1);

      // Return selection details
      return { startCol, endCol, startRow, endRow, selectionCount };
    }

    return null;
  }, []);

  // Convert row data to an object
  const transformRowDataToObject = useCallback((rowData, columns) => {
    const values = rowData.map((cell) => cell?.v);
    const keys = columns.map((col) => col.title);
    return _.zipObject(keys, values);
  }, []);
  // Toggle the attendance log sidebar
  const handleAttendanceLogSidebar = useCallback(() => {
    setAttendanceLog(!attendanceLog);
  }, [attendanceLog]);

  // Handle search criteria changes and fetch muster reports
  const handleSearch = useCallback(() => {
    const criteria = {
      startDate: dayjs(selectedFilterStartDate).format("YYYY-MM-DD"),
      endDate: dayjs(selectedFilterEndDate).format("YYYY-MM-DD"),
      branch: selectedFilterBranch,
    };

    // Avoid duplicate API calls
    if (
      criteria.startDate === previousSearchCriteria.startDate &&
      criteria.endDate === previousSearchCriteria.endDate &&
      criteria.branch === previousSearchCriteria.branch
    ) {
      toast.info('Please select different dates or branch to search')
      return;
    }

    // Update previous search criteria
    previousSearchCriteria.startDate = criteria.startDate;
    previousSearchCriteria.endDate = criteria.endDate;
    previousSearchCriteria.branch = criteria.branch;

    // Reset state
    setIsSearchLoading(true);
    setSpreadsheetData([]);
    setSpreadsheetKey((prev) => prev + 1);
    setModifiedData([]);
    setShowSpreadsheet(true);

    // Fetch data
    dispatch(
      fetchMusterReports({
        filter_start_date: criteria.startDate,
        filter_end_date: criteria.endDate,
        filter_branch: criteria.branch,
      })
    );
  }, [
    dispatch,
    selectedFilterBranch,
    selectedFilterStartDate,
    selectedFilterEndDate,
    previousSearchCriteria,
  ]);

  // Handle start date picker changes
  const handleStartDateChange = useCallback((date) => {
    setSelectedFilterStartDate(date);
    const monthFromDate = dayjs(date)
      .add(1, "month")
      .subtract(1, "day")
      .toDate();

    if (monthFromDate <= yesterdayDate) {
      setMaxToDate(monthFromDate);
      setSelectedFilterEndDate(monthFromDate);
    } else {
      setMaxToDate(yesterdayDate);
      setSelectedFilterEndDate(yesterdayDate);
    }
  }, [yesterdayDate]);


  // Reset the spreadsheet to its original state
  const handleReset = useCallback(() => {
    // Reset the rowBalanceMapsObj
    Object.keys(rowBalanceMapsObj).forEach(
      (key) => delete rowBalanceMapsObj[key]
    );

    musterReports.forEach((row) => {
      rowBalanceMapsObj[row.code] = computeBalanceMap(row);
    });

    // Reset the spreadsheet data and cell styles
    bulkUpdateFlagRef.current = false;
    isRevertingRef.current = false;

    const clonedOriginalData = _.cloneDeep(originalSpreadsheetData);
    const newCellStyles = generateCellStyles(clonedOriginalData, daysColumns);

    setSpreadsheetData(clonedOriginalData);
    setCellStyles(newCellStyles);
    setModifiedData([]);

    // Force a refresh of the spreadsheet
    setShowSpreadsheet(false);
    setSpreadsheetKey((prev) => prev + 1);

    setTimeout(() => {
      setShowSpreadsheet(true);
    }, 0);
  }, [musterReports, originalSpreadsheetData, daysColumns, generateCellStyles, computeBalanceMap, rowBalanceMapsObj]);

  // Handler for resetting specific columns
  const handleResetColumns = useCallback(() => {
    if (selectedColumns.length === 0) {
      toast.info("Please select at least one column to reset");
      return;
    }

    if (modifiedData.length === 0) {
      toast.info('No changes to reset')
      return
    }

    // If all columns are selected, use the existing reset all function
    const allDayColumns = daysColumns
      .filter(col => isValidDay(col.title))
      .map(col => col.title);

    if (selectedColumns.length === allDayColumns.length) {
      handleReset();
      setSelectedColumns([]);
      return;
    }

    // Handle resetting multiple specific columns
    const currentData = _.cloneDeep(spreadsheetData);

    // Create updated data with selected columns reset to original values
    const updatedData = currentData.map((row, rowIndex) => {
      const originalRow = originalSpreadsheetData[rowIndex];
      if (!originalRow) return row;

      const updatedRow = { ...row };
      selectedColumns.forEach(columnTitle => {
        updatedRow[columnTitle] = originalRow[columnTitle];
      });

      return updatedRow;
    });

    // Update the modified data state - remove entries for the reset columns
    setModifiedData(prev => {
      return prev.map(item => {
        const newChanges = { ...item.changes };
        let hasRemainingChanges = false;

        // Remove changes for selected columns
        Object.keys(newChanges).forEach(columnTitle => {
          if (selectedColumns.includes(columnTitle)) {
            delete newChanges[columnTitle];
          } else if (newChanges[columnTitle]) {
            hasRemainingChanges = true;
          }
        });

        // If no more changes in this row, return null to be filtered out
        if (!hasRemainingChanges) {
          return null;
        }

        // Return updated row with the column changes removed
        const updatedRowData = { ...item.rowData };
        selectedColumns.forEach(columnTitle => {
          updatedRowData[columnTitle] = originalSpreadsheetData.find(
            r => r["Emp ID"] === item.rowData["Emp ID"]
          )?.[columnTitle];
        });

        return {
          ...item,
          changes: newChanges,
          rowData: updatedRowData
        };
      }).filter(Boolean); // Filter out null items (rows with no changes left)
    });

    // Reset and recalculate leave balances
    Object.keys(rowBalanceMapsObj).forEach(key => delete rowBalanceMapsObj[key]);

    musterReports.forEach(row => {
      rowBalanceMapsObj[row.code] = computeBalanceMap(row);
    });

    // Apply all changes from updatedData compared to originalSpreadsheetData
    updatedData.forEach((row, rowIndex) => {
      const employeeCode = row["Emp ID"];
      if (!employeeCode || !rowBalanceMapsObj[employeeCode]) return;

      const originalRow = originalSpreadsheetData[rowIndex];
      if (!originalRow) return;

      // Go through each day column and adjust balances for differences
      daysColumns.forEach(col => {
        const dayCol = col.title;
        if (!isValidDay(dayCol) || selectedColumns.includes(dayCol)) return;

        const currentValue = row[dayCol];
        const originalValue = originalRow[dayCol];

        // Skip if values are the same
        if (currentValue === originalValue) return;

        // If original was attendance and current is leave, debit leave balance
        if (isAttendance(originalValue) && isLeave(currentValue)) {
          const debitAmount = originalValue === ATTENDANCE_CODES.HALF_DAY ? 0.5 : 1;
          rowBalanceMapsObj[employeeCode][currentValue] -= debitAmount;
        }
        // If original was leave and current is attendance, credit leave balance
        else if (isLeave(originalValue) && isAttendance(currentValue)) {
          const creditAmount = currentValue === ATTENDANCE_CODES.HALF_DAY ? 0.5 : 1;
          rowBalanceMapsObj[employeeCode][originalValue] += creditAmount;
        }
        // If both original and current are leaves but different, adjust both
        else if (isLeave(originalValue) && isLeave(currentValue) && originalValue !== currentValue) {
          rowBalanceMapsObj[employeeCode][originalValue] += 1;
          rowBalanceMapsObj[employeeCode][currentValue] -= 1;
        }
      });
    });

    // Build map of modified cells
    const modifiedCells = {};
    modifiedData.forEach(modifiedRow => {
      Object.keys(modifiedRow.changes).forEach(day => {
        if (selectedColumns.includes(day)) return;

        const colIndex = daysColumns.findIndex(col => col.title === day);
        if (colIndex >= 0) {
          const colLetter = getColumnName(colIndex);
          const cellKey = `${colLetter}${parseInt(modifiedRow.rowIndex) + 1}`;
          modifiedCells[cellKey] = true;
        }
      });
    });

    // Update cell styling
    const newCellStyles = generateCellStyles(updatedData, daysColumns, modifiedCells);
    setCellStyles(newCellStyles);

    // Apply updates
    setSpreadsheetData(updatedData);
    setShowSpreadsheet(false);
    setSpreadsheetKey(prev => prev + 1);

    setTimeout(() => {
    setShowSpreadsheet(true);
    }, 0);

    setSelectedColumns([]);
    toast.success(`Selected columns have been reset to original values`);
  }, [
    selectedColumns, 
    spreadsheetData, 
    originalSpreadsheetData, 
    daysColumns, 
    musterReports, 
    rowBalanceMapsObj,
    generateCellStyles, 
    handleReset, 
    modifiedData,
    computeBalanceMap,
  ]);

  // Toggle select all columns
  const handleSelectAll = useCallback(() => {
    const allDayColumns = daysColumns
      .filter(col => isValidDay(col.title))
      .map(col => col.title);

    if (selectedColumns.length !== allDayColumns.length) {
      setSelectedColumns(allDayColumns);
      setIsAllSelected(true);
    } else {
      setSelectedColumns([]);
      setIsAllSelected(false);
    }
  }, [daysColumns, selectedColumns]);

  // Handle column selection change
  const handleColumnSelectionChange = useCallback((e) => {
    setSelectedColumns(e.value);

    const allDayColumns = daysColumns
      .filter(col => isValidDay(col.title))
      .map(col => col.title);

    setIsAllSelected(e.value.length === allDayColumns.length && e.value.length > 0);
  }, [daysColumns]);

  // Open the attendance log sidebar on double-click
  const openAttendanceLogs = useCallback((employee, date) => {
    if (employee && date) {
      setRegSidebarData({
        id: employee.user_id,
        name: employee.name ?? "",
        request_date: date,
        device_employee_id: employee.device_employee_id,
        code: employee.code,
      });
      setAttendanceLog(true);
    }
  }, []);

  // Helper function to get tag value based on attendance code
  const getTagValue = (code) => {
    switch (code) {
      case ATTENDANCE_CODES.HALF_DAY:
        return 3;
      case ATTENDANCE_CODES.FULL_DAY:
        return 2;
      case ATTENDANCE_CODES.ABSENT:
        return 4;
      case ATTENDANCE_CODES.HOLIDAY:
        return 1;
      case ATTENDANCE_CODES.WEEK_OFF:
        return 6;
      default:
        return 0;
    }
  };

  const preparePayloadData = useCallback(
    (modifiedData, sheetData, startDate, endDate) => {
      // Initialize the categorized payload structure
      const payloadObj = {
        absent_arr: [],
        full_day_arr: [],
        half_day_arr: [],
        leave_debit_arr: [],
        leave_credit_arr: [],
        dual_leaves_arr: [],
        prev_half_day_arr: [],
        empty_je_ids_arr: [],
        prev_val_weekoff: [],
        new_val_weekoff: [],
      };

      // Create a map of muster report rows indexed by employee code
      const musterMap = _.keyBy(sheetData, "code");

      // Process each modified row
      for (const { rowData, changes } of modifiedData) {
        const code = rowData["Emp ID"];
        const empRecord = findEmployeeByCode(musterMap, code);
        if (!empRecord) continue;

        const userId = empRecord.user_id;
        // Compute leave aliases mapping for this employee
        const leaveAliasesMapping = computeLeaveAliases(empRecord);

        // Process each day that was changed
        for (const dayCol of Object.keys(changes)) {
          const { newValue, oldValue } = changes[dayCol];

          // Skip if the day is not valid
          if (!isValidDay(dayCol)) continue;

          // Get the full date for this day column
          const dateKey = getDateWithinRange(startDate, endDate, dayCol);
          const dateObj = dayjs(dateKey);

          // Get journal entry IDs for this day
          const je_ids_key = `jeIds_day_${dateObj.month() + 1}_${dateObj.date()}`;
          const je_ids = empRecord[je_ids_key];

          // Get the original cell tag from initial data
          const originalCellTag = getOriginalCellTag(code, dayCol);

          // Skip if the new value is the same as the original
          if (newValue === originalCellTag) continue;

          // Prepare the base entry object with common fields
          const baseEntry = {
            userId,
            name: empRecord.name ?? "",
            request_date: dateKey,
            je_ids: je_ids,
            oldValue: originalCellTag,
            newValue: newValue,
          };

          // If je_ids is null or empty, add to empty_je_ids_arr
          if (!je_ids || je_ids.length === 0) {
            const entry = {
              ...baseEntry,
              oldTag: getTagValue(originalCellTag),
              newTag: getTagValue(newValue),
            }
            if (newValue === ATTENDANCE_CODES.HALF_DAY) {
              entry.tag = 3
            } else if (newValue === ATTENDANCE_CODES.FULL_DAY) {
              entry.tag = 2;
            } else if (newValue === ATTENDANCE_CODES.ABSENT) {
              entry.tag = 4;
            } else if (newValue === ATTENDANCE_CODES.WEEK_OFF) {
              entry.tag = 1;
              entry.flag = 9;
            } else if (isLeave(newValue)) {
              entry.flag = 5;
              entry.session = 5;
              entry.leave_id = newValue !== LWP_LEAVE ? leaveAliasesMapping[newValue]?.leaveId : 0
              entry.leave_name = newValue !== LWP_LEAVE ? leaveAliasesMapping[newValue]?.originalName : 'LWP'
            }
            payloadObj.empty_je_ids_arr.push(entry);
            continue;
          }

          if (originalCellTag === ATTENDANCE_CODES.WEEK_OFF) {
            const entry = {
              ...baseEntry
            };

            if (isLeave(newValue)) {
              entry.leaveId = newValue !== LWP_LEAVE ? leaveAliasesMapping[newValue]?.leaveId : 0
              entry.leave_name = newValue !== LWP_LEAVE ? leaveAliasesMapping[newValue]?.originalName : 'LWP'
              entry.flag = 5;
              entry.tag = 1;
              entry.session = 5;
            } else if (newValue === ATTENDANCE_CODES.ABSENT) {
              entry.tag = 4
            }  else if (newValue === ATTENDANCE_CODES.FULL_DAY) {
              entry.tag = 2
            } else if (newValue === ATTENDANCE_CODES.HALF_DAY) {
              entry.tag = 3
            }
            payloadObj.prev_val_weekoff.push(entry)
           }

          else if (newValue === ATTENDANCE_CODES.WEEK_OFF) {
            const entry = {
              ...baseEntry,
              tag: 1,
              flag: 9
            };
            if (isLeave(originalCellTag)) {
                entry.leaveId = originalCellTag !== LWP_LEAVE ? leaveAliasesMapping[originalCellTag]?.leaveId : 0
                entry.leave_name = originalCellTag !== LWP_LEAVE ? leaveAliasesMapping[originalCellTag]?.originalName : 'LWP'
                entry.flag = 5;
                entry.session = 5;
            } else if (originalCellTag === ATTENDANCE_CODES.ABSENT) {
                entry.pevTag = 4
            }  else if (originalCellTag === ATTENDANCE_CODES.FULL_DAY) {
                entry.prevTag = 2
            } else if (originalCellTag === ATTENDANCE_CODES.HALF_DAY) {
                entry.prevTag = 3
                entry.flag = 0
                entry.leave_id = 0;
                entry.checkPrevLeave = true
            }

            payloadObj.new_val_weekoff.push(entry)
          }

          else if (originalCellTag === ATTENDANCE_CODES.HALF_DAY) {
            const entry = {
              ...baseEntry,
              newValue: newValue,
              leave_id: newValue !== LWP_LEAVE ? leaveAliasesMapping[newValue]?.leaveId : 0,
              leave_name: newValue !== LWP_LEAVE ? leaveAliasesMapping[newValue]?.originalName : 'LWP'
            }

            if (newValue === ATTENDANCE_CODES.HALF_DAY) {
              // entry.flag = 6;
              // entry.tag = 3;
              // entry.session = 0.5;
            } else if (newValue === ATTENDANCE_CODES.FULL_DAY) {
              // entry.flag = 5;
              entry.tag = 2;
              // entry.session = 1.0;
            } else if (newValue === ATTENDANCE_CODES.ABSENT) {
              // entry.flag = 5;
              entry.tag = 4;
              // entry.session = 1.0;
            } else if (isLeave(newValue)) {
              entry.flag = 6;
            }

            payloadObj.prev_half_day_arr.push(entry)
          }
          // Both old and new values are attendance types (not leaves)
          else if (!isLeave(newValue) && !isLeave(originalCellTag)) {
            // New value is Absent, old value was HD or FD
            if (
              newValue === ATTENDANCE_CODES.ABSENT &&
              (originalCellTag === ATTENDANCE_CODES.HALF_DAY ||
                originalCellTag === ATTENDANCE_CODES.FULL_DAY)
            ) {
              payloadObj.absent_arr.push({
                ...baseEntry,
                tag: 4, // Tag for Absent
                oldTag: originalCellTag === ATTENDANCE_CODES.HALF_DAY ? 3 : 2, // 3 for HD, 2 for FD
              });
            }
            // New value is Full Day, old value was HD or A
            else if (
              newValue === ATTENDANCE_CODES.FULL_DAY &&
              (originalCellTag === ATTENDANCE_CODES.HALF_DAY ||
                originalCellTag === ATTENDANCE_CODES.ABSENT)
            ) {
              payloadObj.full_day_arr.push({
                ...baseEntry,
                tag: 2, // Tag for Full Day
                oldTag: originalCellTag === ATTENDANCE_CODES.HALF_DAY ? 3 : 4, // 3 for HD, 4 for A
              });
            }
            // New value is Half Day, old value was A or FD
            else if (
              newValue === ATTENDANCE_CODES.HALF_DAY &&
              (originalCellTag === ATTENDANCE_CODES.ABSENT ||
                originalCellTag === ATTENDANCE_CODES.FULL_DAY)
            ) {
              payloadObj.half_day_arr.push({
                ...baseEntry,
                tag: 3, // Tag for Half Day
                oldTag: originalCellTag === ATTENDANCE_CODES.ABSENT ? 4 : 2,
              });
            }
          }

          // New value is a leave, old value was attendance (Debit Leave)
          else if (isLeave(newValue) && !isLeave(originalCellTag)) {
            const entry = {
              ...baseEntry,
              oldTag: getTagValue(originalCellTag),
              leave_id: newValue !== LWP_LEAVE ? leaveAliasesMapping[newValue]?.leaveId : 0,
              leave_name: newValue !== LWP_LEAVE ? leaveAliasesMapping[newValue]?.originalName : 'LWP'
            };

            // Set flags and tags based on previous value
            if (originalCellTag === ATTENDANCE_CODES.HALF_DAY) {
              entry.flag = 6;
              entry.tag = 3;
              entry.session = 6;
            } else if (originalCellTag === ATTENDANCE_CODES.FULL_DAY) {
              entry.flag = 5;
              entry.tag = 1;
              entry.session = 5;
            } else if (originalCellTag === ATTENDANCE_CODES.ABSENT) {
              entry.flag = 5;
              entry.tag = 1;
              entry.session = 5;
            }
            payloadObj.leave_debit_arr.push(entry);
          }

          // Old value was a leave, new value is attendance (Credit Leave)
          else if (!isLeave(newValue) && isLeave(originalCellTag)) {
            const entry = {
              ...baseEntry,
              newTag: getTagValue(newValue),
              leave_id: originalCellTag !== LWP_LEAVE ? leaveAliasesMapping[originalCellTag]?.leaveId : 0,
              leave_name: originalCellTag !== LWP_LEAVE ? leaveAliasesMapping[originalCellTag]?.originalName : 'LWP'
            };

            // Set flags and tags based on new value
            if (newValue === ATTENDANCE_CODES.HALF_DAY) {
              entry.flag = 6;
              entry.tag = 3;
              entry.session = 6;
            } else if (newValue === ATTENDANCE_CODES.FULL_DAY) {
              entry.flag = 5;
              entry.tag = 2;
              entry.session = 5;
            } else if (newValue === ATTENDANCE_CODES.ABSENT) {
              entry.flag = 5;
              entry.tag = 4;
              entry.session = 5;
            }

            payloadObj.leave_credit_arr.push(entry);
          }

          // Both old and new values are leaves (but different types)
          else if (isLeave(newValue) && isLeave(originalCellTag)) {
            const entry = {
              ...baseEntry
            };

            // Handle old leave
            if (originalCellTag === LWP_LEAVE) {
              entry.old_leave_id = 0;
              entry.old_leave_name = 'LWP';
            } else {
              entry.old_leave_id = leaveAliasesMapping[originalCellTag]?.leaveId;
              entry.old_leave_name = leaveAliasesMapping[originalCellTag]?.originalName;
            }

            // Handle new leave
            if (newValue === LWP_LEAVE) {
              entry.new_leave_id = 0;
              entry.new_leave_name = 'LWP';
            } else {
              entry.new_leave_id = leaveAliasesMapping[newValue]?.leaveId;
              entry.new_leave_name = leaveAliasesMapping[newValue]?.originalName;
            }

            payloadObj.dual_leaves_arr.push(entry);
          }
        }
      }

      // groupJeIds for absent_arr, full_days_arr and half_days_arr
      payloadObj.absent_arr = groupByTagAndCombineJeIds(payloadObj.absent_arr);
      payloadObj.full_day_arr = groupByTagAndCombineJeIds(payloadObj.full_day_arr);
      payloadObj.half_day_arr = groupByTagAndCombineJeIds(payloadObj.half_day_arr);

      return { 
        changes: payloadObj, 
        from_date: dayjs(selectedFilterStartDate).format("YYYY-MM-DD"), to_date: dayjs(selectedFilterEndDate).format("YYYY-MM-DD") 
      };
