  // Generate custom cell styles based on muster report data
  const generateCellStyles = useCallback(
    (rows, cols, modifiedCells = {}) => {
      if (_.isEmpty(rows) || _.isEmpty(cols)) return {};

      const styles = {};
      const musterMap = _.keyBy(musterReports, (mrow) => mrow.code?.toString());

      const validColumns = _.compact(
        cols.map((col, colIndex) => {
          if (!isValidDay(col.title)) return null;

          const dayObj = dayjs(col.title, "DD-MMM");
          if (!dayObj.isValid()) return null;

          return {
            ...col,
            colLetter: getColumnName(colIndex),
            tagKey: `tag_day_${dayObj.month() + 1}_${dayObj.date()}`,
            errKey: `err_day_${dayObj.month() + 1}_${dayObj.date()}`,
            leaveKey: `leave_name_day_${dayObj.month() + 1}_${dayObj.date()}`,
            dayKey: `day_${dayObj.month() + 1}_${dayObj.date()}`
          };
        })
      );

      rows.forEach((row, rowIndex) => {
        const musterRow = musterMap[row["Emp ID"]?.toString()];

        validColumns.forEach((validCol) => {
          const cellKey = `${validCol.colLetter}${rowIndex + 1}`;
          const tagValue = musterRow?.[validCol.tagKey];
          const errValue = musterRow?.[validCol.errKey];
          const leaveValue = musterRow?.[validCol.leaveKey];
          const dayValue = musterRow?.[validCol.dayKey];
          const isModified = modifiedCells[cellKey] !== undefined;

          // Check if cell contains both a half day and a leave
          // const isHalfDayWithLeave = dayValue === 'Half Day' && leaveValue;

        if (isModified) {
          styles[cellKey] = `font-weight: 550; color: #000000;background-color: #EFF2E9`;
        // } else if (isHalfDayWithLeave) {
        //   // Special styling for half-day + leave combination
        //   styles[cellKey] = `font-weight: 550; color: #8558df`;
        } else if (
          (errValue && errValue === "Problem") && 
          ((tagValue && tagValue === "Absent") || (dayValue && (dayValue === 'Absent' || (dayValue === 'Half Day' && tagValue !== 'Half Day'))))) {
          styles[cellKey] = `font-weight: 550; color: #C52031`;
        } else if (tagValue && tagValue !== "Normal") {
          styles[cellKey] = `font-weight: 550; color:rgb(15, 94, 158)`;
        } else {
          styles[cellKey] = `font-weight: 550; color: #000;`;
        }
        });
      });

      return styles;
