  // Handle start date picker changes
  const handleStartDateChange = useCallback((date) => {
    setSelectedFilterStartDate(date);
    const monthFromDate = dayjs(date)
      .add(1, "month")
      .subtract(1, "day")
      .toDate();

    if (monthFromDate <= yesterdayDate) {
      setMaxToDate(monthFromDate);
      setSelectedFilterEndDate(monthFromDate);
    } else {
      setMaxToDate(yesterdayDate);
      setSelectedFilterEndDate(yesterdayDate);
    }
  }, [yesterdayDate]);


  // Reset the spreadsheet to its original state
  const handleReset = useCallback(() => {
    // Reset the rowBalanceMapsObj
    Object.keys(rowBalanceMapsObj).forEach(
      (key) => delete rowBalanceMapsObj[key]
    );

    musterReports.forEach((row) => {
      rowBalanceMapsObj[row.code] = computeBalanceMap(row);
    });

    // Reset the spreadsheet data and cell styles
    bulkUpdateFlagRef.current = false;
    isRevertingRef.current = false;

    const clonedOriginalData = _.cloneDeep(originalSpreadsheetData);
    const newCellStyles = generateCellStyles(clonedOriginalData, daysColumns);

    setSpreadsheetData(clonedOriginalData);
    setCellStyles(newCellStyles);
    setModifiedData([]);

    // Force a refresh of the spreadsheet
    setShowSpreadsheet(false);
    setSpreadsheetKey((prev) => prev + 1);

    setTimeout(() => {
      setShowSpreadsheet(true);
    }, 0);
  }, [musterReports, originalSpreadsheetData, daysColumns, generateCellStyles, computeBalanceMap, rowBalanceMapsObj]);

  // Handler for resetting specific columns
  const handleResetColumns = useCallback(() => {
    if (selectedColumns.length === 0) {
      toast.info("Please select at least one column to reset");
      return;
    }

    if (modifiedData.length === 0) {
      toast.info('No changes to reset')
      return
    }

    // If all columns are selected, use the existing reset all function
    const allDayColumns = daysColumns
      .filter(col => isValidDay(col.title))
      .map(col => col.title);

    if (selectedColumns.length === allDayColumns.length) {
      handleReset();
      setSelectedColumns([]);
      return;
    }

    // Handle resetting multiple specific columns
    const currentData = _.cloneDeep(spreadsheetData);

    // Create updated data with selected columns reset to original values
    const updatedData = currentData.map((row, rowIndex) => {
      const originalRow = originalSpreadsheetData[rowIndex];
      if (!originalRow) return row;

      const updatedRow = { ...row };
      selectedColumns.forEach(columnTitle => {
        updatedRow[columnTitle] = originalRow[columnTitle];
      });

      return updatedRow;
    });

    // Update the modified data state - remove entries for the reset columns
    setModifiedData(prev => {
      return prev.map(item => {
        const newChanges = { ...item.changes };
        let hasRemainingChanges = false;

        // Remove changes for selected columns
        Object.keys(newChanges).forEach(columnTitle => {
          if (selectedColumns.includes(columnTitle)) {
            delete newChanges[columnTitle];
          } else if (newChanges[columnTitle]) {
            hasRemainingChanges = true;
          }
        });

        // If no more changes in this row, return null to be filtered out
        if (!hasRemainingChanges) {
          return null;
        }

        // Return updated row with the column changes removed
        const updatedRowData = { ...item.rowData };
        selectedColumns.forEach(columnTitle => {
          updatedRowData[columnTitle] = originalSpreadsheetData.find(
            r => r["Emp ID"] === item.rowData["Emp ID"]
          )?.[columnTitle];
        });

        return {
          ...item,
          changes: newChanges,
          rowData: updatedRowData
        };
      }).filter(Boolean); // Filter out null items (rows with no changes left)
    });

    // Reset and recalculate leave balances
    Object.keys(rowBalanceMapsObj).forEach(key => delete rowBalanceMapsObj[key]);

    musterReports.forEach(row => {
      rowBalanceMapsObj[row.code] = computeBalanceMap(row);
    });

    // Apply all changes from updatedData compared to originalSpreadsheetData
    updatedData.forEach((row, rowIndex) => {
      const employeeCode = row["Emp ID"];
      if (!employeeCode || !rowBalanceMapsObj[employeeCode]) return;

      const originalRow = originalSpreadsheetData[rowIndex];
      if (!originalRow) return;

      // Go through each day column and adjust balances for differences
      daysColumns.forEach(col => {
        const dayCol = col.title;
        if (!isValidDay(dayCol) || selectedColumns.includes(dayCol)) return;

        const currentValue = row[dayCol];
        const originalValue = originalRow[dayCol];

        // Skip if values are the same
        if (currentValue === originalValue) return;

        // If original was attendance and current is leave, debit leave balance
        if (isAttendance(originalValue) && isLeave(currentValue)) {
          const debitAmount = originalValue === ATTENDANCE_CODES.HALF_DAY ? 0.5 : 1;
          rowBalanceMapsObj[employeeCode][currentValue] -= debitAmount;
        }
        // If original was leave and current is attendance, credit leave balance
        else if (isLeave(originalValue) && isAttendance(currentValue)) {
          const creditAmount = currentValue === ATTENDANCE_CODES.HALF_DAY ? 0.5 : 1;
          rowBalanceMapsObj[employeeCode][originalValue] += creditAmount;
        }
        // If both original and current are leaves but different, adjust both
        else if (isLeave(originalValue) && isLeave(currentValue) && originalValue !== currentValue) {
          rowBalanceMapsObj[employeeCode][originalValue] += 1;
          rowBalanceMapsObj[employeeCode][currentValue] -= 1;
        }
      });
    });

    // Build map of modified cells
    const modifiedCells = {};
    modifiedData.forEach(modifiedRow => {
      Object.keys(modifiedRow.changes).forEach(day => {
        if (selectedColumns.includes(day)) return;

        const colIndex = daysColumns.findIndex(col => col.title === day);
        if (colIndex >= 0) {
          const colLetter = getColumnName(colIndex);
          const cellKey = `${colLetter}${parseInt(modifiedRow.rowIndex) + 1}`;
          modifiedCells[cellKey] = true;
        }
      });
    });

    // Update cell styling
    const newCellStyles = generateCellStyles(updatedData, daysColumns, modifiedCells);
    setCellStyles(newCellStyles);

    // Apply updates
    setSpreadsheetData(updatedData);
    setShowSpreadsheet(false);
    setSpreadsheetKey(prev => prev + 1);

    setTimeout(() => {
    setShowSpreadsheet(true);
    }, 0);

    setSelectedColumns([]);
    toast.success(`Selected columns have been reset to original values`);
  }, [
