    switch (eventName) {
      case "onbeforepaste":
        return handleSpreadsheetEvents.handleOnBeforePaste({ worksheet, section, colIndex, rowIndex, newValue, oldValue });
      case "onbeforeselection":
        return handleSpreadsheetEvents.handleOnBeforeSelection({ worksheet, section, colIndex, rowIndex, newValue, oldValue });
      case "onchange":
        return handleSpreadsheetEvents.handleOnChangeEvent({ worksheet, section, colIndex, rowIndex, newValue, oldValue });
      case "onafterchanges":
        return handleSpreadsheetEvents.handleOnAfterChangesEvent({ worksheet, section, colIndex, rowIndex, newValue, oldValue });
      case "onload":
        return handleSpreadsheetEvents.handleOnLoadEvent({ worksheet, section, colIndex, rowIndex, newValue, oldValue });
      case "onclick":
        return handleSpreadsheetEvents.handleOnClickEvent({ worksheet, section, colIndex, rowIndex, newValue, oldValue });
      case "oncopy":
        return handleSpreadsheetEvents.handleOnCopy({ worksheet, section, colIndex, rowIndex, newValue, oldValue });
      case "onfilter":
        return handleSpreadsheetEvents.handleOnFilterEvent({ worksheet, section, colIndex, rowIndex, newValue, oldValue });
