  // Trigger initial search on component mount
  useEffect(() => {

  if (!isInitializing && selectedFilterStartDate && selectedFilterEndDate) {
    handleSearch();
  }    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [initialRenderCount, selectedFilterStartDate, selectedFilterEndDate]);

  // Prepare spreadsheet data and columns when musterReports changes
  useEffect(() => {
    if (_.isEmpty(musterReports)) return;

    const dayArray = generateDayArray(reportStartDate, reportEndDate);
    const headers = [...DEFAULT_HEADERS, ...dayArray];
    const dynamicNestedHeaders = generateNestedHeaders(dayArray);

    const daysCols = _.map(headers, (header) => {
      if (_.includes(dayArray, header)) {
        return { type: "dropdown", title: header, width: 80 };
      }
      return { 
        ...DEFAULT_HEADERS_PROPS, 
        title: header, 
        width: DEFAULT_HEADERS_WIDTHS[header] || DEFAULT_HEADERS_WIDTHS.default 
      };
    });

    const newSpreadsheetData = musterReports.map((musterData) => {
      const row = {
        "Emp ID": musterData.code,
        Name: musterData.name,
        Dept: musterData.department,
        Designation: musterData.designation,
      };

      _.forEach(dayArray, (day) => {
        const keys = generatePrefixedKeys(day);

        const dateObj = dayjs(day, "DD-MMM");
        const dayNum = dateObj.date();
        const monthNum = dateObj.month() + 1;
        const leaveKey = `leave_name_day_${monthNum}_${dayNum}`;
        const dayKey = `day_${monthNum}_${dayNum}`;
        const errKey = `err_day_${monthNum}_${dayNum}`;
        const tagKey = `tag_day_${monthNum}_${dayNum}`;

        let foundKey = _.find(keys, (k) => {
          const val = musterData[k];
          return val != null && !(k.includes("tag") && val === "Normal");
        });

        let dayValue = "Absent";
        let defaultKey = "";

        if (musterData[dayKey] && musterData[dayKey] === 'Half Day' && musterData[leaveKey]) {
          foundKey = dayKey
        }
        if (musterData[errKey] && musterData[errKey] === 'Problem') {
          if (musterData[leaveKey] && 
            ((musterData[tagKey] && musterData[tagKey] !== 'Half Day') || (musterData[dayKey] && musterData[dayKey] !== 'Half Day'))) {
            foundKey = leaveKey
          } else if (musterData[tagKey] && musterData[tagKey] !== 'Normal') {
            foundKey = tagKey
          } else if (musterData[dayKey] && musterData[tagKey] !== 'Absent') {
            foundKey = dayKey
          }
        }

        if (foundKey) {
          dayValue = musterData[foundKey];

          if (dayValue === 'Leave' &&  !musterData[leaveKey]) {
            defaultKey = leaveKey;
            dayValue = 'LWP'
          } else {
            defaultKey = foundKey;
          }
        }

        const { tag } = getStatus(
          musterData[defaultKey],
          dayValue,
          defaultKey?.split("_")[0]
        );

        row[day] = tag;
      });

      return row;
    });

    setSpreadsheetData(newSpreadsheetData);
    setOriginalSpreadsheetData(_.cloneDeep(newSpreadsheetData));
    setDaysColumns(daysCols);
    setCellStyles(generateCellStyles(newSpreadsheetData, daysCols));
    setNestedHeaders(dynamicNestedHeaders);
    setSpreadsheetKey((prev) => prev + 1);
  }, [
