import React, { useEffect, useState, useCallback, useMemo, useRef, lazy, Suspense } from "react";
import { useDispatch, useSelector } from "react-redux";
import { Container, Card, Row, Col, CardHeader, Spinner, Offcanvas, CardBody, } from "reactstrap";
import Header from "../../Layouts/Header";
import Flatpickr from "react-flatpickr";
import { Dropdown as Library1Dropdown } from "primereact/dropdown";
import { MultiSelect } from "primereact/multiselect";
import { getMusterSpreadsheetReports as fetchMusterReports } from "../../store/actions";
import dayjs from "dayjs";
import _, { debounce, groupBy } from "lodash";
import AttendanveSidebar from "../Reports/AttendanveSidebar";
import { licenceKey } from "../../Components/constants/licence_key";
import Skeleton from 'react-loading-skeleton'
import 'react-loading-skeleton/dist/skeleton.css';
import { handleExportExcel } from "../../Components/Common/SpreadsheetWrapper";
import { toast } from "react-toastify";
import "primereact/resources/themes/lara-light-indigo/theme.css";
import "primereact/resources/primereact.min.css";
import { bulkAttendanceUpdateApi, getPayrollFreezeListing, get_freeze_unfreeze_configs } from "../../helpers/awsapibackend_helper";
import { useMutation, useQuery } from "@tanstack/react-query";
import { PAYROLL_FREEZE_LISTING, REPORT_FREEZE_UNFREEZE_CONFIG } from "../../reactQueryKeys/PayrollQueryKeys";
import { useLocation, useNavigate, } from "react-router-dom";
import ConfirmationPopup from "../../Components/Common/ConfirmationPopup";
import CommonTooltip from '../../Components/Common/CommonTooltip';
import { getWorkingStatus } from "../Reports/ReportHelpers/attendanceMasterConfigsHelper";
import { getDateForDay } from "../Reports/ReportHelpers/musterConfigsHelper";

const SpreadsheetContainer = lazy(() => import('../../Components/Common/SpreadsheetContainer'));

const ATTENDANCE_CODES = {
  FULL_DAY: "FD",
  HALF_DAY: "HD",
  ABSENT: "A",
  WEEK_OFF: "WO",
  HOLIDAY: "H"
};

const TAG_VALUES_MAP = {
  [ATTENDANCE_CODES.HALF_DAY]: 3,
  [ATTENDANCE_CODES.FULL_DAY]: 2,
  [ATTENDANCE_CODES.ABSENT]: 4,
  [ATTENDANCE_CODES.HOLIDAY]: 1,
  [ATTENDANCE_CODES.WEEK_OFF]: 6
};

const ALLOWED_VALUES = [
  ATTENDANCE_CODES.ABSENT, 
  ATTENDANCE_CODES.HALF_DAY, 
  ATTENDANCE_CODES.FULL_DAY,
  ATTENDANCE_CODES.WEEK_OFF
];
const LWP_LEAVE = 'LWP';

const DEFAULT_VALUES = [
  ATTENDANCE_CODES.HALF_DAY, 
  ATTENDANCE_CODES.FULL_DAY, 
  ATTENDANCE_CODES.ABSENT, 
  ATTENDANCE_CODES.WEEK_OFF, 
  ATTENDANCE_CODES.HOLIDAY
];

const SPECIAL_CODES = [
  ATTENDANCE_CODES.WEEK_OFF, 
  ATTENDANCE_CODES.HOLIDAY
];

// Check if a value represents an attendance code
const isAttendance = (val) => [
  ATTENDANCE_CODES.ABSENT, 
  ATTENDANCE_CODES.FULL_DAY, 
  ATTENDANCE_CODES.HALF_DAY
].includes(val);

// Check if a value represents a leave code (not attendance and not special)
// const isLeave = (val) => val && !isAttendance(val) && !SPECIAL_CODES.includes(val);
// Update the isLeave function to explicitly recognize LWP as a leave
const isLeave = (val) => val === LWP_LEAVE || (val && !isAttendance(val) && !SPECIAL_CODES.includes(val));

const DEFAULT_HEADERS = ["Emp ID", "Name", "Dept", "Designation"];

const DEFAULT_HEADERS_WIDTHS = { 
  "Designation": 150, 
  "Dept": 150, 
  "Name": 200, 
  "default": 100 
};
const DEFAULT_HEADERS_PROPS = { 
  type: "text", 
  readOnly: true, 
  filter: true, 
  locked: true 
};

// Check if a given string is a valid day (e.g. "DD-MMM")
const isValidDay = (day) => dayjs(day, "DD-MMM", true).isValid();

const WORKSHEET_TITLE  = 'Bulk Attendance Adjustments'

// Generate Excel-like column names (A, B, C, ..., Z, AA, AB, etc.)
const getColumnName = (index) => {
  let name = "";
  while (index >= 0) {
    name = String.fromCharCode((index % 26) + 65) + name;
    index = Math.floor(index / 26) - 1;
  }
  return name;
};

function groupByTagAndCombineJeIds(array) {
  const groupedByTag = _.groupBy(array, 'tag');

  return _.map(groupedByTag, (items, tag) => {
    const allJeIds = _.flatMap(items, item => {
      const jeIdsStr = String(item.je_ids);
      const matches = jeIdsStr.match(/\d+/g) || [];
      return matches;
    });

    return {
      tag: parseInt(tag),
      je_ids: _.uniq(allJeIds).join(',')
    };
  });
}

function findEmployeeByCode(employeeMap, code) {
  return employeeMap[
    Object.keys(employeeMap)?.find(
      (key) =>
        parseInt(key) === parseInt(code) ||
        String(key)?.toLowerCase() === String(code)?.toLowerCase()
    )
  ];
}

const BulkAttendanceUpdate = () => {
  const dispatch = useDispatch();

  const {
    musterReports,
    musterReportSuccess,
    organizations,
    reportStartDate,
    reportEndDate,
  } = useSelector((state) => ({
    musterReports: state.Report.musterSpreadsheetReports,
    musterReportSuccess: state.Report.musterSpreadsheetReportSuccess,
    organizations: state.Organizations.organizations,
    reportStartDate: state.Report.reportStartDate,
    reportEndDate: state.Report.reportEndDate,
    isBulkTagsUpdate: state.Report.isBulkTagsUpdate,
    bulkTagsUpdateError: state.Report.bulkTagsUpdateError
  }));
  const [nestedHeaders, setNestedHeaders] = useState([]);
  const [initialRenderCount, setInitialRenderCount] = useState(0)
  const [selectedFilterBranch, setSelectedFilterBranch] = useState("");
  const [selectedFilterStartDate, setSelectedFilterStartDate] = useState(null);
  const [selectedFilterEndDate, setSelectedFilterEndDate] = useState(null);
  const [isSearchLoading, setIsSearchLoading] = useState(false);
  const [organizationsList, setOrganizationsList] = useState([]);
  const [spreadsheetData, setSpreadsheetData] = useState([]);
  const [originalSpreadsheetData, setOriginalSpreadsheetData] = useState([]);
  const [daysColumns, setDaysColumns] = useState([]);
  const [spreadsheetKey, setSpreadsheetKey] = useState(0);
  const [cellStyles, setCellStyles] = useState({});
  const [modifiedData, setModifiedData] = useState([]);
  const [regsidebardata, setRegSidebarData] = useState({});
  const [attendanceLog, setAttendanceLog] = useState(false);
  const [yesterdayDate] = useState(dayjs(new Date()).subtract(1, "day").toDate());
  const [maxToDate, setMaxToDate] = useState(yesterdayDate);
  const [tagLoader, setTagLoader] = useState(false);
  const [showSpreadsheet, setShowSpreadsheet] = useState(false);
  const [selectedColumns, setSelectedColumns] = useState([]);
  const [isAllSelected, setIsAllSelected] = useState(false);
  const [selectedDays, setSelectedDays] = useState(0);
  const [resultFromDate, setResultFromDate] = useState(dayjs(selectedFilterStartDate).format("YYYY-MM-DD"));
  const [resultToDate, setResultToDate] = useState(dayjs(selectedFilterEndDate).format("YYYY-MM-DD"));
  const [unlockStartDate, setUnlockStartDate] = useState(null);
  const [unlockEndDate, setUnlockEndDate] = useState(null);
  const [isUnlocked, setIsUnlocked] = useState(false);
  const [isConfirmationPopupOpen, setIsConfirmationPopupOpen] = useState(false);
  const [isInitializing, setIsInitializing] = useState(true);

  const navigate = useNavigate();
  const location = useLocation();

  const isRestoringDropdownValueRef = useRef(false);

  const fullScreenRef = useRef(null);
  const bulkUpdateFlagRef = useRef(false);
  const isRevertingRef = useRef(false);

  const getDateWithinRange = useCallback((startStr, endStr, displayDay) => {
      const start = dayjs(startStr, "YYYY-MM-DD");
      const end = dayjs(endStr, "YYYY-MM-DD");
      let result = dayjs(displayDay, "DD-MMM").year(start.year());

      if (result.isBefore(start)) result = result.add(1, "year");
      if (result.isAfter(end)) result = result.subtract(1, "year");
      return result.format("YYYY-MM-DD");
    }, []);

  const getDayNameFromDate = useCallback((dateStr) => {
    if (!isValidDay(dateStr)) return "";

    const fullDate = getDateWithinRange(
      dayjs(selectedFilterStartDate).format("YYYY-MM-DD"),
      dayjs(selectedFilterEndDate).format("YYYY-MM-DD"),
      dateStr
    );
    return dayjs(fullDate).format('ddd');
  }, [selectedFilterStartDate, selectedFilterEndDate, getDateWithinRange]);

  const generateNestedHeaders = useCallback((dayArray) => {
    const row = [
      { title: "Employees", colspan: 2, frozen: true, align: "center" },
      { title: "Hierarchy", colspan: 2, frozen: false, align: "center" },
      ...dayArray.map(day => ({ title: day, colspan: 1, frozen: false, align: "center" })),
      { title: "", colspan: "11", frozen: false, align: "center"}
    ];
    return [row];
  }, []);

  const previousSearchCriteria = useMemo(() => ({
    startDate: null,
    endDate: null,
    branch: ""
  }), []);

  const rowBalanceMapsObj = useMemo(() => ({}), []);

  const { mutate: submitBulkTagUpdates, isPending: isTagUpdatePending } = useMutation({

    mutationFn: bulkAttendanceUpdateApi,
    onSuccess: (data) => {
      if (data?.statusCode === 200) {
        toast.success(data?.message || "Changes saved successfully");
        // Reset state
        setIsSearchLoading(true);
        setSpreadsheetData([]);
        setSpreadsheetKey((prev) => prev + 1);
        setModifiedData([]);

        if (reportStartDate && reportEndDate) {
          dispatch(
            fetchMusterReports({
              filter_start_date: reportStartDate,
              filter_end_date: reportEndDate,
              filter_branch: selectedFilterBranch,
            })
          );
        }
      } else {
        toast.error(data?.message || "Failed to save changes");
      }
    },
    onError: (error) => {
      console.error("API Error:", error);
      toast.error(error?.message || "An error occurred while saving changes");
    },
  });

  // Generate an array of day strings (e.g., ["01-Jan", "02-Jan", ...]) between two dates
  const generateDayArray = useCallback((fromStr, toStr) => {
    const fromDate = dayjs(fromStr, "YYYY-MM-DD");
    const toDate = dayjs(toStr, "YYYY-MM-DD");
    const difference = toDate.diff(fromDate, "day");

    return _.times(difference + 1, (i) =>
      fromDate.add(i, "day").format("DD-MMM")
    );
  }, []);

  // Generate prefixed keys (used to extract leave/holiday values from API data)
  const generatePrefixedKeys = useCallback((day) => {
    const prefixes = [
      "holiday_name_day_",
      "leave_name_day_",
      "err_day_",
      "tag_day_",
      "day_",
    ];
    const dateObj = dayjs(day, "DD-MMM");
    const dayNum = dateObj.date();
    const monthNum = dateObj.month() + 1;

    return _.map(prefixes, (prefix) => `${prefix}${monthNum}_${dayNum}`);
  }, []);

  // Determine the cell status and corresponding tag
  const getStatus = useCallback((tagCol, dayCol, leaveType) => {
    const classList = {
      "Full Day": "ps-Ptag ps-unline",
      Absent: "ps-Atag ps-unline",
      "Over Time": "ps-ottag ps-unline",
      "Half Day": "ps-Atag ps-unline",
      Leave: "ps-Pwotag ps-unline",
      "Week Off": "ps-WOtag ps-unline",
      "First Half Leave": "ps-Pwotag ps-unline",
      "Second Half Leave": "ps-Pwotag ps-unline",
      Holiday: "ps-WOtag ps-unline",
    };

    const tag = {
      "Full Day": ATTENDANCE_CODES.FULL_DAY,
      Absent: ATTENDANCE_CODES.ABSENT,
      "Over Time": "OT",
      "Half Day": ATTENDANCE_CODES.HALF_DAY,
      Leave: "L",
      "Week Off": ATTENDANCE_CODES.WEEK_OFF,
      "First Half Leave": ATTENDANCE_CODES.HALF_DAY,
      "Second Half Leave": ATTENDANCE_CODES.HALF_DAY,
      Holiday: ATTENDANCE_CODES.HOLIDAY,
    };

    if (dayCol === "Week Off") {
      return { class: classList[dayCol], tag: tag[dayCol] };
    }

    if (leaveType === "leave") {
      return { class: classList["Leave"], tag: dayCol };
    }


    const finalCol =
      tagCol && tagCol !== "Normal" && tagCol !== "Overwrite" ? tagCol : dayCol;

    return {
      class: classList[finalCol] ?? "ps-Atag ps-unline",
      tag: tag[finalCol] ?? ATTENDANCE_CODES.ABSENT,
    };
  }, []);

  // Generate custom cell styles based on muster report data
  const generateCellStyles = useCallback(
    (rows, cols, modifiedCells = {}) => {
      if (_.isEmpty(rows) || _.isEmpty(cols)) return {};

      const styles = {};
      const musterMap = _.keyBy(musterReports, (mrow) => mrow.code?.toString());

      const validColumns = _.compact(
        cols.map((col, colIndex) => {
          if (!isValidDay(col.title)) return null;

          const dayObj = dayjs(col.title, "DD-MMM");
          if (!dayObj.isValid()) return null;

          return {
            ...col,
            colLetter: getColumnName(colIndex),
            tagKey: `tag_day_${dayObj.month() + 1}_${dayObj.date()}`,
            errKey: `err_day_${dayObj.month() + 1}_${dayObj.date()}`,
            leaveKey: `leave_name_day_${dayObj.month() + 1}_${dayObj.date()}`,
            dayKey: `day_${dayObj.month() + 1}_${dayObj.date()}`
          };
        })
      );

      rows.forEach((row, rowIndex) => {
        const musterRow = musterMap[row["Emp ID"]?.toString()];

        validColumns.forEach((validCol) => {
          const cellKey = `${validCol.colLetter}${rowIndex + 1}`;
          const tagValue = musterRow?.[validCol.tagKey];
          const errValue = musterRow?.[validCol.errKey];
          const leaveValue = musterRow?.[validCol.leaveKey];
          const dayValue = musterRow?.[validCol.dayKey];
          const isModified = modifiedCells[cellKey] !== undefined;

          // Check if cell contains both a half day and a leave
          // const isHalfDayWithLeave = dayValue === 'Half Day' && leaveValue;

        if (isModified) {
          styles[cellKey] = `font-weight: 550; color: #000000;background-color: #EFF2E9`;
        // } else if (isHalfDayWithLeave) {
        //   // Special styling for half-day + leave combination
        //   styles[cellKey] = `font-weight: 550; color: #8558df`;
        } else if (
          (errValue && errValue === "Problem") && 
          ((tagValue && tagValue === "Absent") || (dayValue && (dayValue === 'Absent' || (dayValue === 'Half Day' && tagValue !== 'Half Day'))))) {
          styles[cellKey] = `font-weight: 550; color: #C52031`;
        } else if (tagValue && tagValue !== "Normal") {
          styles[cellKey] = `font-weight: 550; color:rgb(15, 94, 158)`;
        } else {
          styles[cellKey] = `font-weight: 550; color: #000;`;
        }
        });
      });

      return styles;
