  const {
    data: payrollFreezDates,
    isLoading: isPayrollFreezeDatesLoading,
    refetch: refetchPayrollFreezeDates,
  } = useQuery({
    queryKey: [PAYROLL_FREEZE_LISTING],
    queryFn: () => getPayrollFreezeListing({}),
    staleTime: 10*(60*1000),
  });

  const {
    data: freezeConfigData,
    isLoading: freezeConfigDataLoading,
    refetch: refetchFreezeUnfreezeConfig,
    isRefetching: freezeConfigDataRefetching
  } = useQuery({
    queryKey: [REPORT_FREEZE_UNFREEZE_CONFIG, resultFromDate, resultToDate],
    queryFn: () => get_freeze_unfreeze_configs({
      from_date : resultFromDate,
      to_date : resultToDate
    }),
    enabled: Boolean(resultFromDate && resultToDate),
    staleTime: 10*(60*1000),
  });

  const [freezeDates, setFreezeDates] = useState([]);
  useEffect(() => {
    if (
      payrollFreezDates !== undefined &&
      payrollFreezDates?.data?.length > 0
    ) {
      const filteredData = payrollFreezDates?.data?.filter(
        (item) => item.salary_type === 1
      );
      if (filteredData !== undefined && filteredData?.length > 0) {
        setFreezeDates(filteredData);
      } else {
        setFreezeDates([]);
      }
    } else {
      setFreezeDates([]);
    }
  }, [payrollFreezDates]);

  useEffect(() => {
    if (freezeDates !== undefined) {
      let isPayrollFreeze = false;
      for (let freezeDate of freezeDates) {
        if (
          freezeDate.from_date === resultFromDate &&
          freezeDate.to_date === resultToDate
        ) {
          isPayrollFreeze = true;
          break;
        }
      }
    }
  }, [resultFromDate, resultToDate, freezeDates]);

  // Update organization list when organizations data changes
  useEffect(() => {
    if (!_.isEmpty(organizations)) {
      setOrganizationsList([
        {
          items: _.map(organizations, (org) => ({
            label: org.label,
            value: org.value,
          })),
        },
      ]);
    }
  }, [organizations]);

   useEffect(() => {
      const start = dayjs(selectedFilterStartDate).startOf("day");
      const end = dayjs(selectedFilterEndDate).startOf("day");
      const diff = end.diff(start, "day") + 1;
      setSelectedDays(diff);
    }, [selectedFilterStartDate, selectedFilterEndDate]);

  // Update search loading state when report data arrives
  useEffect(() => {
    if (musterReportSuccess || musterReports) {
      setIsSearchLoading(false);
    }
  }, [musterReportSuccess, musterReports]);


  // Initialize rowBalanceMapsObj when musterReports data arrives
  useEffect(() => {
    if (!_.isEmpty(musterReports)) {
      Object.keys(rowBalanceMapsObj).forEach(
        (key) => delete rowBalanceMapsObj[key]
      );

      musterReports.forEach((row) => {
        rowBalanceMapsObj[row.code] = computeBalanceMap(row);
      });
    }
  }, [musterReports, rowBalanceMapsObj, computeBalanceMap]);

  // Trigger initial search on component mount
  useEffect(() => {

  if (!isInitializing && selectedFilterStartDate && selectedFilterEndDate) {
    handleSearch();
  }    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [initialRenderCount, selectedFilterStartDate, selectedFilterEndDate]);

  // Prepare spreadsheet data and columns when musterReports changes
  useEffect(() => {
    if (_.isEmpty(musterReports)) return;

    const dayArray = generateDayArray(reportStartDate, reportEndDate);
    const headers = [...DEFAULT_HEADERS, ...dayArray];
    const dynamicNestedHeaders = generateNestedHeaders(dayArray);

    const daysCols = _.map(headers, (header) => {
      if (_.includes(dayArray, header)) {
        return { type: "dropdown", title: header, width: 80 };
      }
      return { 
        ...DEFAULT_HEADERS_PROPS, 
        title: header, 
        width: DEFAULT_HEADERS_WIDTHS[header] || DEFAULT_HEADERS_WIDTHS.default 
      };
    });

    const newSpreadsheetData = musterReports.map((musterData) => {
      const row = {
        "Emp ID": musterData.code,
        Name: musterData.name,
        Dept: musterData.department,
        Designation: musterData.designation,
      };

      _.forEach(dayArray, (day) => {
        const keys = generatePrefixedKeys(day);

        const dateObj = dayjs(day, "DD-MMM");
        const dayNum = dateObj.date();
        const monthNum = dateObj.month() + 1;
        const leaveKey = `leave_name_day_${monthNum}_${dayNum}`;
        const dayKey = `day_${monthNum}_${dayNum}`;
        const errKey = `err_day_${monthNum}_${dayNum}`;
        const tagKey = `tag_day_${monthNum}_${dayNum}`;

        let foundKey = _.find(keys, (k) => {
          const val = musterData[k];
          return val != null && !(k.includes("tag") && val === "Normal");
        });

        let dayValue = "Absent";
        let defaultKey = "";

        if (musterData[dayKey] && musterData[dayKey] === 'Half Day' && musterData[leaveKey]) {
          foundKey = dayKey
        }
        if (musterData[errKey] && musterData[errKey] === 'Problem') {
          if (musterData[leaveKey] && 
            ((musterData[tagKey] && musterData[tagKey] !== 'Half Day') || (musterData[dayKey] && musterData[dayKey] !== 'Half Day'))) {
            foundKey = leaveKey
          } else if (musterData[tagKey] && musterData[tagKey] !== 'Normal') {
            foundKey = tagKey
          } else if (musterData[dayKey] && musterData[tagKey] !== 'Absent') {
            foundKey = dayKey
          }
        }

        if (foundKey) {
          dayValue = musterData[foundKey];

          if (dayValue === 'Leave' &&  !musterData[leaveKey]) {
            defaultKey = leaveKey;
            dayValue = 'LWP'
          } else {
            defaultKey = foundKey;
          }
        }

        // Use real-time calculation for attendance status
        const workingHours = musterData[`working_hours_day_${monthNum}_${dayNum}`] || dayValue;
        const tagValue = musterData[tagKey] || 'Normal';
        const calculatedStatus = handleDayWorkHour(
          day, 
          workingHours, 
          musterData.code, 
          musterData.salary_type || 1, 
          tagValue
        );

        // Fallback to static calculation if real-time calculation fails
        const finalStatus = calculatedStatus || getStatus(
          musterData[defaultKey],
          dayValue,
          defaultKey?.split("_")[0]
        ).tag;

        row[day] = finalStatus;
      });

      return row;
    });

    setSpreadsheetData(newSpreadsheetData);
    setOriginalSpreadsheetData(_.cloneDeep(newSpreadsheetData));
    setDaysColumns(daysCols);
    setCellStyles(generateCellStyles(newSpreadsheetData, daysCols));
    setNestedHeaders(dynamicNestedHeaders);
    setSpreadsheetKey((prev) => prev + 1);
  }, [
    musterReports,
    reportStartDate,
    reportEndDate,
    generateDayArray,
    getStatus,
    generateCellStyles,
    generatePrefixedKeys,
    generateNestedHeaders
  ]);

  // Monitor changes to selectedColumns to update isAllSelected state
  useEffect(() => {
    const allDayColumns = daysColumns
      .filter(col => isValidDay(col.title))
      .map(col => col.title);

    setIsAllSelected(
      selectedColumns.length === allDayColumns.length && 
      allDayColumns.length > 0 &&
      selectedColumns.length > 0
    );
  }, [selectedColumns, daysColumns]);

  // Inject custom styles
  useEffect(() => {
    injectCustomStyles();

    return () => {
      const styleElement = document.getElementById('muster-report-custom-styles');
      if (styleElement) {
        styleElement.remove();
      }
    };
  }, [injectCustomStyles]);

  const onDayCreate = (dObj, dStr, fp, dayElem) => {
    const filteredDateRanges =
      payrollFreezDates !== undefined && payrollFreezDates?.data?.length > 0
        ? payrollFreezDates?.data?.filter((item) => item.salary_type === 1)
        : [];
    const dateRanges =
      filteredDateRanges !== undefined && filteredDateRanges?.length > 0
        ? filteredDateRanges
        : [];

    // Convert the current day element to a comparable format (yyyy-mm-dd)
    const dayDate = dayjs(dayElem.dateObj).format("YYYY-MM-DD");

    // Iterate over each date range and apply color if the day falls within a range
    for (const range of dateRanges) {
      const fromDate = dayjs(range.from_date, "YYYY-MM-DD");
      const toDate = dayjs(range.to_date, "YYYY-MM-DD");
      const currentDate = dayjs(dayDate, "YYYY-MM-DD");

      if (
        currentDate.isAfter(fromDate.subtract(1, "day")) &&
        currentDate.isBefore(toDate.add(1, "day"))
      ) {
        dayElem.style.backgroundColor = "#FFFFFF";
        dayElem.style.color = "#004E9B";
        dayElem.classList.add("flatpickr-disabled");
        dayElem.disabled = true;
      }
    }
  };

  const checkFreezedDateRange = () => {
    const start = dayjs(selectedFilterStartDate);
    const end = dayjs(selectedFilterEndDate);
    const filteredRestrictedDates =
      payrollFreezDates !== undefined && payrollFreezDates?.data?.length > 0
        ? payrollFreezDates?.data?.filter((item) => item.salary_type === 1)
        : [];

    const restrictedDates =
      filteredRestrictedDates?.length > 0 ? filteredRestrictedDates : [];

    const hasRestrictedDates = restrictedDates?.some((item) => {
      const from = dayjs(item.from_date);
      const to = dayjs(item.to_date);

      return start.isBefore(to) && end.isAfter(from);
    });

    if (hasRestrictedDates) {
      toast.error("Selected dates include some finalized days");
    } else {
      handleSearch()
    }
  };

  const getLastDateRange = (data) => {
    // Sort the data by `to_date` in descending order
    const sortedData = data.sort((a, b) =>
      dayjs(b.to_date).isAfter(dayjs(a.to_date)) ? 1 : -1
    );

    // Get the latest to_date and from_date
    const lastToDate = dayjs(sortedData[0].to_date);
    const lastFromDate = dayjs(sortedData[0].from_date);

    // Calculate the date 7 days ago and yesterday's date
    const sevenDaysAgo = dayjs().subtract(7, "day");
    const yesterday = dayjs().subtract(1, "day");

    // Check if the lastToDate is within the last 7 days
    if (lastToDate.isAfter(sevenDaysAgo)) {
      // Return the last `from_date` and `to_date`
      setSelectedFilterStartDate(dayjs(lastFromDate).toDate());
      setSelectedFilterEndDate(dayjs(lastToDate).toDate());
      setMaxToDate(dayjs(lastToDate).toDate());
      setResultFromDate(lastFromDate.format("YYYY-MM-DD"));
      setResultToDate(lastToDate.format("YYYY-MM-DD"));

      return {
        from_date: lastFromDate.format("YYYY-MM-DD"),
        to_date: lastToDate.format("YYYY-MM-DD"),
      };
    } else {
      // Return the day after last `to_date` and yesterday's dated

      const nextFromDate = lastToDate.add(1, "day");
      const maxEndDate = nextFromDate.add(30, "day");

      setSelectedFilterStartDate(dayjs(nextFromDate).toDate());
      setResultFromDate(nextFromDate.format("YYYY-MM-DD"));

      if (yesterday.isAfter(maxEndDate)) {
        setSelectedFilterEndDate(dayjs(maxEndDate).startOf("day").toDate());
        setMaxToDate(dayjs(maxEndDate).startOf("day").toDate());
        setResultToDate(maxEndDate.format("YYYY-MM-DD"));
      } else {
        setSelectedFilterEndDate(dayjs(yesterday).startOf("day").toDate());
        setMaxToDate(dayjs(yesterday).startOf("day").toDate());
        setResultToDate(yesterday.format("YYYY-MM-DD"));
      }
      setInitialRenderCount(prev => prev + 1)
      return {
        from_date: nextFromDate.format("YYYY-MM-DD"),
        to_date: yesterday.format("YYYY-MM-DD"),
      };
    }
  };

  useEffect(() => {
    if (!isPayrollFreezeDatesLoading) {
      if (isUnlocked) {
        setSelectedFilterStartDate(unlockStartDate);
        setSelectedFilterEndDate(unlockEndDate);
        setMaxToDate(unlockEndDate);
        setResultFromDate(
          dayjs(unlockStartDate).startOf("day").format("YYYY-MM-DD")
        );
        setResultToDate(
          dayjs(unlockEndDate).startOf("day").format("YYYY-MM-DD")
        );
        location.state = null; // to change location state to null to fixed issue
        setIsUnlocked(false);
      } else {
        const filteredRestrictedDates =
          payrollFreezDates !== undefined && payrollFreezDates?.data?.length > 0
            ? payrollFreezDates?.data?.filter((item) => item.salary_type === 1)
            : [];

        const restrictedDates =
          filteredRestrictedDates?.length > 0 ? filteredRestrictedDates : [];

        if (restrictedDates?.length > 0) {
          getLastDateRange(restrictedDates);
        } else {
          setSelectedFilterStartDate(
            dayjs(yesterdayDate).startOf("month").toDate()
          );
          setSelectedFilterEndDate(dayjs(yesterdayDate).toDate());
          setMaxToDate(dayjs(yesterdayDate).toDate());
          setResultFromDate(
            dayjs(dayjs(yesterdayDate).startOf("month").toDate()).format(
              "YYYY-MM-DD"
            )
          );
          setResultToDate(
            dayjs(dayjs(yesterdayDate).toDate()).format("YYYY-MM-DD")
          );
        }
      }
      setIsInitializing(false);
    }

    // eslint-disable-next-line
  }, [payrollFreezDates]);

  const displayColumns = daysColumns.map((col, index) => {
    if (index > 3 && isValidDay(col?.title)) {
      const dateForCheck = getDateWithinRange(resultFromDate, resultToDate, col?.title);
      const isLocked = isDateInFreezeRange(dateForCheck);

      return {
        ...col,
        title: getDayNameFromDate(col?.title),
        readOnly: isLocked,
        locked: isLocked,
        className: isLocked ? 'freeze-locked-column' : ''
      };
    }
    return col;
  });

  return (
    <>
      <Header title={WORKSHEET_TITLE} />
      <div
        className={`page-content`}
      >
        <Container fluid>
          <Row>
            <Col lg={12}>
              <Card className="ps-card">
                <CardHeader className="ps-card-header ps-searchbar py-3">
                   <div className="ps-emp-total">
                      <h5>Bulk Attendance Adjustments</h5>
                    </div>
                </CardHeader>
                <CardHeader className="ps-card-header ps-searchbar">
                  {/* Date Range Picker - From */}
                  <div className="ps-form-group calendar-icon">
                    <label htmlFor="StartDate">From</label>
                    <Flatpickr
                      className="calendar form-control"
                      options={{
                        dateFormat: "d-m-Y",
                        defaultDate: dayjs(selectedFilterStartDate).toDate(),
                        maxDate: yesterdayDate,
                        onDayCreate: onDayCreate,
                        disable: [
                          function (date) {
                            return dayjs(date).isAfter(
                              dayjs(location.state?.cycle_date)
                                .subtract(1, "day")
                                .toDate(),
                              "YYYY-MM-DD"
                            );
                          },
                        ],
                      }}
                      onChange={(date) => handleStartDateChange(date[0])}
                      value={selectedFilterStartDate}
                    />
                  </div>

                  {/* Date Range Picker - To */}
                  <div className="ps-form-group calendar-icon">
                    <label htmlFor="EndDate">To</label>
                    <Flatpickr
                        className="calendar form-control"
                        options={{
                          dateFormat: "d-m-Y",
                          defaultDate: dayjs(selectedFilterEndDate).toDate(),
                          minDate: selectedFilterStartDate,
                          maxDate: maxToDate,
                          disable: [
                            function (date) {
                              return dayjs(date).isAfter(
                                dayjs(location.state?.cycle_date)
                                  .subtract(1, "day")
                                  .toDate(),
                                "YYYY-MM-DD"
                              );
                            },
                          ],
                          onDayCreate: onDayCreate,
                        }}
                        onChange={(date) => setSelectedFilterEndDate(date[0])}
                        value={selectedFilterEndDate}
                    />
                  </div>

                  {/* Branch Dropdown */}
                  <div className="ps-form-group">
                    <label>Choose Branch</label>
                    <Library1Dropdown
                      value={selectedFilterBranch}
                      options={organizationsList}
                      onChange={(e) => setSelectedFilterBranch(e.value)}
                      optionLabel="label"
                      optionGroupLabel="label"
                      optionGroupChildren="items"
                      placeholder="Choose Branch"
                      className="w-100 ps-check-drop"
                    />
                  </div>

                  {/* Search Button */}
                  <div className="button-wrapper">
                    <button
                      onClick={checkFreezedDateRange}
                      disabled={isSearchLoading}
                      title="Search"
                      type="button"
                      className="ps-btn sm-btn primary-btn"
                    >
                     Search
                    </button>
                  </div>

                  <div className="d-flex ms-auto">
                  <div className="button-wrapper me-2">

                  <CommonTooltip
                    placement="left"
                    title={
                      <div className="text-start p-1" style={{ maxWidth: "300px" }}>
                        <ul className="mb-2 ps-2" style={{ listStyleType: "none" }}>
                          <li className="mb-2">
                            <span style={{ color: 'rgb(15, 94, 158)', fontWeight: 'bold' }}> FD</span>: Blue text means a forced overwrite has been performed
                          </li>
                          <li className="mb-2">
                            <span style={{ color: '#C52031', fontWeight: 'bold' }}> A</span>: Red text means that there is a punch error on that day
                          </li>
                          <li className="mb-2">
                            <span> Grey</span>: Any cells with unsaved changes are highlighted in grey.
                          </li>
                          <li className="mb-2 ">
                            <i className="fas fa-info-circle me-1"></i> Double-click on the cell opens the history of the day
                          </li>
                        </ul>
                      </div>
                    }
                  >
                    <button
                      id="helpTooltipBtn"
                      type="button"
                      className="ps-btn sm-btn grey-outline-btn icon-w"
                    >
                      <svg 
                        width="18" 
                        height="18" 
                        viewBox="0 0 24 24" 
                        fill="none" 
                        stroke="currentColor" 
                        strokeWidth="2" 
                        strokeLinecap="round" 
                        strokeLinejoin="round"
                      >
                        <circle cx="12" cy="12" r="9.5"></circle>
                        <line x1="12" y1="16" x2="12" y2="12"></line>
                        <line x1="12" y1="8" x2="12.01" y2="8"></line>
                      </svg>
                    </button>
                  </CommonTooltip>
              </div>
                    {/* Save Button */}
                    <div className="button-wrapper me-1">
                      <button
                        onClick={handleSave}
                        disabled={isTagUpdatePending || modifiedData.length === 0}
                        title="Save Changes"
                        type="button"
                        className="ps-btn sm-btn grey-outline-btn"
                       >
                        {isTagUpdatePending ? (
                          <span
                            className="spinner-border spinner-border-sm"
                            role="status"
                            aria-hidden="true"
                          ></span>
                        ) : (
                          "Save Changes"
                        )}
                      </button>

                      {/* Export Excel Button */}
                      <button
                        onClick={() => handleExportExcel(WORKSHEET_TITLE)}
                        title="Export Excel"
                        type="button"
                        className="ps-btn sm-btn grey-outline-btn"
                      >
                      Export
                      </button>
                    </div>

                    {/* Reset Columns Section */}
                    <div className="d-flex align-items-end">
                      <div className="ps-form-group mx-2">
                        <label>Reset Columns</label>
                          <MultiSelect
                            value={selectedColumns}
                            onChange={handleColumnSelectionChange}
                            options={getDayColumnsForMultiSelect()}
                            placeholder="Select columns"
                            maxSelectedLabels={1}
                            className="w-100 ps-check-drop"
                            style={{ flex: 1 }}
                            panelHeaderTemplate={renderMultiSelectHeader}
                          />
                      </div>
                      {/* Reset Button - Simple approach */}
                      <button
                        onClick={handleResetColumns}
                        disabled={selectedColumns.length === 0 || isTagUpdatePending}
                        title="Reset Selected Columns"
                        type="button"
                        className="ps-btn sm-btn grey-outline-btn icon-w"
                      >
                        <svg
                          width="18"
                          height="18"
                          viewBox="0 0 16 16"
                          fill="currentColor"
                          style={{ pointerEvents: "none" }}
                        >
                          <path d="M8 3a5 5 0 1 1-4.546 2.914.5.5 0 0 0-.908-.417A6 6 0 1 0 8 2v1z" />
                          <path d="M8 4.466V.534a.25.25 0 0 0-.41-.192L5.23 2.308a.25.25 0 0 0 0 .384l2.36 1.966A.25.25 0 0 0 8 4.466z" />
                        </svg>
                      </button>
                    </div>
                  </div>
                </CardHeader>

                <CardBody>
                  <div className={`ps-spredsheetTable table-responsive ps-table-wrapper position-relative`}>
                    {isInitializing || isSearchLoading || isPayrollFreezeDatesLoading || isTagUpdatePending ? (
                      <Skeleton count={8} height={50} />
                    ) : musterReportSuccess &&
                      showSpreadsheet &&
                      !_.isEmpty(spreadsheetData) ? (
                        <Suspense fallback={<div className="text-center py-3"><Skeleton count={8} height={50} /></div>}>
                        <SpreadsheetContainer
                          spreadsheetKey={spreadsheetKey}
                          spreadsheetData={spreadsheetData}
                          daysColumns={displayColumns}
                          sheetName="SpreadSheet"
                          licenceKey={licenceKey}
                          isEditable={true}
                          globalEventHandler={globalEventHandler}
                          defaultColAlign="center"
                          freezeColumns="2"
                          pageRowlimit={50}
                          cellStyles={cellStyles}
                          nestedHeaders={nestedHeaders}
                        />
                        </Suspense>
                    ) : (
                     <div className="text-center py-5">
                        <svg
                          width="58"
                          height="58"
                          viewBox="0 0 58 58"
                          fill="none"
                          xmlns="http://www.w3.org/2000/svg"
                        >
                          <rect width="58" height="58" rx="29" fill="#E3F1FF" />
                          <path
                            d="M35.74 34.74L31.12 30.12C31.42 29.74 31.67 29.29 31.86 28.78C32.06 28.24 32.16 27.67 32.16 27.08C32.16 25.67 31.66 24.46 30.68 23.48C29.7 22.5 28.49 22 27.08 22C25.67 22 24.46 22.5 23.48 23.48C22.5 24.46 22 25.67 22 27.08C22 28.49 22.5 29.7 23.48 30.68C24.46 31.66 25.67 32.16 27.08 32.16C27.65 32.16 28.22 32.06 28.76 31.87C29.26 31.69 29.72 31.43 30.12 31.12L34.74 35.74C34.87 35.88 35.04 35.95 35.24 35.95C35.44 35.95 35.6 35.88 35.74 35.74C35.87 35.6 35.94 35.43 35.94 35.23C35.94 35.03 35.87 34.87 35.74 34.74ZM27.08 23.42C28.1 23.42 28.97 23.78 29.67 24.49C30.38 25.19 30.74 26.06 30.74 27.08C30.74 28.1 30.38 28.97 29.67 29.68C28.96 30.39 28.1 30.74 27.08 30.74C26.06 30.74 25.19 30.38 24.49 29.68C23.78 28.97 23.42 28.1 23.42 27.08C23.42 26.06 23.78 25.19 24.49 24.49C25.19 23.78 26.06 23.42 27.08 23.42Z"
                            fill="#2381D9"
                          />
                        </svg>
                        <h5 className="mt-2 mb-1">No Data Found</h5>
                        <p>Try Adjusting the filters again</p>
                      </div>
                    )}
                  </div>
                </CardBody>
              </Card>
            </Col>
          </Row>
        </Container>
      </div>

      {attendanceLog && (
        <Offcanvas
          isOpen={attendanceLog}
          toggle={handleAttendanceLogSidebar}
          direction="end"
          className="offcanvas-end border-0 custom-offcavas"
          style={{ width: "462px" }}
          autoFocus={false}
        >
          <AttendanveSidebar
            Attendancelogsidebar={handleAttendanceLogSidebar}
            regsidebardata={regsidebardata}
            isReadOnly={true}
          />
        </Offcanvas>
      )}

      <ConfirmationPopup
        isOpen={isConfirmationPopupOpen}
        setIsOpen={setIsConfirmationPopupOpen}
        title="Confirm Save Changes"
        subTitle="Saving these attendance changes might affect payroll calculations. Please type CONFIRM to proceed."
        handleProceed={proceedWithSave}
        isLoading={isTagUpdatePending}
        warning="Ensure accuracy, as these changes will affect payroll and leave balances."
      />
    </>
  );
};

export default BulkAttendanceUpdate;

