import React, { useEffect, useState, useCallback, useMemo, useRef, lazy, Suspense } from "react";
import { useDispatch, useSelector } from "react-redux";
import { Container, Card, Row, Col, CardHeader, Spinner, Offcanvas, CardBody, } from "reactstrap";
import Header from "../../Layouts/Header";
import Flatpickr from "react-flatpickr";
import { Dropdown as Library1Dropdown } from "primereact/dropdown";
import { MultiSelect } from "primereact/multiselect";
import { getMusterSpreadsheetReports as fetchMusterReports } from "../../store/actions";
import dayjs from "dayjs";
import _, { debounce, groupBy } from "lodash";
import AttendanveSidebar from "../Reports/AttendanveSidebar";
import { licenceKey } from "../../Components/constants/licence_key";
import Skeleton from 'react-loading-skeleton'
import 'react-loading-skeleton/dist/skeleton.css';
import { handleExportExcel } from "../../Components/Common/SpreadsheetWrapper";
import { toast } from "react-toastify";
import "primereact/resources/themes/lara-light-indigo/theme.css";
import "primereact/resources/primereact.min.css";
import { bulkAttendanceUpdateApi, getPayrollFreezeListing, get_freeze_unfreeze_configs } from "../../helpers/awsapibackend_helper";
import { useMutation, useQuery } from "@tanstack/react-query";
import { PAYROLL_FREEZE_LISTING, REPORT_FREEZE_UNFREEZE_CONFIG } from "../../reactQueryKeys/PayrollQueryKeys";
import { useLocation, useNavigate, } from "react-router-dom";
import ConfirmationPopup from "../../Components/Common/ConfirmationPopup";
import CommonTooltip from '../../Components/Common/CommonTooltip';
import { getWorkingStatus } from "../Reports/ReportHelpers/attendanceMasterConfigsHelper";
import { getDateForDay } from "../Reports/ReportHelpers/musterConfigsHelper";

const SpreadsheetContainer = lazy(() => import('../../Components/Common/SpreadsheetContainer'));

const ATTENDANCE_CODES = {
  FULL_DAY: "FD",
  HALF_DAY: "HD",
  ABSENT: "A",
  WEEK_OFF: "WO",
  HOLIDAY: "H"
};

const TAG_VALUES_MAP = {
  [ATTENDANCE_CODES.HALF_DAY]: 3,
  [ATTENDANCE_CODES.FULL_DAY]: 2,
  [ATTENDANCE_CODES.ABSENT]: 4,
  [ATTENDANCE_CODES.HOLIDAY]: 1,
  [ATTENDANCE_CODES.WEEK_OFF]: 6
};

const ALLOWED_VALUES = [
  ATTENDANCE_CODES.ABSENT, 
  ATTENDANCE_CODES.HALF_DAY, 
  ATTENDANCE_CODES.FULL_DAY,
  ATTENDANCE_CODES.WEEK_OFF
];
const LWP_LEAVE = 'LWP';

const DEFAULT_VALUES = [
  ATTENDANCE_CODES.HALF_DAY, 
  ATTENDANCE_CODES.FULL_DAY, 
  ATTENDANCE_CODES.ABSENT, 
  ATTENDANCE_CODES.WEEK_OFF, 
  ATTENDANCE_CODES.HOLIDAY
];

const SPECIAL_CODES = [
  ATTENDANCE_CODES.WEEK_OFF, 
  ATTENDANCE_CODES.HOLIDAY
];

// Check if a value represents an attendance code
const isAttendance = (val) => [
  ATTENDANCE_CODES.ABSENT, 
  ATTENDANCE_CODES.FULL_DAY, 
  ATTENDANCE_CODES.HALF_DAY
].includes(val);

// Check if a value represents a leave code (not attendance and not special)
// const isLeave = (val) => val && !isAttendance(val) && !SPECIAL_CODES.includes(val);
// Update the isLeave function to explicitly recognize LWP as a leave
const isLeave = (val) => val === LWP_LEAVE || (val && !isAttendance(val) && !SPECIAL_CODES.includes(val));

const DEFAULT_HEADERS = ["Emp ID", "Name", "Dept", "Designation"];

const DEFAULT_HEADERS_WIDTHS = { 
  "Designation": 150, 
  "Dept": 150, 
  "Name": 200, 
  "default": 100 
};
const DEFAULT_HEADERS_PROPS = { 
  type: "text", 
  readOnly: true, 
  filter: true, 
  locked: true 
};

// Check if a given string is a valid day (e.g. "DD-MMM")
const isValidDay = (day) => dayjs(day, "DD-MMM", true).isValid();

const WORKSHEET_TITLE  = 'Bulk Attendance Adjustments'

// Generate Excel-like column names (A, B, C, ..., Z, AA, AB, etc.)
const getColumnName = (index) => {
  let name = "";
  while (index >= 0) {
    name = String.fromCharCode((index % 26) + 65) + name;
    index = Math.floor(index / 26) - 1;
  }
  return name;
};

function groupByTagAndCombineJeIds(array) {
  const groupedByTag = _.groupBy(array, 'tag');

  return _.map(groupedByTag, (items, tag) => {
    const allJeIds = _.flatMap(items, item => {
      const jeIdsStr = String(item.je_ids);
      const matches = jeIdsStr.match(/\d+/g) || [];
      return matches;
    });

    return {
      tag: parseInt(tag),
      je_ids: _.uniq(allJeIds).join(',')
    };
  });
}

function findEmployeeByCode(employeeMap, code) {
  return employeeMap[
    Object.keys(employeeMap)?.find(
      (key) =>
        parseInt(key) === parseInt(code) ||
        String(key)?.toLowerCase() === String(code)?.toLowerCase()
    )
  ];
}

