          const musterRecord = _.find(
            musterReports,
            (mr) => mr?.code?.toString() === filteredRowData[0]?.toString()
          );

          if (musterRecord) {
            const clickedDate = getDateWithinRange(
              reportStartDate,
              reportEndDate,
              columnTitle
            );

            openAttendanceLogs(musterRecord, clickedDate);
          }
        }
      }
    }
