    static generateDynamicColumnsMusterReport = (column, start_date, end_date, tablePrefix = '', isSpreadsheetFormat = false) => {
        const start = new Date(start_date);
        const end = new Date(end_date);
        const columns = [];
        const prefix = tablePrefix
            ? tablePrefix.endsWith('.')
            ? tablePrefix
            : tablePrefix + '.'
            : '';
        let currentDate = new Date(start);
        while (currentDate <= end) {
            const day = currentDate.getDate();
            const month = currentDate.getMonth() + 1;
            const formattedDate = currentDate.toISOString().split('T')[0];
            if (isSpreadsheetFormat) {
                columns.push(
                    `MAX(` +
                        `CASE WHEN ${prefix}attandance_date = '${formattedDate}' ` +
                        `THEN CONCAT(` +
                            `CAST(CASE WHEN COALESCE(${column}, '') = '' THEN 'null' ELSE ${column} END AS CHAR), '#', ` +
                            `CAST(CASE WHEN COALESCE(${prefix}tag_str, '') = '' THEN 'null' ELSE ${prefix}tag_str END AS CHAR), '#', ` +
