import { db2 } from "../connect.js";
import mysql from 'mysql2';
export default class ReportsDao {
    static async getOrganizationAnalytics(user_id) {
        const query = 'SELECT * FROM organization_analytics WHERE user_id = ? LIMIT 1';
        try {
            const [row] = await db2.query(query, [user_id]);
            return row;
        } catch (err) {
            throw err;
        }
    }

    static async check_token(token) {
        const query = 'SELECT count(*) AS cnt_token FROM big_data_tokens WHERE token = ? AND status = 1'

        try {
            const [row] = await db2.query(query, [token]);
            return row;
        } catch (err) {
            throw err;
        }
    }

    static async saveBigDataToken(token, user_id, owner_id, email_id, instance_id,org_ids,expiry,status) {


        const query = 'INSERT INTO big_data_tokens (token, user_id, owner_id, email_id,instance_id,org_ids,expiry,status,created,modified) VALUES (?,?,?,?,?,?,?,?,NOW(),NOW())';
        try {
            const [row] = await db2.query(query, [token, user_id, owner_id, email_id, instance_id,org_ids,expiry,status]);
            return row;
        } catch (err) {
            throw err;
        }
    }

    static async getAttendanceMasterReports(ho_id, branch_id, user_organizations, start_date, end_date, user_type, department_ids) {

        var query = '';
        var parameters = [];
        var department_condition = ""
        if (user_type !== 1 && department_ids !== null) {
            if (department_ids) {
              department_condition = ` AND emp.department_id IN (${department_ids})`;
            } else {
              department_condition = ` AND emp.department_id IS NULL`;
            }
        }

        query = `
            SELECT 
                h.user_id as employee_id,
                emp.code,
                emp.device_employee_id,
                emp.name,
                h.attandance_date,
                COALESCE(es.salary_type, 1) as salary_type,
                h.tag,
                des.name as designation,
                d.name as department,
                DATE_FORMAT(h.first_punch, '%I:%i %p') as first_punch,
                case when h.first_punch = h.last_punch then null else DATE_FORMAT(h.last_punch, '%I:%i %p') end as last_punch,
                case 
                    when h.flag = 'Holiday' then h.flag
                    when h.flag = 'Week Off' then h.flag 
                    when tag = 'Full Day' or tag = 'Half Day' or tag = 'Absent' then tag else
                        (case
                            when tag = 'Overwrite' then h.overwrite
                            when h.flag IS NOT NULL then h.flag
                        else
                            h.working_hrs
                        end)
                end as status,
                CONCAT(
                    IF(FLOOR(TIME_TO_SEC(h.working_hrs) / 3600) > 0, CONCAT(FLOOR(TIME_TO_SEC(h.working_hrs) / 3600), 'h '), ''),
                    IF(MOD(FLOOR(TIME_TO_SEC(h.working_hrs) / 60), 60) > 0, CONCAT(MOD(FLOOR(TIME_TO_SEC(h.working_hrs) / 60), 60), 'm'), '')
                ) as working_hrs,
                CONCAT(
                    IF(FLOOR(TIME_TO_SEC(h.break_hrs) / 3600) > 0, CONCAT(FLOOR(TIME_TO_SEC(h.break_hrs) / 3600), 'h '), ''),
                    IF(MOD(FLOOR(TIME_TO_SEC(h.break_hrs) / 60), 60) > 0, CONCAT(MOD(FLOOR(TIME_TO_SEC(h.break_hrs) / 60), 60), 'm'), '')
                ) as break_hrs,
                CASE WHEN MOD(h.p_cnt, 2) <> 0 THEN 'Problem' END AS error_state,
                l.name as leave_name,
	            htd.holiday_name,
                emp.status as employee_status,
                emp.tag_updated_at
            FROM
            (SELECT 
                user_id,
                attandance_date,
                MAX(case when tag = 1 then 'Normal' when tag = 2 then 'Full Day' when tag = 3 then 'Half Day' when tag = 4 then 'Absent' when tag = 5 then 'Overwrite' end) as tag,
                (case when flag = 5 then 'Leave' when flag = 6 then 'Half Day' WHEN flag = 7 then 'Half Day' when flag = 9 then 'Week Off' when flag = 10 then 'Holiday' end) as flag,
                min(punch_min_datetime) as first_punch,
                max(punch_max_datetime) as last_punch,
                MAX(case when tag = 5 then overwrite else working_hrs end) AS working_hrs,
                max(break_hrs) as break_hrs,
                sum(p_cnt) as p_cnt,
                max(j.overwrite) as overwrite,
                max(j.leave_id) as leave_id
            from journal_entries j 
            where j.ho_id = ? and j.attandance_date >= ? and j.attandance_date <= ? and j.tag > 0
            group by 1,2 ) h 
            left join leaves l on l.id = h.leave_id
            left join holiday_template_details htd on h.leave_id = htd.id and h.flag = 'Holiday' and htd.ho_id = ?
            JOIN employees emp on emp.id = h.user_id and emp.ho_id = ? ${department_condition}
            JOIN employee_branches eb on eb.employee_id = h.user_id and eb.status = 1
            LEFT JOIN employee_salaries es on es.emp_id = emp.id
            LEFT JOIN departments d on emp.department_id = d.id and  d.ho_id = ?
            LEFT JOIN designations des on des.id = emp.designation_id and des.ho_id = ?
        `;

        parameters = [ho_id, start_date, end_date, ho_id, ho_id, ho_id, ho_id];

        if (branch_id) {
            query += ` WHERE eb.branch_id = ? `;
            parameters.push(branch_id);
        } else if (user_organizations !== '') {
            query += ` WHERE eb.branch_id in (${user_organizations}) `;
        }

        query += `
            group by 1,5
            order by h.attandance_date desc;
        `;        

        const connection = await db2.getConnection();      
        try {
            const [row] = await connection.query(query, parameters);
            return row;
        } catch (err) {
            throw err;
        } finally {
            await connection.destroy();
        }

    }

    static generateDynamicColumns = (column, start_date, end_date, tablePrefix = '') => {
        const start = new Date(start_date);
        const end = new Date(end_date);
        const columns = [];
        const dateFormats = [];
        const errStateColumns = [];
        const tagColumns = [];
        const leaveColumns = [];
        const holidayColumns = [];
        const jeIdColumns = [];
        const breakHoursColumns = [];

        const prefix = tablePrefix ? (tablePrefix.endsWith('.') ? tablePrefix : tablePrefix + '.') : '';

        let currentDate = new Date(start);

        while (currentDate <= end) {
            const day = currentDate.getDate();
            const month = currentDate.getMonth() + 1;
            const formattedDate = currentDate.toISOString().split('T')[0];

            columns.push(`MAX(CASE WHEN ${prefix}attandance_date = '${formattedDate}' THEN ${column} ELSE NULL END) AS 'day_${month}_${day}'`)
            dateFormats.push(`'${formattedDate}' AS 'date_${day}'`);
            errStateColumns.push(`MAX(CASE WHEN ${prefix}attandance_date = '${formattedDate}' THEN ${prefix}error_state ELSE NULL END) AS 'err_day_${month}_${day}'`);
            tagColumns.push(`MAX(CASE WHEN ${prefix}attandance_date = '${formattedDate}' THEN ${prefix}${tablePrefix === 'ds' ? 'tag_str' : 'tag'} ELSE NULL END) AS 'tag_day_${month}_${day}'`)
            leaveColumns.push(`MAX(CASE WHEN ${prefix}attandance_date = '${formattedDate}' THEN ${prefix}leave_name ELSE NULL END) AS 'leave_name_day_${month}_${day}'`)
            holidayColumns.push(`MAX(CASE WHEN ${prefix}attandance_date = '${formattedDate}' THEN ${prefix}holiday_name ELSE NULL END) AS 'holiday_name_day_${month}_${day}'`)
            jeIdColumns.push(`MAX(CASE WHEN ${prefix}attandance_date = '${formattedDate}' THEN ${prefix}je_ids ELSE NULL END) AS 'jeIds_day_${month}_${day}'`);
            breakHoursColumns.push(`MAX(CASE WHEN ${prefix}attandance_date = '${formattedDate}' THEN ${prefix}break_hrs ELSE NULL END) AS ' break_hrs_day_${month}_${day}'`)

            currentDate.setDate(currentDate.getDate() + 1);
        }

        return {
            columns: columns.join(', '),
            dateFormats: dateFormats.join(', '),
            errStateColumns: errStateColumns.join(', '),
            tagColumns: tagColumns.join(', '),
            leaveColumns: leaveColumns.join(', '),
            holidayColumns : holidayColumns.join(', '),
            jeIdColumns: jeIdColumns.join(', '),
            breakHoursColumns : breakHoursColumns.join(', '),
        };
    };

    static generateDynamicColumnsMusterReport = (column, start_date, end_date, tablePrefix = '', isSpreadsheetFormat = false) => {
        const start = new Date(start_date);
        const end = new Date(end_date);
        const columns = [];

        const prefix = tablePrefix
            ? tablePrefix.endsWith('.')
            ? tablePrefix
            : tablePrefix + '.'
            : '';

        let currentDate = new Date(start);
        while (currentDate <= end) {
            const day = currentDate.getDate();
            const month = currentDate.getMonth() + 1;
            const formattedDate = currentDate.toISOString().split('T')[0];

            if (isSpreadsheetFormat) {
                columns.push(
                    `MAX(` +
                        `CASE WHEN ${prefix}attandance_date = '${formattedDate}' ` +
                        `THEN CONCAT(` +
                            `CAST(CASE WHEN COALESCE(${column}, '') = '' THEN 'null' ELSE ${column} END AS CHAR), '#', ` +
                            `CAST(CASE WHEN COALESCE(${prefix}tag_str, '') = '' THEN 'null' ELSE ${prefix}tag_str END AS CHAR), '#', ` +
                            `CAST(CASE WHEN COALESCE(${prefix}error_state, '') = '' THEN 'null' ELSE ${prefix}error_state END AS CHAR), '#', ` +
                            `CAST(CASE WHEN COALESCE(${prefix}leave_name, '') = '' THEN 'null' ELSE ${prefix}leave_name END AS CHAR), '#', ` +
                            `CAST(CASE WHEN COALESCE(${prefix}holiday_name, '') = '' THEN 'null' ELSE ${prefix}holiday_name END AS CHAR), '#', ` +
                            `CAST(CASE WHEN COALESCE(${prefix}je_ids, '') = '' THEN 'null' ELSE ${prefix}je_ids END AS CHAR)` +
                        `) ` +
                        `ELSE NULL ` +
                        `END` +
                    `) AS 'day_${month}_${day}'`
                );
            } else {
                columns.push(
                    `MAX(` +
                        `CASE WHEN ${prefix}attandance_date = '${formattedDate}' ` +
                        `THEN CONCAT(` +
                            `CASE WHEN COALESCE(${column}, '') = '' THEN 'null' ELSE ${column} END, '#', ` +
                            `CASE WHEN COALESCE(${prefix}tag, '') = '' THEN 'null' ELSE ${prefix}tag END, '#', ` +
                            `CASE WHEN COALESCE(${prefix}error_state, '') = '' THEN 'null' ELSE ${prefix}error_state END, '#', ` +
                            `CASE WHEN COALESCE(${prefix}leave_name, '') = '' THEN 'null' ELSE ${prefix}leave_name END, '#', ` +
                            `CASE WHEN COALESCE(${prefix}holiday_name, '') = '' THEN 'null' ELSE ${prefix}holiday_name END` +
                        `) ` +
                        `ELSE NULL ` +
                        `END` +
                    `) AS 'day_${month}_${day}'`
                );
            }
            currentDate.setDate(currentDate.getDate() + 1);
        }

        return {
            columns: columns.join(', ')
        };
    };


    static async getDailyPunchReports(ho_id, branch_id, user_organizations, start_date, end_date, user_type, department_ids) {

        const { columns, tagColumns, errStateColumns } = this.generateDynamicColumns('punches', start_date, end_date);

        var parameters = [];
        var department_condition = ''
        if (user_type !== 1 && department_ids !== null) {
            if (department_ids) {
              department_condition = ` AND emp.department_id IN (${department_ids})`;
            } else {
              department_condition = ` AND emp.department_id IS NULL`;
            }
        }

        let query = `
        with daily_punch_report as (
            SELECT 
                ho_id,
                user_id,
                code,
                name,
                designation,
                department,
                device_employee_id,
                employee_status,
                tag_updated_at,
                ${columns},
                ${tagColumns},
                ${errStateColumns}
            FROM
            (
                SELECT
                    a.ho_id,
                    a.attandance_date,
                    DATE_FORMAT(a.attandance_date, "%d") AS day,
                    DATE_FORMAT(a.attandance_date, "%d-%m-%Y") AS date,
                    a.user_id,
                    des.name as designation,
                    d.name as department,
                    emp.code,
                    emp.name,
                    emp.device_employee_id,
                    emp.status as employee_status,
                    emp.tag_updated_at,
                    sum(a.p_cnt) as p_cnt,
                    max(a.flag) as flag,
                    a.tag,
                    GROUP_CONCAT(COALESCE(a.punches, a.flag) ORDER BY a.from_time) AS punches,
                    MAX(working_hrs) as working_hrs,
                    MAX(break_hrs) as break_hrs,
                    CASE WHEN MOD(sum(a.p_cnt), 2) <> 0 THEN 'Problem' END AS error_state
                FROM (
                    SELECT 
                        ho_id,
                        org_id,
                        attandance_date,
                        user_id,
                        (case when flag = 5 then 'Leave' when flag = 6 then 'First Half Leave' when flag = 7 then 'Second Half Leave' when flag = 9 then 'Week Off' end) as flag,
                        (case when tag = 1 then 'Normal' when tag = 2 then 'Full Day' when tag = 3 then 'Half Day' when tag = 4 then 'Absent' when tag = 5 then 'Overwrite' end) as tag,
                        shift_id,
                        working_hrs,
                        break_hrs,
                        from_time,
                        sum(p_cnt) over (PARTITION by id) as p_cnt,
                        IF(p.value IS NULL, NULL, GROUP_CONCAT(DATE_FORMAT(p.value, '%I:%i %p') ORDER BY p.value)) AS punches
                    FROM 
                        journal_entries j
                        LEFT JOIN JSON_TABLE(
                            IFNULL(punch_json, '[]'),
                            "$[*]"
                            COLUMNS(
                                value VARCHAR(50) PATH "$"
                            )
                        ) AS p ON TRUE
                    WHERE ho_id = ? AND attandance_date >= ? and attandance_date <= ? and tag > 0
                    GROUP BY 1,2,3,4,5,6,7,8 ORDER BY j.from_time
                ) a
                JOIN employees emp ON a.user_id = emp.id and emp.ho_id = ? ${department_condition}`;

        query +=`
                LEFT JOIN departments d on emp.department_id = d.id and  d.ho_id = ?
                LEFT JOIN designations des on des.id = emp.designation_id and des.ho_id = ?
                WHERE a.attandance_date >= ?  and a.attandance_date <= ?
        `;

        parameters = [ho_id, start_date, end_date, ho_id, ho_id, ho_id, start_date, end_date];

        if(branch_id) {
            user_organizations = branch_id;
        }

        let user_organizations_condition = ''
        if(user_organizations !== "") { 
            user_organizations_condition = `and eb.branch_id in (${user_organizations})`;
        }

        query += `
                GROUP BY 1,2,3,4,5,6,7,8
                ) subquery
            GROUP BY ho_id, user_id
            )
            Select dpr.*,eb.branch_id
            from 
                daily_punch_report dpr JOIN employee_branches eb ON eb.employee_id = dpr.user_id and eb.status = 1 ${user_organizations_condition}
            GROUP BY ho_id, user_id
        `;

        const connection = await db2.getConnection();      
        try {
            const [row] = await connection.query(query, parameters);
            return row;
        } catch (err) {
            throw err;
        } finally {
            await connection.destroy();
        }
    }

