const mysql = require("mysql");
const SecretsManager = require("./secretsManager.js");
const jwtTokenLayer = require("/opt/nodejs/app.js");
const AWS = require('aws-sdk');
const sqs = new AWS.SQS({ apiVersion: 'latest' });

let pool;

async function initDbPool() {
  if (pool) return;

  try {
    const secretName = process.env.payroll_secret_name;
    const region = process.env.payroll_secret_region;

    if (!secretName || !region) {
      throw new Error("Missing environment variables: payroll_secret_name or payroll_secret_region");
    }

    const secretValue = await SecretsManager.getSecret(secretName, region);
    const config = typeof secretValue === "string" ? JSON.parse(secretValue) : secretValue;

    if (!config.host || !config.username || !config.password || !config.dbname) {
      throw new Error("Database configuration from Secrets Manager is incomplete.");
    }

    pool = mysql.createPool({
      connectionLimit: 10,
      database: config.dbname,
      user: config.username,
      password: config.password,
      host: config.host,
      connectTimeout: 10000
    });
  } catch (error) {
    console.error("Failed to initialize database pool:", error);
    pool = null;
    throw error;
  }
}

