                    </div>
                </CardHeader>
                <CardHeader className="ps-card-header ps-searchbar">
                  {/* Date Range Picker - From */}
                  <div className="ps-form-group calendar-icon">
                    <label htmlFor="StartDate">From</label>
                    <Flatpickr
                      className="calendar form-control"
                      options={{
                        dateFormat: "d-m-Y",
                        defaultDate: dayjs(selectedFilterStartDate).toDate(),
                        maxDate: yesterdayDate,
                        onDayCreate: onDayCreate,
                        disable: [
                          function (date) {
                            return dayjs(date).isAfter(
