static async getMusterReportsOptimised(ho_id, branch_id, user_organizations, org_id, is_owner, start_date, end_date, user_type, department_ids) {
        const { columns } = this.generateDynamicColumnsMusterReport('b.status', start_date, end_date);
        var query = '';
        var parameters = [];
        var department_condition = ""
        if (user_type !== 1 && department_ids !== null) {
            if (department_ids) {
              department_condition = ` AND emp.department_id IN (${department_ids})`;
            } else {
              department_condition = ` AND emp.department_id IS NULL`;
            }
        }
        var branchJoin = "";
        var branchWhereCondition = "";
        if(org_id > 0) {
            branchJoin = 'JOIN employee_branches eb ON eb.employee_id = b.user_id and eb.status = 1';
            branchWhereCondition = ` AND eb.branch_id = ${org_id} AND eb.ho_id = ${ho_id}`;
        } else if(branch_id) {
            branchJoin = 'JOIN employee_branches eb ON eb.employee_id = b.user_id and eb.status = 1';
            branchWhereCondition = ` AND eb.branch_id = ${branch_id} AND eb.ho_id = ${ho_id}`;
        } else if(org_id === 0 && is_owner !== 1) {
            branchJoin = 'JOIN employee_branches eb ON eb.employee_id = b.user_id and eb.status = 1';
            branchWhereCondition += ` AND eb.branch_id in (${user_organizations}) AND eb.ho_id = ${ho_id}`;
        }

        query = `
            SELECT
                b.ho_id,
                b.user_id,
                emp.code,
                emp.name,
                emp.device_employee_id,
                des.name as designation,
                dep.name as department,
                COALESCE(es.salary_type, 1) as salary_type,
                count(case when b.status = 'Full Day' then day end) as working_days,
                count(case when b.status = 'Absent' then day end) as absent_days,
                emp.status as employee_status,
                emp.tag_updated_at,
                ${columns}
            FROM (
                SELECT
                    a.ho_id,
                    a.attandance_date,
                    DATE_FORMAT(a.attandance_date, "%d") AS day,
                    DATE_FORMAT(a.attandance_date, "%M") AS month,
                    a.user_id,
                    a.flag,
                    a.tag,
                    (case 
                        when flag = 'Holiday' then flag
                        when flag = 'Week Off' then flag
                        when tag = 'Full Day' or tag = 'Half Day' or tag = 'Absent' then tag else 
                            (case 
                                when tag = 'Overwrite' then a.overwrite
                                when a.flag IS NOT NULL then a.flag
                            else
                                a.working_hrs
                            end)
                    end) status,
                    TIME_TO_SEC(working_hrs) as working_hrs,
                    TIME_TO_SEC(break_hrs) as break_hrs,
                    CASE WHEN MOD(p_cnt, 2) <> 0 THEN 'Problem' END AS error_state,
                    l.name as leave_name,
		            htd.holiday_name
                FROM (
                    SELECT 
                        j.ho_id,
                        j.attandance_date,
                        j.user_id,
                        flag as flag_int,
                        (case when flag = 5 then 'Leave' when flag = 6 then 'First Half Leave' when flag = 7 then 'Second Half Leave' when flag = 9 then 'Week Off'  when flag = 10 then 'Holiday' end) as flag,
                        (case when tag = 1 then 'Normal' when tag = 2 then 'Full Day' when tag = 3 then 'Half Day' when tag = 4 then 'Absent' when tag = 5 then 'Overwrite' end) as tag,
                        MAX(case when tag = 5 then overwrite else working_hrs end) AS working_hrs,
                        MAX(break_hrs) AS break_hrs,
                        SUM(p_cnt) AS p_cnt,
                        j.overwrite,
                        max(j.leave_id) as leave_id
                    FROM journal_entries j 
                    WHERE j.ho_id = ? AND j.attandance_date >= ? AND j.attandance_date <= ? AND tag > 0 
                        GROUP BY 1,2,3
                ) a
                left join leaves l on l.id = a.leave_id and l.ho_id = ?
                left join holiday_template_details htd on a.leave_id = htd.id and a.flag = 'Holiday' and htd.ho_id = ?
            ) b
            join employees emp on b.user_id = emp.id and emp.ho_id = ? ${department_condition}
            join departments dep on dep.id = emp.department_id and dep.ho_id = ?
            join designations des on des.id = emp.designation_id and des.ho_id = ?
            ${branchJoin}
            LEFT JOIN employee_salaries es on es.emp_id = emp.id AND es.ho_id = ?
            WHERE b.ho_id = ?  and emp.ho_id = ? and emp.status > 0 
        `;

        parameters = [ho_id, start_date, end_date, ho_id, ho_id, ho_id, ho_id, ho_id, ho_id, ho_id, ho_id];

