                DATE_FORMAT(a.attandance_date, '%d-%m-%Y') AS attandance_date,
                a.p_cnt,
                a.flag,
                a.tag,
                CONCAT(
                    IF(FLOOR(TIME_TO_SEC(working_hrs) / 3600) > 0, CONCAT(FLOOR(TIME_TO_SEC(working_hrs) / 3600), 'h '), ''),
                    IF(MOD(FLOOR(TIME_TO_SEC(working_hrs) / 60), 60) > 0, CONCAT(MOD(FLOOR(TIME_TO_SEC(working_hrs) / 60), 60), 'm'), '')
                ) as working_hrs,
                a.working_hrs AS working_hrs_num,
                CONCAT(
                    IF(FLOOR(TIME_TO_SEC(break_hrs) / 3600) > 0, CONCAT(FLOOR(TIME_TO_SEC(break_hrs) / 3600), 'h '), ''),
                    IF(MOD(FLOOR(TIME_TO_SEC(break_hrs) / 60), 60) > 0, CONCAT(MOD(FLOOR(TIME_TO_SEC(break_hrs) / 60), 60), 'm'), '')
                ) as break_hrs,
                CASE WHEN MOD(p_cnt, 2) <> 0 THEN 'Problem' END AS error_state
            FROM (
                SELECT 
                    j.ho_id,
                    attandance_date,
                    user_id,
                    shift_id,
                    from_time,
                    to_time,
                    flag,
                    (case when tag = 1 then 'Normal' when tag = 2 then 'Full Day' when tag = 3 then 'Half Day' when tag = 4 then 'Absent' when tag = 5 then 'Overwrite' end) as tag,
                    MAX(case when tag = 5 then overwrite else working_hrs end) AS working_hrs,
                    MAX(break_hrs) AS break_hrs,
                    SUM(p_cnt) AS p_cnt,
                    min(punch_min_datetime) as min_punch_in,
                    max(punch_max_datetime) as max_punch_out
                FROM journal_entries j
                WHERE j.ho_id = ? AND attandance_date >= ? AND attandance_date <= ? AND j.tag > 0
                GROUP BY 1, 2, 3, 4,5, 6, 7, 8
