        let parameters = [];


        if(payrollId > 0){
            //freeze config
            // console.log('in freeze query');

            query = `
            SELECT 
                pd.ho_id,
                pd.employee_id as user_id,
                pd.bank_details,
                emp.code,
                emp.name,
                emp.device_employee_id,
                emp.status as employee_status,
