                a.p_cnt,
                a.flag,
                a.tag,
                CONCAT(
                    IF(FLOOR(TIME_TO_SEC(working_hrs) / 3600) > 0, CONCAT(FLOOR(TIME_TO_SEC(working_hrs) / 3600), 'h '), ''),
                    IF(MOD(FLOOR(TIME_TO_SEC(working_hrs) / 60), 60) > 0, CONCAT(MOD(FLOOR(TIME_TO_SEC(working_hrs) / 60), 60), 'm'), '')
                ) as working_hrs,
                a.working_hrs AS working_hrs_num,
                CONCAT(
                    IF(FLOOR(TIME_TO_SEC(break_hrs) / 3600) > 0, CONCAT(FLOOR(TIME_TO_SEC(break_hrs) / 3600), 'h '), ''),
