    static async getMusterSpreadSheetReports(ho_id, branch_id, user_organizations, start_date, end_date, user_type, department_ids) {

        const { columns } = this.generateDynamicColumnsMusterReport('ds.calculated_status', start_date, end_date, 'ds', true);

        let department_condition = "";
        if (user_type !== 1 && department_ids !== null) {
          if (department_ids) {
            department_condition = ` AND emp.department_id IN (${department_ids})`;
          } else {
            department_condition = ` AND emp.department_id IS NULL`;
          }
        }

        let query = `
        WITH OrgConfigs AS (
            SELECT
                ho_id,
                MAX(CASE WHEN key_name = 'full_day_hrs' THEN key_value END) AS org_full_day_hrs,
                MAX(CASE WHEN key_name = 'half_day_hrs' THEN key_value END) AS org_half_day_hrs
            FROM organization_configurations
            WHERE ho_id = ? 
              AND key_name IN ('full_day_hrs', 'half_day_hrs')
              AND status = 1
            GROUP BY ho_id
        ),
        EmpConfigs AS (
            SELECT
                ho_id,
                employee_id,
                MAX(CASE WHEN key_name = 'full_day_hrs' THEN key_value END) AS emp_full_day_hrs,
                MAX(CASE WHEN key_name = 'half_day_hrs' THEN key_value END) AS emp_half_day_hrs
            FROM employee_payroll_configurations
            WHERE ho_id = ? 
              AND key_name IN ('full_day_hrs', 'half_day_hrs')
              AND status = 1
            GROUP BY ho_id, employee_id
        ),
        ValidEmployees AS (
            SELECT
                emp.id AS user_id,
                emp.ho_id,
                emp.code,
                emp.name,
                emp.device_employee_id,
                emp.department_id,
                emp.designation_id,
                MIN(eb.branch_id) AS branch_id
            FROM employees emp
            JOIN employee_branches eb
                ON eb.employee_id = emp.id
                AND eb.ho_id = emp.ho_id
                AND eb.status = 1
            JOIN employee_salaries es
                ON es.emp_id = emp.id
                AND es.ho_id = emp.ho_id
                AND es.salary_type = 1 
            WHERE emp.ho_id = ?
              AND emp.status = 1 
              ${department_condition}
            GROUP BY emp.id
        ),
        DailyAttendance AS (
            SELECT
                j.ho_id,
                j.attandance_date,
                j.user_id,
                COALESCE(ec.emp_full_day_hrs, oc.org_full_day_hrs) AS full_day_hrs,
                COALESCE(ec.emp_half_day_hrs, oc.org_half_day_hrs) AS half_day_hrs,
                MAX(j.flag) as flag_int,
                MAX(j.tag) as tag_int,
                MAX(j.overwrite) as overwrite_val,
                MAX(j.working_hrs) AS max_working_hrs, 
                MAX(j.break_hrs) AS break_hrs,
                SUM(j.p_cnt) AS p_cnt,
                MAX(j.leave_id) as leave_id,
                GROUP_CONCAT(j.id ORDER BY j.id SEPARATOR ',') AS je_ids
            FROM journal_entries j
            JOIN ValidEmployees ve ON j.user_id = ve.user_id AND j.ho_id = ?
            LEFT JOIN OrgConfigs oc ON j.ho_id = oc.ho_id
            LEFT JOIN EmpConfigs ec ON j.ho_id = ec.ho_id AND j.user_id = ec.employee_id
            WHERE j.ho_id = ?
              AND j.attandance_date BETWEEN ? AND ? 
              AND j.tag > 0 
            GROUP BY
                j.ho_id,
                j.attandance_date,
                j.user_id
        ),
        DailyStatus AS (
            SELECT
                da.*, 
                ve.code, ve.name, ve.device_employee_id, ve.department_id, ve.designation_id, ve.branch_id,
                (CASE da.tag_int WHEN 1 THEN 'Normal' WHEN 2 THEN 'Full Day' WHEN 3 THEN 'Half Day' WHEN 4 THEN 'Absent' WHEN 5 THEN 'Overwrite' END) as tag_str,
                (CASE WHEN da.tag_int = 5 THEN da.overwrite_val ELSE da.max_working_hrs END) AS effective_working_hrs,
                (CASE
                    WHEN da.flag_int = 10 THEN 'Holiday'      
                    WHEN da.flag_int = 9 THEN 'Week Off'      
                    WHEN da.tag_int = 2 THEN 'Full Day'       
                    WHEN da.tag_int = 3 THEN 'Half Day'       
                    WHEN da.tag_int = 4 THEN 'Absent'         
                    WHEN da.tag_int = 5 THEN                  
                        CASE
                            WHEN TIME_TO_SEC(da.overwrite_val) >= TIME_TO_SEC(da.full_day_hrs) THEN 'Full Day'
                            WHEN TIME_TO_SEC(da.overwrite_val) >= TIME_TO_SEC(da.half_day_hrs) THEN 'Half Day'
                            ELSE 'Absent'
                        END
                    WHEN TIME_TO_SEC(CASE WHEN da.tag_int = 5 THEN da.overwrite_val ELSE da.max_working_hrs END) >= TIME_TO_SEC(da.full_day_hrs) THEN 'Full Day'
                    WHEN TIME_TO_SEC(CASE WHEN da.tag_int = 5 THEN da.overwrite_val ELSE da.max_working_hrs END) >= TIME_TO_SEC(da.half_day_hrs) THEN 'Half Day'
                    WHEN da.flag_int = 5 THEN 'Leave'
                    WHEN da.flag_int = 6 THEN 'First Half Leave'
                    WHEN da.flag_int = 7 THEN 'Second Half Leave'
                    ELSE 'Absent'
                END) as calculated_status,
                CASE WHEN MOD(da.p_cnt, 2) <> 0 THEN 'Problem' END AS error_state,
                CASE WHEN da.leave_id = 0 AND da.flag_int = 5 THEN 'LWP' ELSE l.name END AS leave_name,
                htd.holiday_name
            FROM DailyAttendance da
            JOIN ValidEmployees ve ON da.user_id = ve.user_id
            LEFT JOIN leaves l ON l.id = da.leave_id AND l.ho_id = da.ho_id
            LEFT JOIN holiday_template_details htd ON htd.id = da.leave_id AND da.flag_int = 10 AND htd.ho_id = da.ho_id
        )
        SELECT
            ds.ho_id,
            ds.branch_id as org_id,
            ds.user_id,
            ds.code,   
            ds.name,   
            ds.device_employee_id,
            des.name as designation,
            dep.name as department,
            COUNT(CASE WHEN ds.calculated_status = 'Full Day' THEN 1 END) as working_days,
            COUNT(CASE WHEN ds.calculated_status = 'Absent' THEN 1 END) as absent_days,
            ${columns}
        FROM DailyStatus ds
        LEFT JOIN departments dep ON dep.id = ds.department_id AND dep.ho_id = ds.ho_id
        LEFT JOIN designations des ON des.id = ds.designation_id AND des.ho_id = ds.ho_id
        JOIN employee_branches eb 
          ON eb.employee_id = ds.user_id 
          AND eb.status = 1
        WHERE ds.ho_id = ?  
          AND eb.ho_id = ?`;
        if (branch_id) {
          query += ` AND eb.branch_id = ? `;
        } else if (user_organizations !== "") {
          query += ` AND eb.branch_id IN (${user_organizations}) `;
        }

        query += ` GROUP BY ds.ho_id, ds.user_id ORDER BY ds.ho_id, ds.user_id`;

        let queryParams = [ ho_id, ho_id, ho_id, ho_id, ho_id, start_date, end_date, ho_id, ho_id ];

        if (branch_id) {
          queryParams.push(branch_id);
        }
        // console.log('main query >> \n', mysql.format(query, queryParams));

        let leaveQuery = `
          SELECT
            emp.ho_id,
            emp.id AS user_id,
            COALESCE((
              SELECT JSON_ARRAYAGG(
                JSON_OBJECT(
                  'leave_balance', IFNULL(leave_balance, 0),
                  'leave_id', leave_id,
                  'leave_name', leave_name
                )
              )
              FROM (
                SELECT
                  elb.leave_balance,
                  elb.leave_id,
                  l.name AS leave_name
                FROM employee_leave_balances AS elb
                JOIN leaves AS l 
                  ON l.ho_id = ? 
                  AND elb.leave_id = l.id
                WHERE elb.ho_id = ? 
                  AND elb.employee_id = emp.id 
                  AND elb.status = 1 
                  AND l.status = 1
                UNION ALL
                SELECT
                  0 AS leave_balance,
                  0 AS leave_id,
                  'LWP' AS leave_name
              ) AS leave_data
            ), '[]') AS leave_data
          FROM employees emp
          JOIN employee_branches eb 
            ON eb.employee_id = emp.id 
            AND eb.status = 1
          JOIN employee_salaries es
            ON es.ho_id = ?
            AND es.emp_id = emp.id
            AND es.salary_type = 1
          WHERE emp.ho_id = ?
        `;
        if (branch_id) {
          leaveQuery += ` AND eb.branch_id = ? `;
        } else if (user_organizations !== "") {
          leaveQuery += ` AND eb.branch_id IN (${user_organizations}) `;
        }
        leaveQuery += ` ${department_condition} GROUP BY emp.ho_id, emp.id `;

        let leaveParams = [ho_id, ho_id, ho_id, ho_id];
        if (branch_id) {
          leaveParams.push(branch_id);
        }

        const connection = await db2.getConnection();
        try {
          const [[mdata], [leaveData]] = await Promise.all([
            connection.query(query, queryParams),
            connection.query(leaveQuery, leaveParams)
          ]);

        const leaveDataMap = new Map();
        leaveData.forEach(row => leaveDataMap.set(row.user_id, row));

        const musterSpreadsheetData = mdata.map(mrow => {
          const leaveRow = leaveDataMap.get(mrow.user_id) || {};
          return {
            ...mrow,
            leave_data: leaveRow.leave_data || "[]",
          };
        });

        return musterSpreadsheetData;
        } catch (err) {
            throw err;
        } finally {
            await connection.destroy();
        }

    }

    static async getLeaveTakenReports(ho_id, branch_id, user_organizations, start_date, end_date, user_type, department_ids) {

        var query = '';
        var parameters = [];
        var user_organizations_condition = '';

        if(user_organizations !== '') {
            user_organizations_condition = `and eb.branch_id in (${user_organizations})`;
        }

        var department_condition = ""
        if (user_type !== 1 && department_ids !== null) {
            if (department_ids) {
                department_condition = ` AND e.department_id IN (${department_ids})`;
            } else {
                department_condition = ` AND e.department_id IS NULL`;
            }
        }

        query = `
            with leave_data as 
            (SELECT 
                je.ho_id,
                je.leave_id,
                je.user_id,
                e.name,
                e.code,
                e.status as employee_status,
                e.tag_updated_at,
                des.name as designation,
                d.name as department,
                (case when flag in (5,6,7) and leave_id > 0 then l.name when flag in (5,6,7) and leave_id = 0 then 'LWP' end) as leave_name,
                sum(case when flag = 5 AND tag <> 0 then 1 when flag in (6,7) AND tag <> 0 then 0.5 else 0 end) as leave_taken
            FROM 
                journal_entries je left join leaves l on  je.leave_id = l.id and l.ho_id = ?
            join
                employees e on e.id = je.user_id and e.ho_id = je.ho_id ${department_condition}
                LEFT JOIN departments d on e.department_id = d.id and  d.ho_id = ?
                LEFT JOIN designations des on des.id = e.designation_id and des.ho_id = ?
            WHERE 
                je.ho_id = ? and je.attandance_date >= ? and je.attandance_date <= ? and e.ho_id = ? and e.status > 0 and je.flag in (5,6,7) and je.payroll_tag = 0
            group by 1,2,3  
            ORDER BY leave_taken  DESC)
            Select 
                ld.ho_id,
                eb.branch_id,
                ld.name,
                ld.code,
                ld.designation,
                ld.department,
                ld.leave_id,
                ld.user_id,
                ld.leave_name,
                ld.leave_taken,
                ld.employee_status,
                ld.tag_updated_at
            from 
                leave_data ld JOIN employee_branches eb ON eb.employee_id = ld.user_id and eb.status = 1 ${user_organizations_condition}
            group by ld.ho_id,ld.name, ld.leave_id,ld.user_id,ld.leave_name, ld.leave_taken
        `;

        parameters = [ho_id, ho_id, ho_id, ho_id, start_date, end_date, ho_id];

        const connection = await db2.getConnection();      
        try {
            const [row] = await connection.query(query, parameters);
            return row;
        } catch (err) {
            throw err;
        } finally {
            await connection.destroy();
        }

    }

