                pd.ot_amt as overtime_wage,
                pd.total_penalty_day,
                pd.late_penalty_allow_days,
                pd.penalties_amt as late_panelty,
                pd.extras_amt as extra_amount_with_paid_off_days,
                pd.holiday,
                pd.salary as basic_salary,
                emp.status as employee_status,
                emp.tag_updated_at,
                es.bank_details
            from payroll_details pd            
            JOIN employees emp ON emp.id = pd.employee_id and emp.ho_id = ?
            LEFT JOIN employee_salaries es ON es.emp_id = pd.employee_id and es.ho_id = ?
            where 
                pd.ho_id = ? AND pd.payroll_id = ? AND pd.status = 1 AND pd.salary_type = 1
            GROUP BY pd.ho_id, pd.user_id, emp.name, emp.code   
            `;

            parameters = [ho_id, ho_id, ho_id, payrollId];

            const connection = await db2.getConnection();      
            try {
                const [row] = await connection.query(query, parameters);
                return row;
            } catch (err) {
                console.log('err',err)
                throw err;
            } finally {
                await connection.destroy();
            }

        } else {
            // console.log('in else');

            var user_organizations_condition = '';
            var department_condition = ''

            if(user_organizations !== '') {
                user_organizations_condition = `and eb.branch_id in (${user_organizations}) `;
            }

            if (user_type !== 1 && department_ids !== null) {
            if (department_ids) {
                department_condition = ` AND emp.department_id IN (${department_ids})`;
            } else {
                department_condition = ` AND emp.department_id IS NULL`;
            }
            }

            let additionalQuery = '';
            let additionJoin = '';
            let additionLoanAdvJoin = '';
            let additionalSelectParams = '';
            let grossWagesQuery = '';
            // let statutoryComplianceQuery = '';
            // let otherEarningQUery = '';
            if(payrollId > 0) {
                additionalQuery = `
                freeze_salary as
            (
                SELECT 
                    pd.daily_wage,
                    pd.gross_earnings,
                            pd.ot_amt,
                    pd.penalties_amt,
                        pd.extras_amt,
                        pd.loan_adv_amt,
                        pd.ho_id,
                        pd.employee_id
                                    FROM payroll_details pd
                                    WHERE pd.ho_id = ${ho_id}
                                    AND pd.payroll_id = ${payrollId}
                                    AND pd.status = 1
                                )
                `

                additionJoin = `LEFT JOIN freeze_salary fs ON fs.ho_id = dj.ho_id AND fs.employee_id = dj.user_id`
                additionalSelectParams = `
                MAX(fs.daily_wage) as gross_wage,
                MAX(fs.ot_amt) as overtime_wage,
                MAX(fs.penalties_amt)  as late_panelty,
                MAX(fs.extras_amt) as extra_amount_with_paid_off_days,
                ${payrollId} as payroll_id,
                fs.loan_adv_amt as loan_amount,
                0 as advance_amount,
                `
            } else {

                 additionalSelectParams = `
                 round((SUM(dj.full_day + (dj.half_day / 2) + dj.on_leave + dj.off_days)) * (MAX(es.salary) / MAX(dj.daily_wage_days)),0) as gross_wage,
                CASE
                    WHEN overtime_method = 1 THEN
                        MAX(overtime_hourly_pay) / 60 * SUM(overtime_hours)
                    ELSE
                        (MAX(salary_per_day) / (TIME_TO_SEC(full_day_hrs) / 60)) * MAX(overtime_hourly_pay) * SUM(overtime_hours)
                END AS overtime_wage,
                IFNULL(pd.total_penalty, 0) + IFNULL(extra.panelty_extra, 0) AS late_panelty,
                ifnull(round((MAX(case when off_d_settle_type = 1 then (off_d_extra_work_pay - 1) else 0 end) * SUM(dj.off_days_paid) * round(MAX(es.salary) / MAX(dj.daily_wage_days),3)),2),0) + MAX(ifnull(extra.extra_amount,0)) as extra_amount_with_paid_off_days,
                0 as payroll_id,
                ld.final_amount as loan_amount,
                ad.final_amount_advance as advance_amount,
                `

                additionalQuery = `
                loan_details as
                (
                SELECT 
                    ho_id,
                    employee_id,
                    SUM(case when extra_amount < remaining_amount and extra_amount is not null and extra_amount > 0 then extra_amount when installment_amt > 0 and remaining_amount > installment_amt then installment_amt else remaining_amount end) as final_amount
                FROM
                (
                SELECT 
                    lap.ho_id,
                    lap.employee_id,
                    lap.id,
                    lap.installment_amt,
                    lap.type,
                    MAX(lap.credit) as main_amount,
                    SUM(lap2.debit) as deducted_amount,
                    ifnull(MAX(lap.credit),0) - ifnull(SUM(lap2.debit),0) as remaining_amount,
                    SUM(pe.amount) as extra_amount
                FROM 
                    loan_advance_payments lap 
                    left join loan_advance_payments lap2 on lap.id = lap2.reference_id and lap2.status = 1
                    left join payroll_extras pe on pe.ho_id = ${ho_id} and lap.id = pe.loan_id AND pe.status = 1 and pe.type = 2 and pe.adjustment_type = 2
                WHERE
                    lap.ho_id = ${ho_id} AND
                    lap.transaction_date <= '${end_date}' AND
                    lap.status = 1 AND
                    lap.reference_id = 0
                    AND lap.type = 1
                    AND pe.id is null
                Group by 1,2,3,4
                    ) a
                    GROUP BY 1,2
                ),

                advance_details as
                (
                SELECT 
                    ho_id,
                    employee_id,
                    SUM(case when extra_amount < remaining_amount and extra_amount is not null and extra_amount > 0 then extra_amount when installment_amt > 0 and remaining_amount > installment_amt then installment_amt else remaining_amount end) as final_amount_advance
                FROM
                (
                SELECT 
                    lap.ho_id,
                    lap.employee_id,
                    lap.id,
                    lap.installment_amt,
                    lap.type,
                    MAX(lap.credit) as main_amount,
                    SUM(lap2.debit) as deducted_amount,
                    ifnull(MAX(lap.credit),0) - ifnull(SUM(lap2.debit),0) as remaining_amount,
                    (
                        SELECT SUM(pe.amount)
                        FROM payroll_extras pe
                        JOIN loan_advance_payments lap_status_check 
                            ON pe.loan_id = lap_status_check.id 
                            AND lap_status_check.status = 1
                        WHERE pe.ho_id = lap.ho_id
                            AND pe.loan_id = lap.id
                            AND pe.status = 1 
                            AND pe.type = 2 
                            AND pe.adjustment_type = 2
                    ) AS extra_amount
                FROM 
                    loan_advance_payments lap 
                    left join loan_advance_payments lap2 on lap.id = lap2.reference_id and lap2.status = 1
                    LEFT JOIN (
                        SELECT loan_id
                        FROM payroll_extras 
                        WHERE ho_id = ${ho_id} AND status = 1 AND type = 2 AND adjustment_type = 2
                        GROUP BY loan_id
                    ) extra ON lap.id = extra.loan_id
                WHERE
                    lap.ho_id = ${ho_id} AND
                    lap.transaction_date <= '${end_date}' AND
                    lap.status = 1 AND
                    lap.reference_id = 0
                    AND (extra.loan_id > 0 OR lap.type = 2)
                Group by 1,2,3,4
                    ) a
                WHERE 
                    extra_amount IS NULL 
                    OR extra_amount > 0
                GROUP BY 1,2
                ),

                late_entries AS (
                    SELECT
                        ho_id,
                        user_id,
                        GROUP_CONCAT(
                            late_minutes_list_date_wise
                            ORDER BY attandance_date
                            SEPARATOR ','
                        ) AS late_minutes_list,
                        MAX(salary) AS salary,
                        MAX(daily_wage_calculate_as) AS daily_wage_calculate_as,
                        MAX(daily_wage_no_of_days) AS daily_wage_no_of_days,
                        MAX(total_days_in_month) AS total_days_in_month,
                        MAX(full_day_penalty_enable) AS full_day_penalty_enable,
                        MAX(full_day_penalty_time) AS full_day_penalty_time,
                        MAX(half_day_penalty_enable) AS half_day_penalty_enable,
                        MAX(half_day_penalty_time) AS half_day_penalty_time,
                        MAX(late_penalty_method) AS late_penalty_method,
                        MAX(late_penalty_per_minute) AS late_penalty_per_minute,
                        MAX(late_penalty_per_day) AS late_penalty_per_day,
                        MAX(late_penalty_multiplier_wage) AS late_penalty_multiplier_wage,
                        MAX(late_penalty_allow_days) AS late_penalty_allow_days
                    FROM detail_journal
                    GROUP BY user_id
                ),

                split_minutes AS (
                    SELECT
                        le.*,
                        jt.pos AS day_index,
                        CAST(jt.val AS UNSIGNED) AS minutes_late
                    FROM late_entries le,
                    JSON_TABLE(
                        CONCAT('[', REPLACE(le.late_minutes_list, ',', ','), ']'),
                        '$[*]' COLUMNS (
                            pos FOR ORDINALITY,
                            val INT PATH '$'
                        )
                    ) AS jt
                ),

                penalty_calc AS (
                    SELECT
                        ho_id,
                        user_id,
                        day_index,
                        minutes_late,
                        CASE 
                            WHEN full_day_penalty_enable = '1' AND (minutes_late * 60) > TIME_TO_SEC(full_day_penalty_time) AND salary > 0
                            THEN (salary / (CASE WHEN daily_wage_calculate_as = 2 THEN daily_wage_no_of_days ELSE total_days_in_month END))
                            WHEN half_day_penalty_enable = '1' AND (minutes_late * 60) > TIME_TO_SEC(half_day_penalty_time) AND salary > 0
                            THEN (salary / (CASE WHEN daily_wage_calculate_as = 2 THEN daily_wage_no_of_days ELSE total_days_in_month END)) / 2
                            ELSE 0 
                        END AS day_wise_penalty,
                        CASE 
                            WHEN late_penalty_method = 1 THEN late_penalty_per_day
                            WHEN late_penalty_method = 2 THEN
                            CASE 
                                WHEN salary > 0 THEN late_penalty_multiplier_wage * (salary / (CASE WHEN daily_wage_calculate_as = 2 THEN daily_wage_no_of_days ELSE total_days_in_month END))
                                ELSE 0 
                            END
                            WHEN late_penalty_method = 3 THEN late_penalty_per_minute * minutes_late
                            ELSE 0 
                        END AS mins_wise_penalty,
                        late_penalty_allow_days
                    FROM split_minutes
                ),

                penalized_data AS (
                    SELECT
                    ho_id,
                    user_id,
                    SUM(
                        CASE 
                        WHEN day_index > late_penalty_allow_days THEN
                            CASE 
                                WHEN day_wise_penalty > 0 THEN day_wise_penalty
                                ELSE GREATEST(mins_wise_penalty, 0)
                            END
                        ELSE 0
                        END
                    ) AS total_penalty
                    FROM penalty_calc
                    GROUP BY user_id
                )
                `
                additionLoanAdvJoin = `
                LEFT JOIN loan_details ld on ld.ho_id = dj.ho_id and ld.employee_id = dj.user_id 
                LEFT JOIN advance_details ad on ad.ho_id = dj.ho_id and ad.employee_id = dj.user_id 
                LEFT JOIN penalized_data pd ON pd.ho_id = dj.ho_id and dj.user_id = pd.user_id
                `
                grossWagesQuery = is_salary_compliance ? `
                (
                  SELECT monthly_gross                        
                  FROM (SELECT 
                  SUM(
                      CASE 
                          WHEN gw.calculation_type = 1 THEN (COALESCE(es.salary, 0) * gw.amount / 100)
                          WHEN gw.calculation_type = 2 THEN gw.amount
                          ELSE 0
                      END
                  ) AS monthly_gross

                  FROM gross_wages gw
                  LEFT JOIN employee_salaries es ON es.emp_id = dj.user_id
                  LEFT JOIN gross_wages gw_sub 
                      ON gw_sub.ho_id = gw.ho_id
                      AND gw_sub.employee_id = dj.user_id
                      AND gw_sub.type = 1
                      AND gw_sub.effective_date = "${start_date}"
                      AND gw_sub.status = 1
                      AND gw.head_type = gw_sub.head_type
                  WHERE gw.ho_id = ${ho_id} 
                      AND gw.type = 1
                      AND gw.status = 1
                      AND gw.effective_date = "${start_date}" 
                      AND (
                          gw.employee_id = dj.user_id 
                          OR (gw.employee_id = 0 AND gw_sub.head_type IS NULL)
                 ) 
               ) AS subquery
              ) AS monthly_gross,

              (
                  SELECT monthly_deduction                        
                  FROM (SELECT 
                  SUM(
                      CASE 
                          WHEN gw.calculation_type = 1 THEN (COALESCE(es.salary, 0) * gw.amount / 100)
                          WHEN gw.calculation_type = 2 THEN ((gw.amount / NULLIF(MAX(CASE 
                                        WHEN daily_wage_calculate_as = 2 THEN COALESCE(daily_wage_no_of_days, 0) 
                                        ELSE COALESCE(total_days_in_month, 0) 
                                        END), 0)) * SUM(COALESCE(dj.full_day, 0) + COALESCE(dj.half_day, 0) / 2 + COALESCE(dj.on_leave, 0) + COALESCE(dj.off_days, 0)))
                          ELSE 0
                      END
                  ) AS monthly_deduction

                  FROM gross_wages gw
                  LEFT JOIN employee_salaries es ON es.emp_id = dj.user_id
                  LEFT JOIN gross_wages gw_sub 
                      ON gw_sub.ho_id = gw.ho_id
                      AND gw_sub.employee_id = dj.user_id
                      AND gw_sub.type = 2
                      AND gw_sub.effective_date = "${start_date}"
                      AND gw_sub.status = 1
                      AND gw.head_type = gw_sub.head_type
                  WHERE gw.ho_id = ${ho_id} 
                      AND gw.type = 2
                      AND gw.status = 1
                      AND gw.effective_date = "${start_date}"
                      AND (
                          gw.employee_id = dj.user_id 
                          OR (gw.employee_id = 0 AND gw_sub.head_type IS NULL)
                 ) 
               ) AS subquery
              ) AS monthly_deduction,

          (
              SELECT SUM(stat_pay)
              FROM (
                  WITH 
                   attendance_summary AS (
                    SELECT 
                        user_id,
                        ho_id,
                        SUM(COALESCE(full_day, 0) + COALESCE(half_day, 0) / 2 + COALESCE(on_leave, 0) + COALESCE(off_days, 0)) AS total_attendance_days,
                        MAX(CASE 
                            WHEN daily_wage_calculate_as = 2 THEN COALESCE(daily_wage_no_of_days, 0) 
                        ELSE COALESCE(total_days_in_month, 0) 
                    END) AS effective_wage_days
                    FROM detail_journal
                    GROUP BY user_id
                ),
                bonus_incentive_data AS (
                        SELECT 
                           bi.ho_id,
                            SUM(
                            COALESCE(CASE 
                                WHEN COALESCE(ebi.bonus, bi.bonus) = 1 
                                THEN (COALESCE(ebi.bonus_amount, bi.bonus_amount)/ NULLIF(MAX(CASE 
                                        WHEN daily_wage_calculate_as = 2 THEN COALESCE(daily_wage_no_of_days, 0) 
                                        ELSE COALESCE(total_days_in_month, 0) 
                                        END), 0) * SUM(COALESCE(dj.full_day, 0) + COALESCE(dj.half_day, 0) / 2 + COALESCE(dj.on_leave, 0) + COALESCE(dj.off_days, 0))) ELSE 0 END, 0) +
                            COALESCE(CASE 
                                WHEN COALESCE(ebi.incentive, bi.incentive) = 1 
                                THEN COALESCE(ebi.incentive_amount, bi.incentive_amount) 
                                ELSE 0 
                            END, 0)
                        ) AS other_earning
                        FROM bonus_incentive bi
                        LEFT JOIN employee_bonus_incentive ebi 
                            ON bi.ho_id = ebi.ho_id 
                            AND bi.effective_date = ebi.effective_date 
                            AND ebi.employee_id = dj.user_id 
                            AND ebi.status = 1
                        WHERE bi.ho_id = ${ho_id}
                        AND bi.status = 1
                        AND bi.effective_date = '${start_date}'
                        GROUP BY COALESCE(ebi.employee_id, 0)
                        ),
                    gross_data AS (
                            SELECT 
                            gw1.ho_id,
                            COALESCE(gw2.employee_id, gw1.employee_id) as employee_id,
                            COALESCE(gw2.calculation_type, gw1.calculation_type) as calculation_type,
                            COALESCE(gw2.amount, gw1.amount) as amount
                            FROM gross_wages gw1
                            LEFT JOIN gross_wages gw2
                                ON gw1.ho_id = gw2.ho_id
                                AND gw1.head_type = gw2.head_type
                                AND gw1.effective_date = gw2.effective_date
                                AND gw1.status = gw2.status
                                AND gw2.employee_id = dj.user_id
                            WHERE gw1.ho_id = ${ho_id}
                                AND gw1.head_type IN ('basic_pay', 'dearness_allowance')
                                AND gw1.effective_date = '${start_date}'
                                AND gw1.status = 1
                                AND gw1.employee_id = 0
                                ORDER BY gw1.employee_id DESC
                    ),
                  statutory_data AS (
                      SELECT 
                          COALESCE(esc.provident_fund, sc.provident_fund) AS provident_fund,
                          COALESCE(esc.pf_rate, sc.pf_rate) AS pf_rate,
                          es.salary AS es_salary,
                          round((SUM(dj.full_day + (dj.half_day / 2) + dj.on_leave + dj.off_days)) * (MAX(es.salary) / MAX(dj.daily_wage_days)),0) as daily_salary,
                          SUM(COALESCE(
                                CASE 
                                    WHEN IFNULL(s.calculation_type, 2) = 1 THEN (((es.salary / sum1.effective_wage_days) * sum1.total_attendance_days)* IFNULL(s.amount, 0) / 100)
                                    WHEN IFNULL(s.calculation_type, 2) = 2 THEN IFNULL((s.amount / sum1.effective_wage_days) * sum1.total_attendance_days, 0)
                                    ELSE 0
                                END, 0
                            )) AS salary,
                          COALESCE(esc.esic, sc.esic) AS esic,
                          COALESCE(esc.esic_rate, sc.esic_rate) AS esic_rate,
                          COALESCE(esc.professional_tax, sc.professional_tax) AS professional_tax,
                          COALESCE(esc.pt_amount, sc.pt_amount) AS pt_amount,
                          COALESCE(esc.labour_welfare_fund, sc.labour_welfare_fund) AS labour_welfare_fund,
                          COALESCE(esc.lwf_amount, sc.lwf_amount) AS lwf_amount,
                          COALESCE(bid.other_earning,0) as other_earning,

                          CASE
                            WHEN overtime_method = 1 THEN
                                MAX(overtime_hourly_pay) / 60 * SUM(overtime_hours)
                            ELSE
                                (MAX(salary_per_day) / (TIME_TO_SEC(full_day_hrs) / 60)) * MAX(overtime_hourly_pay) * SUM(overtime_hours)
                        END AS overtime_wage,

                        ifnull(round((MAX(case when off_d_settle_type = 1 then (off_d_extra_work_pay - 1) else 0 end) 
                        * SUM(dj.off_days_paid) * round(MAX(es.salary) / MAX(dj.daily_wage_days),3)),2),0) 
                        + MAX(ifnull(extra.extra_amount,0)) as extra_amount_esic

                         FROM statutory_compliance sc
                      LEFT JOIN employee_statutory_compliance esc 
                          ON sc.ho_id = esc.ho_id 
                          AND sc.effective_date = esc.effective_date 
                          AND esc.employee_id = dj.user_id
                          AND esc.status = 1
                      LEFT JOIN employee_salaries es 
                          ON es.emp_id = dj.user_id
                    LEFT JOIN attendance_summary as sum1 
                            ON sum1.user_id = dj.user_id
                            AND sum1.ho_id = sc.ho_id
                    LEFT JOIN bonus_incentive_data as bid
                            ON bid.ho_id = ${ho_id}
                      LEFT JOIN gross_data s 
                      ON s.ho_id = ${ho_id}
                      WHERE sc.ho_id = ${ho_id}
                      AND sc.status = 1
                      AND sc.effective_date = '${start_date}'
                      ORDER BY s.employee_id DESC
                  )
                  SELECT 
                        LEAST(
                            SUM(salary)
                            * COALESCE((CASE 
                                        WHEN provident_fund = 1 THEN pf_rate 
                                        ELSE 0 
                                        END), 0) 
                            / 100,
                            1800
                        )
                      + (CASE 
                          WHEN COALESCE(es_salary,0) <= 21000 THEN ((COALESCE(daily_salary,0) + COALESCE(other_earning,0) + COALESCE(overtime_wage,0) + COALESCE(extra_amount_esic,0)) * (CASE WHEN esic = 1 THEN esic_rate ELSE 0 END) / 100) 
                          ELSE 0 
                      END)
                     + COALESCE(
                            CASE 
                                WHEN professional_tax = 1 THEN 
                                    CASE 
                                        WHEN COALESCE(
                                            SUM(
                                                COALESCE(dj.full_day, 0) + 
                                                COALESCE(dj.half_day, 0) / 2 + 
                                                COALESCE(dj.on_leave, 0) + 
                                                COALESCE(dj.off_days, 0)
                                            ), 
                                        0) > 0 
                                        THEN pt_amount 
                                        ELSE 0 
                                    END
                                ELSE 0 
                            END,
                        0)


                     + COALESCE(
                            CASE 
                                WHEN labour_welfare_fund = 1 THEN 
                                    CASE 
                                        WHEN COALESCE(
                                            SUM(
                                                COALESCE(dj.full_day, 0) + 
                                                COALESCE(dj.half_day, 0) / 2 + 
                                                COALESCE(dj.on_leave, 0) + 
                                                COALESCE(dj.off_days, 0)
                                            ), 
                                        0) > 0 
                                        THEN lwf_amount 
                                        ELSE 0 
                                    END
                                ELSE 0 
                            END,
                        0) AS stat_pay
                  FROM statutory_data
                  ORDER BY salary DESC
              ) AS subquery
          ) AS monthly_statutory_compliance,

          (
              SELECT SUM(other_earning)
              FROM (
                  WITH other_earning_data AS (
                      SELECT 
                          COALESCE(ebi.bonus, bi.bonus) AS bonus,
                          COALESCE(ebi.bonus_amount, bi.bonus_amount) AS bonus_amount,                                
                          COALESCE(ebi.incentive, bi.incentive) AS incentive,
                          COALESCE(ebi.incentive_amount, bi.incentive_amount) AS incentive_amount
                      FROM bonus_incentive bi
                      LEFT JOIN employee_bonus_incentive ebi 
                          ON bi.ho_id = ebi.ho_id 
                          AND bi.effective_date = ebi.effective_date 
                          AND ebi.employee_id = dj.user_id 
                          AND ebi.status = 1
                      WHERE bi.ho_id = ${ho_id}
                      AND bi.status = 1
                      AND bi.effective_date = '${start_date}' 
                  )
                  SELECT 
                       (COALESCE(CASE WHEN bonus = 1 THEN ((bonus_amount / NULLIF(MAX(CASE 
                                        WHEN daily_wage_calculate_as = 2 THEN COALESCE(daily_wage_no_of_days, 0) 
                                        ELSE COALESCE(total_days_in_month, 0) 
                                        END), 0)) * SUM(COALESCE(dj.full_day, 0) + COALESCE(dj.half_day, 0) / 2 + COALESCE(dj.on_leave, 0) + COALESCE(dj.off_days, 0))) ELSE 0 END,0) 
                       + COALESCE(CASE WHEN incentive = 1 THEN incentive_amount ELSE 0 END,0)) AS other_earning
                  FROM other_earning_data
              ) AS subquery
          ) AS monthly_other_earning,

           (
              SELECT 
                  COALESCE(SUM(
                      CASE 
                          WHEN gw.calculation_type = 1 THEN (es.salary * gw.amount / 100) 
                          WHEN gw.calculation_type = 2 THEN gw.amount                     
                          WHEN gw.calculation_type = 3 THEN ( 
                              COALESCE(
                                  (SELECT 
                                      CASE 
                                          WHEN gw_bp.calculation_type = 1 THEN (es.salary * gw_bp.amount / 100) 
                                          WHEN gw_bp.calculation_type = 2 THEN gw_bp.amount                     
                                          ELSE 0 
                                      END
                                  FROM gross_wages gw_bp 
                                  WHERE gw_bp.ho_id = gw.ho_id 
                                  AND gw_bp.employee_id = gw.employee_id 
                                  AND gw_bp.head_type = 'basic_pay' 
                                  LIMIT 1
                                  ),
                                  (SELECT 
                                      CASE 
                                          WHEN gw_ho.calculation_type = 1 THEN (es.salary * gw_ho.amount / 100) 
                                          WHEN gw_ho.calculation_type = 2 THEN gw_ho.amount                     
                                          ELSE 0 
                                      END
                                  FROM gross_wages gw_ho 
                                  WHERE gw_ho.ho_id = gw.ho_id 
                                  AND gw_ho.employee_id = 0 
                                  AND gw_ho.head_type = 'basic_pay' 
                                  LIMIT 1
                                  ),
                                  0
                              ) * gw.amount / 100 
                          )
                          ELSE 0 
                      END
                  ), 0) 
              FROM gross_wages gw
              LEFT JOIN employee_salaries es ON es.emp_id = dj.user_id
              WHERE gw.ho_id = ${ho_id} 
              AND gw.employee_id = dj.user_id
              AND gw.status = 1
              AND gw.type = 3
              AND gw.effective_date = "${start_date}"
          ) AS other_expenses,` : ""
            }

            query = `WITH emp_wise_config AS (
                SELECT
                    employee_id,
                    ho_id,
                    MAX(CASE WHEN key_name = 'full_day_hrs' THEN key_value END) AS full_day_hrs,
                    MAX(CASE WHEN key_name = 'half_day_hrs' THEN key_value END) AS half_day_hrs,
                    MAX(CASE WHEN key_name = 'late_grace_period' THEN key_value END) AS late_grace_period,
                    MAX(CASE WHEN key_name = 'shift_module_mode' THEN key_value END) AS shift_module_mode,
                    MAX(CASE WHEN key_name = 'shift_switch_decision_taken' THEN key_value END) AS shift_switch_decision_taken,
                    MAX(CASE WHEN key_name = 'leave_cycle' THEN key_value END) AS leave_cycle,
                    MAX(CASE WHEN key_name = 'payroll_default_settings_done' THEN key_value END) AS payroll_default_settings_done,
                    MAX(CASE WHEN key_name = 'off_d_settle_type' THEN key_value END) AS off_d_settle_type,
                    MAX(CASE WHEN key_name = 'off_d_extra_work_pay' THEN key_value END) AS off_d_extra_work_pay,
                    MAX(CASE WHEN key_name = 'daily_wage_calculate_as' THEN key_value END) AS daily_wage_calculate_as,
                    MAX(CASE WHEN key_name = 'daily_wage_no_of_days' THEN key_value END) AS daily_wage_no_of_days,
                    MAX(CASE WHEN key_name = 'overtime_buffer_period' THEN key_value END) AS overtime_buffer_period,
                    MAX(CASE WHEN key_name = 'overtime_hourly_pay' THEN key_value END) AS overtime_hourly_pay,
                    MAX(CASE WHEN key_name = 'overtime_method' THEN key_value END) AS overtime_method,
                    MAX(CASE WHEN key_name = 'is_late_coming_penalty' THEN key_value END) AS is_late_coming_penalty,
                    MAX(CASE WHEN key_name = 'late_penalty_grace_time' THEN key_value END) AS late_penalty_grace_time,
                    MAX(CASE WHEN key_name = 'late_penalty_allow_days' THEN key_value END) AS late_penalty_allow_days,
                    MAX(CASE WHEN key_name = 'late_penalty_method' THEN key_value END) AS late_penalty_method,
                    MAX(CASE WHEN key_name = 'late_penalty_per_day' THEN key_value END) AS late_penalty_per_day,
                    MAX(CASE WHEN key_name = 'late_penalty_multiplier_wage' THEN key_value END) AS late_penalty_multiplier_wage,
                    MAX(CASE WHEN key_name = 'late_penalty_per_minute' THEN key_value END) AS late_penalty_per_minute,
                    MAX(CASE WHEN key_name = 'full_day_penalty_enable' THEN key_value END) AS full_day_penalty_enable,
                    MAX(CASE WHEN key_name = 'full_day_penalty_time' THEN key_value END) AS full_day_penalty_time,
                    MAX(CASE WHEN key_name = 'half_day_penalty_enable' THEN key_value END) AS half_day_penalty_enable,
                    MAX(CASE WHEN key_name = 'half_day_penalty_time' THEN key_value END) AS half_day_penalty_time,
                    MAX(CASE WHEN key_name = 'overtime_duration' THEN key_value END) AS overtime_duration
                FROM employee_payroll_configurations
                WHERE ho_id = ? AND status = 1
                GROUP BY 1,2
                ),

                org_wise_config AS (
                SELECT
                    ho_id,
                    MAX(CASE WHEN key_name = 'full_day_hrs' THEN key_value END) AS full_day_hrs,
                    MAX(CASE WHEN key_name = 'half_day_hrs' THEN key_value END) AS half_day_hrs,
                    MAX(CASE WHEN key_name = 'late_grace_period' THEN key_value END) AS late_grace_period,
                    MAX(CASE WHEN key_name = 'shift_module_mode' THEN key_value END) AS shift_module_mode,
                    MAX(CASE WHEN key_name = 'shift_switch_decision_taken' THEN key_value END) AS shift_switch_decision_taken,
                    MAX(CASE WHEN key_name = 'leave_cycle' THEN key_value END) AS leave_cycle,
                    MAX(CASE WHEN key_name = 'payroll_default_settings_done' THEN key_value END) AS payroll_default_settings_done,
                    MAX(CASE WHEN key_name = 'off_d_settle_type' THEN key_value END) AS off_d_settle_type,
                    MAX(CASE WHEN key_name = 'off_d_extra_work_pay' THEN key_value END) AS off_d_extra_work_pay,
                    MAX(CASE WHEN key_name = 'daily_wage_calculate_as' THEN key_value END) AS daily_wage_calculate_as,
                    MAX(CASE WHEN key_name = 'daily_wage_no_of_days' THEN key_value END) AS daily_wage_no_of_days,
                    MAX(CASE WHEN key_name = 'overtime_buffer_period' THEN key_value END) AS overtime_buffer_period,
                    MAX(CASE WHEN key_name = 'overtime_hourly_pay' THEN key_value END) AS overtime_hourly_pay,
                    MAX(CASE WHEN key_name = 'overtime_method' THEN key_value END) AS overtime_method,
                    MAX(CASE WHEN key_name = 'is_late_coming_penalty' THEN key_value END) AS is_late_coming_penalty,
                    MAX(CASE WHEN key_name = 'late_penalty_grace_time' THEN key_value END) AS late_penalty_grace_time,
                    MAX(CASE WHEN key_name = 'late_penalty_allow_days' THEN key_value END) AS late_penalty_allow_days,
                    MAX(CASE WHEN key_name = 'late_penalty_method' THEN key_value END) AS late_penalty_method,
                    MAX(CASE WHEN key_name = 'late_penalty_per_day' THEN key_value END) AS late_penalty_per_day,
                    MAX(CASE WHEN key_name = 'late_penalty_multiplier_wage' THEN key_value END) AS late_penalty_multiplier_wage,
                    MAX(CASE WHEN key_name = 'late_penalty_per_minute' THEN key_value END) AS late_penalty_per_minute,
                    MAX(CASE WHEN key_name = 'full_day_penalty_enable' THEN key_value END) AS full_day_penalty_enable,
                    MAX(CASE WHEN key_name = 'full_day_penalty_time' THEN key_value END) AS full_day_penalty_time,
                    MAX(CASE WHEN key_name = 'half_day_penalty_enable' THEN key_value END) AS half_day_penalty_enable,
                    MAX(CASE WHEN key_name = 'half_day_penalty_time' THEN key_value END) AS half_day_penalty_time,
                    MAX(CASE WHEN key_name = 'overtime_duration' THEN key_value END) AS overtime_duration
                FROM organization_configurations
                WHERE ho_id = ? AND status = 1
                ),

                detail_journal AS (
                SELECT
                    a.ho_id,
                    a.attandance_date,
                    DATE_FORMAT(a.attandance_date, "%d") AS day,
                    DATE_FORMAT(a.attandance_date, "%M") AS month,
                    a.user_id,
                    CASE 
                    WHEN min_flag IN (5, 6, 7) AND max_flag = 9 THEN 9
                    WHEN min_flag = 0 AND max_flag = 9 THEN 0
                    ELSE min_flag
                    END AS flag,
                    a.tag,
                    a.total_days_in_month,
                    a.selected_days,
                    NULL AS error_state,
                    CASE 
                    WHEN (CASE 
                            WHEN min_flag IN (5, 6, 7) AND max_flag = 9 THEN 9
                            WHEN min_flag = 0 AND max_flag = 9 THEN 0
                            ELSE min_flag
                            END) = 0 THEN 
                        CASE 
                        WHEN tag = 5 AND overwrite >= full_day_hrs THEN 1 
                        WHEN tag = 5 AND overwrite < full_day_hrs AND overwrite >= half_day_hrs THEN 0
                        WHEN tag = 5 AND overwrite < half_day_hrs THEN 0
                        WHEN tag = 2 THEN 1
                        WHEN tag = 3 THEN 0
                        WHEN tag = 4 THEN 0
                        WHEN working_hrs >= full_day_hrs THEN 1 
                        WHEN working_hrs < full_day_hrs AND working_hrs >= half_day_hrs THEN 0
                        WHEN working_hrs < half_day_hrs THEN 0
                        ELSE 0 
                        END 
                    ELSE 0 
                    END AS full_day,
                    CASE 
                    WHEN (CASE 
                            WHEN min_flag IN (5, 6, 7) AND max_flag = 9 THEN 9
                            WHEN min_flag = 0 AND max_flag = 9 THEN 0
                            WHEN min_flag = 0 AND max_flag = 10 THEN 0
                            ELSE min_flag
                            END) NOT IN (5,9,10) THEN 
                        CASE 
                        WHEN tag = 5 AND overwrite >= full_day_hrs THEN 0 
                        WHEN tag = 5 AND overwrite < full_day_hrs AND overwrite >= half_day_hrs THEN 1
                        WHEN tag = 5 AND overwrite < half_day_hrs THEN 0
                        WHEN tag = 2 THEN 0
                        WHEN tag = 3 THEN 1
                        WHEN tag = 4 THEN 0
                        WHEN working_hrs >= full_day_hrs THEN 0 
                        WHEN working_hrs < full_day_hrs AND working_hrs >= half_day_hrs THEN 1
                        WHEN working_hrs < half_day_hrs THEN 0
                        ELSE 0 
                        END 
                    ELSE 0 
                    END AS half_day,
                    CASE 
                    WHEN leave_id = 0 THEN
                        CASE 
                        WHEN tag = 4 THEN 1
                        WHEN (CASE 
                                WHEN min_flag IN (5, 6, 7) AND max_flag = 9 THEN 9
                                WHEN min_flag = 0 AND max_flag = 9 THEN 0
                                ELSE min_flag
                                END) = 0 
                            AND tag = 5 AND overwrite < half_day_hrs and payroll_tag not in (2,3) THEN 1
                        WHEN (CASE 
                                WHEN min_flag IN (5, 6, 7) AND max_flag = 9 THEN 9
                                WHEN min_flag = 0 AND max_flag = 9 THEN 0
                                ELSE min_flag
                                END) = 0 
                            AND tag = 5 AND overwrite < full_day_hrs AND overwrite >= half_day_hrs and payroll_tag not in (2,3) THEN 0.5
                        WHEN (CASE 
                                WHEN min_flag IN (5, 6, 7) AND max_flag = 9 THEN 9
                                WHEN min_flag = 0 AND max_flag = 9 THEN 0
                                ELSE min_flag
                                END) = 0 
                            AND tag = 3 and payroll_tag not in (2,3) THEN 0.5
                        WHEN (CASE 
                                WHEN min_flag IN (5, 6, 7) AND max_flag = 9 THEN 9
                                WHEN min_flag = 0 AND max_flag = 9 THEN 0
                                ELSE min_flag
                                END) = 0 
                            AND tag = 1 AND working_hrs < half_day_hrs and payroll_tag not in (2,3) THEN 1
                        WHEN (CASE 
                                WHEN min_flag IN (5, 6, 7) AND max_flag = 9 THEN 9
                                WHEN min_flag = 0 AND max_flag = 9 THEN 0
                                ELSE min_flag
                                END) = 0 
                            AND tag = 1 AND working_hrs < full_day_hrs AND working_hrs >= half_day_hrs and payroll_tag not in (2,3) THEN 0.5
                        WHEN (CASE 
                                WHEN min_flag IN (5, 6, 7) AND max_flag = 9 THEN 9
                                WHEN min_flag = 0 AND max_flag = 9 THEN 0
                                ELSE min_flag
                                END) IN (5,6,7) 
                            AND tag = 3 and payroll_tag not in (2,3) THEN 0.5
                        WHEN (CASE 
                                WHEN min_flag IN (5, 6, 7) AND max_flag = 9 THEN 9
                                WHEN min_flag = 0 AND max_flag = 9 THEN 0
                                ELSE min_flag
                                END) IN (5) 
                            AND tag = 5 AND overwrite < half_day_hrs and payroll_tag not in (2,3) THEN 1
                        WHEN (CASE 
                                WHEN min_flag IN (5, 6, 7) AND max_flag = 9 THEN 9
                                WHEN min_flag = 0 AND max_flag = 9 THEN 0
                                ELSE min_flag
                                END) IN (6,7) 
                            AND tag = 5 AND overwrite < half_day_hrs and payroll_tag not in (2,3) THEN 0.5
                        WHEN (CASE 
                                WHEN min_flag IN (5, 6, 7) AND max_flag = 9 THEN 9
                                WHEN min_flag = 0 AND max_flag = 9 THEN 0
                                ELSE min_flag
                                END) IN (5) 
                            AND tag = 1 AND working_hrs < half_day_hrs and payroll_tag not in (2,3) THEN 1
                        WHEN (CASE 
                                WHEN min_flag IN (5, 6, 7) AND max_flag = 9 THEN 9
                                WHEN min_flag = 0 AND max_flag = 9 THEN 0
                                ELSE min_flag
                                END) IN (6,7) 
                            AND tag = 1 AND working_hrs < half_day_hrs and payroll_tag not in (2,3) THEN 0.5
                        ELSE 0
                        END
                    ELSE 0
                    END AS unpaid_days,
                    CASE 
                        WHEN leave_id > 0 AND (CASE 
                                                WHEN min_flag IN (5, 6, 7) AND max_flag = 9 THEN 9
                                                WHEN min_flag = 0 AND max_flag = 9 THEN 0
                                                ELSE min_flag
                                                END) IN (5,6,7) THEN
                            CASE 
                            WHEN (CASE 
                                    WHEN min_flag IN (5, 6, 7) AND max_flag = 9 THEN 9
                                    WHEN min_flag = 0 AND max_flag = 9 THEN 0
                                    ELSE min_flag
                                    END) = 5 or payroll_tag = 2 THEN 1
                            WHEN (CASE 
                                    WHEN min_flag IN (5, 6, 7) AND max_flag = 9 THEN 9
                                    WHEN min_flag = 0 AND max_flag = 9 THEN 0
                                    ELSE min_flag
                                    END) IN (6,7) THEN 0.5
                            ELSE 0
                            END
                        WHEN leave_id = 0 AND (CASE 
                                                WHEN min_flag IN (5, 6, 7) AND max_flag = 9 THEN 9
                                                WHEN min_flag = 0 AND max_flag = 9 THEN 0
                                                ELSE min_flag
                                                END) IN (5,6,7) THEN
                            CASE 
                            WHEN (CASE 
                                    WHEN min_flag IN (5, 6, 7) AND max_flag = 9 THEN 9
                                    WHEN min_flag = 0 AND max_flag = 9 THEN 0
                                    ELSE min_flag
                                    END) = 5 and payroll_tag = 2 THEN 1
                            WHEN (CASE 
                                    WHEN min_flag IN (5, 6, 7) AND max_flag = 9 THEN 9
                                    WHEN min_flag = 0 AND max_flag = 9 THEN 0
                                    ELSE min_flag
                                    END) IN (6,7) and payroll_tag = 3 THEN 0.5
                            ELSE 0
                            END
                        ELSE 0
                    END AS on_leave,
                    CASE
                    WHEN (CASE 
                            WHEN min_flag IN (5, 6, 7) AND max_flag = 10 THEN 10
                            WHEN min_flag = 0 AND max_flag = 9 THEN 0
                            ELSE min_flag
                            END) in (9,10) THEN 1
                    ELSE 0
                    END AS off_days,
                    CASE
                    WHEN (CASE 
                            WHEN min_flag IN (5, 6, 7) AND max_flag = 10 THEN 10
                            WHEN min_flag = 0 AND max_flag = 9 THEN 0
                            ELSE min_flag
                            END) in (9,10) THEN 
                        CASE 
                        WHEN (payroll_tag = 4 OR payroll_tag = 5) AND TIME_TO_SEC(working_hrs) >= TIME_TO_SEC(full_day_hrs) then 1
                        WHEN (payroll_tag = 4 OR payroll_tag = 5) AND TIME_TO_SEC(working_hrs) < TIME_TO_SEC(full_day_hrs) AND TIME_TO_SEC(working_hrs) >= TIME_TO_SEC(half_day_hrs) then 0.5
                        ELSE 0
                        END
                    ELSE 0
                    END AS off_days_paid,
                    (case when daily_wage_calculate_as = 2 then daily_wage_no_of_days else total_days_in_month end) as daily_wage_days,
                    CASE
                    WHEN TIME_TO_SEC(working_hrs) > TIME_TO_SEC(full_day_hrs) + TIME_TO_SEC(overtime_buffer_period) and is_approved_ot = 1
                    THEN FLOOR((TIME_TO_SEC(working_hrs) - (TIME_TO_SEC(full_day_hrs) + TIME_TO_SEC(overtime_buffer_period))) / TIME_TO_SEC(overtime_duration)) * TIME_TO_SEC(overtime_duration) / 60
                    ELSE 0
                END AS overtime_hours,
                CASE
                    WHEN (CASE 
                        WHEN min_flag IN (5, 6, 7) AND max_flag = 10 THEN 10
                        WHEN min_flag IN (5, 6, 7) AND max_flag = 9 THEN 9
                        WHEN min_flag = 0 AND max_flag = 9 THEN 0
                        ELSE min_flag
                        END) in (10) THEN 1 
                    ELSE 0
                END AS holiday,
                overtime_hourly_pay,
                overtime_method,
                round(MAX(salary) / MAX((case when daily_wage_calculate_as = 2 then daily_wage_no_of_days else total_days_in_month end)),2) AS salary_per_day,
                full_day_hrs,
                penalty_day,
                late_penalty_allow_days,
                late_penalty_method,
                late_penalty_per_day,
                late_penalty_multiplier_wage,
                late_penalty_per_minute,
                full_day_penalty_enable,
                full_day_penalty_time,
                half_day_penalty_enable,
                half_day_penalty_time,
                off_d_extra_work_pay,
                off_d_settle_type,
                salary,
                is_ignore_late,
                punch_min_datetime,
                shift_start_time,
                daily_wage_calculate_as,
                daily_wage_no_of_days,
                late_minutes_list_date_wise,
                TIME_TO_SEC(late_grace_period) / 60 AS late_grace_period_in_minutes
                FROM (
                    SELECT 
                    j.ho_id,
                    j.attandance_date,
                    j.user_id,
                    es.salary,
                    CASE WHEN emp_co.full_day_hrs IS NULL THEN o_co.full_day_hrs ELSE emp_co.full_day_hrs END AS full_day_hrs,
                    CASE WHEN emp_co.half_day_hrs IS NULL THEN o_co.half_day_hrs ELSE emp_co.half_day_hrs END AS half_day_hrs,
                    CASE WHEN emp_co.late_grace_period IS NULL THEN o_co.late_grace_period ELSE emp_co.late_grace_period END AS late_grace_period,
                    CASE WHEN emp_co.off_d_extra_work_pay IS NULL THEN o_co.off_d_extra_work_pay ELSE emp_co.off_d_extra_work_pay END AS off_d_extra_work_pay,
                    CASE WHEN emp_co.daily_wage_calculate_as IS NULL THEN o_co.daily_wage_calculate_as ELSE emp_co.daily_wage_calculate_as END AS daily_wage_calculate_as,
                    CASE WHEN emp_co.daily_wage_no_of_days IS NULL THEN o_co.daily_wage_no_of_days ELSE emp_co.daily_wage_no_of_days END AS daily_wage_no_of_days,
                    CASE WHEN emp_co.overtime_buffer_period IS NULL THEN o_co.overtime_buffer_period ELSE emp_co.overtime_buffer_period END AS overtime_buffer_period,
                    CASE WHEN emp_co.overtime_hourly_pay IS NULL THEN o_co.overtime_hourly_pay ELSE emp_co.overtime_hourly_pay END AS overtime_hourly_pay,
                    CASE WHEN emp_co.overtime_method IS NULL THEN o_co.overtime_method ELSE emp_co.overtime_method END AS overtime_method,
                    CASE WHEN emp_co.late_penalty_grace_time IS NULL THEN o_co.late_penalty_grace_time ELSE emp_co.late_penalty_grace_time END AS late_penalty_grace_time,
                    CASE WHEN emp_co.late_penalty_allow_days IS NULL THEN o_co.late_penalty_allow_days ELSE emp_co.late_penalty_allow_days END AS late_penalty_allow_days,
                    CASE WHEN emp_co.late_penalty_method IS NULL THEN o_co.late_penalty_method ELSE emp_co.late_penalty_method END AS late_penalty_method,
                    CASE WHEN emp_co.late_penalty_per_day IS NULL THEN o_co.late_penalty_per_day ELSE emp_co.late_penalty_per_day END AS late_penalty_per_day,
                    CASE WHEN emp_co.late_penalty_multiplier_wage IS NULL THEN o_co.late_penalty_multiplier_wage ELSE emp_co.late_penalty_multiplier_wage END AS late_penalty_multiplier_wage,
                    CASE WHEN emp_co.late_penalty_per_minute IS NULL THEN o_co.late_penalty_per_minute ELSE emp_co.late_penalty_per_minute END AS late_penalty_per_minute,
                    CASE WHEN emp_co.full_day_penalty_enable IS NULL THEN o_co.full_day_penalty_enable ELSE emp_co.full_day_penalty_enable END AS full_day_penalty_enable,
                    CASE WHEN emp_co.full_day_penalty_time IS NULL THEN o_co.full_day_penalty_time ELSE emp_co.full_day_penalty_time END AS full_day_penalty_time,
                    CASE WHEN emp_co.half_day_penalty_enable IS NULL THEN o_co.half_day_penalty_enable ELSE emp_co.half_day_penalty_enable END AS half_day_penalty_enable,
                    CASE WHEN emp_co.half_day_penalty_time IS NULL THEN o_co.half_day_penalty_time ELSE emp_co.half_day_penalty_time END AS half_day_penalty_time,
                    CASE WHEN emp_co.off_d_settle_type IS NULL THEN o_co.off_d_settle_type ELSE emp_co.off_d_settle_type END AS off_d_settle_type,
                    CASE WHEN emp_co.overtime_duration IS NULL THEN o_co.overtime_duration ELSE emp_co.overtime_duration END AS overtime_duration,
                    MIN(flag) AS min_flag,
                    MAX(flag) AS max_flag,
                    MAX(tag) AS tag,
                    (CASE WHEN flag = 5 THEN 'Leave' WHEN flag = 6 THEN 'First Half Leave' WHEN flag = 7 THEN 'Second Half Leave' WHEN flag = 9 THEN 'Week Off' END) AS flag_status,
                    (CASE WHEN tag = 1 THEN 'Normal' WHEN tag = 2 THEN 'Full Day' WHEN tag = 3 THEN 'Half Day' WHEN tag = 4 THEN 'Absent' WHEN tag = 5 THEN 'Overwrite' END) AS tag_status,
                    MAX(CASE WHEN tag = 5 THEN overwrite ELSE working_hrs END) AS working_hrs,
                    MAX(break_hrs) AS break_hrs,
                    SUM(p_cnt) AS p_cnt,
                    MAX(is_approved_ot) AS is_approved_ot,
                    MAX(payroll_tag) as payroll_tag, 
                    j.overwrite,
                    j.is_ignore_late,
                    MAX(j.leave_id) AS leave_id,
                    MIN(j.punch_min_datetime) AS punch_min_datetime,
                    MAX(j.punch_max_datetime) AS punch_max_datetime,
                    MIN(from_time) AS shift_start_time,
                    MAX(to_time) AS shift_end_time,
                    DAY(LAST_DAY(?)) AS total_days_in_month,
                    DATEDIFF(?, ?) + 1 AS selected_days,
                    SUM(CASE 
                            WHEN j.punch_min_datetime > ((from_time) + INTERVAL TIME_TO_SEC(CASE WHEN emp_co.late_grace_period IS NULL THEN o_co.late_grace_period ELSE emp_co.late_grace_period END) SECOND) AND j.is_ignore_late = 0 
                            THEN 1 
                        ELSE 0 
                    END) AS penalty_day,
                    GROUP_CONCAT(
                        CASE 
                            WHEN
                                j.punch_min_datetime > DATE_ADD(j.from_time, INTERVAL TIME_TO_SEC(CASE WHEN emp_co.late_grace_period IS NULL THEN o_co.late_grace_period ELSE emp_co.late_grace_period END) / 60 MINUTE)
                                AND j.is_ignore_late = 0
                                AND j.flag NOT IN (5,6,7,9)
                                AND j.payroll_tag NOT IN (2,3)
                                AND j.tag = 1
                            THEN TIMESTAMPDIFF(
                                MINUTE,
                                DATE_ADD(j.from_time, INTERVAL TIME_TO_SEC(CASE WHEN emp_co.late_grace_period IS NULL THEN o_co.late_grace_period ELSE emp_co.late_grace_period END) / 60 MINUTE),
                                j.punch_min_datetime
                            )
                            ELSE null
                        END
                    ) AS late_minutes_list_date_wise
                    FROM journal_entries j 
                    JOIN employee_salaries as es ON es.ho_id = j.ho_id AND es.emp_id = j.user_id
                    LEFT JOIN emp_wise_config emp_co ON j.ho_id = emp_co.ho_id AND j.user_id = emp_co.employee_id 
                    JOIN org_wise_config o_co ON j.ho_id = o_co.ho_id  
                    WHERE j.ho_id = ?
                    AND j.attandance_date >= ? 
                    AND j.attandance_date <= ? 
                    AND tag > 0 
                    GROUP BY 1, 2, 3
                ) a
                LEFT JOIN leaves l ON l.id = a.leave_id AND l.ho_id = ?
                GROUP BY 1, 2, 5
                ),

                extra_col as
                (
                SELECT
                    ho_id,
                    employee_id, 
                    sum(case when adjustment_type = 1 and  type = 1 then amount else 0 end) as extra_amount,
                    sum(case when adjustment_type = 3 and  type = 2 then amount else 0 end) as panelty_extra
                FROM payroll_extras
                WHERE 
                    ho_id = ? 
                    and status = 1 and type IN (1,2)
                    and adjustment_type in (1,3)
                    and adjustment_date >= ?
                    and adjustment_date <= ?
                GROUP BY ho_id,employee_id
                ),

                ${additionalQuery}

                SELECT 
                dj.ho_id,
                dj.user_id,
                emp.name,
                emp.code,
                emp.device_employee_id,
                es.bank_details,
                d.name as department,
                emp.status as employee_status,
                emp.tag_updated_at,
                des.name as designation,
                es.bank_details,
                GROUP_CONCAT(dj.late_minutes_list_date_wise) as lat,
                org_names.org_names,
                es.salary as basic_salary,
                extra.extra_amount,
                SUM(dj.full_day) AS full_day,
                SUM(dj.half_day) AS half_day,
                SUM(dj.unpaid_days) AS unpaid_days,
                SUM(dj.on_leave) AS on_leave,
                SUM(dj.off_days) AS off_days,
                SUM(dj.off_days_paid) AS off_days_paid,
                SUM(dj.full_day + (dj.half_day / 2) + dj.on_leave + dj.off_days) AS paid_days,
                MAX(dj.total_days_in_month) AS total_days_in_month,
                MAX(dj.selected_days) AS selected_days,
                MAX(case when dj.full_day = 1 then dj.error_state end) as error_state,
                round(MAX(es.salary) / MAX(dj.daily_wage_days),2) AS salary_per_day,
                ${additionalSelectParams}
                MAX(dj.daily_wage_days) as daily_wage_days,
                SUM(penalty_day) as total_penalty_day,
                MAX(dj.late_penalty_allow_days) as late_penalty_allow_days, 
                ${grossWagesQuery}
                SUM(dj.holiday) as holiday
                FROM detail_journal dj
                JOIN employee_salaries es ON es.ho_id = dj.ho_id
                AND es.emp_id = dj.user_id 
                AND es.salary_type = 1
                JOIN employees emp ON emp.id = dj.user_id and emp.ho_id = ? ${department_condition}
                LEFT JOIN departments d on emp.department_id = d.id and d.ho_id = ?
                LEFT JOIN designations des on des.id = emp.designation_id and des.ho_id = ?
                ${additionJoin}
                JOIN (
                    SELECT eb.employee_id, GROUP_CONCAT(DISTINCT org.name ORDER BY org.name ASC SEPARATOR ', ') AS org_names
                    FROM employee_branches eb
                    LEFT JOIN organizations org ON org.id = eb.branch_id
                    WHERE
                    eb.ho_id = ? and eb.status = 1 ${user_organizations_condition}
                    GROUP BY eb.employee_id
                ) org_names ON org_names.employee_id = emp.id
                LEFT JOIN extra_col extra on extra.ho_id = dj.ho_id and dj.user_id = extra.employee_id
                ${additionLoanAdvJoin}
                GROUP BY dj.ho_id, dj.user_id, emp.name, emp.code;`;
            parameters = [
                ho_id, ho_id, start_date, end_date, start_date, ho_id, start_date, end_date, ho_id, ho_id, start_date, end_date, ho_id, ho_id, ho_id,  ho_id
            ];

            const connection = await db2.getConnection();      

            try {
                // console.log('query >>',connection.format(query, parameters))
                const [row] = await connection.query(query, parameters);
                return row;
            } catch (err) {
                console.log('err',err)
                throw err;
            } finally {
                await connection.destroy();
            }
        }

    }

    static async getWorkDurationReports(ho_id, branch_id, departmentId, desingationId, user_organizations, start_date, end_date, user_type, department_ids) {

        var query = '';
        var parameters = [];
        var user_organizations_condition = '';
        var whereCondition = '';
        var department_condition = '';
        if (user_type !== 1 && department_ids !== null) {
            if (department_ids) {
              department_condition = ` AND emp.department_id IN (${department_ids})`;
            } else {
              department_condition = ` AND emp.department_id IS NULL`;
            }
        }

        parameters = [ho_id, ho_id, ho_id, start_date, end_date, ho_id, ho_id, ho_id];

        if(user_organizations !== '') {
            user_organizations_condition = `AND eb.branch_id in (${user_organizations})`;
        }

        if(departmentId !== 0 || desingationId !== 0) {
            whereCondition = " WHERE ";

            if(desingationId !== 0 && departmentId === 0) {
                whereCondition += " emp.designation_id = ? ";
                parameters.push(desingationId);
            }
            if(departmentId !== 0 && desingationId === 0) {
                whereCondition += " emp.department_id = ? ";
                parameters.push(departmentId);
            }
            if(departmentId !== 0 && desingationId !== 0) {
                whereCondition += " emp.department_id = ? AND emp.designation_id = ? ";
                parameters.push(departmentId, desingationId);
            }
        }

        query = `
            WITH emp_wise_config AS (
                SELECT
                    employee_id,
                    ho_id,
                    MAX(CASE WHEN key_name = 'full_day_hrs' THEN key_value END) AS full_day_hrs,
                    MAX(CASE WHEN key_name = 'half_day_hrs' THEN key_value END) AS half_day_hrs,
                    MAX(CASE WHEN key_name = 'overtime_buffer_period' THEN key_value END) AS overtime_buffer_period
                FROM employee_payroll_configurations
                WHERE ho_id = ?
                GROUP BY 1,2
            ),

            org_wise_config AS (
                SELECT
                    ho_id,
                    MAX(CASE WHEN key_name = 'full_day_hrs' THEN key_value END) AS full_day_hrs,
                    MAX(CASE WHEN key_name = 'half_day_hrs' THEN key_value END) AS half_day_hrs,
                    MAX(CASE WHEN key_name = 'overtime_buffer_period' THEN key_value END) AS overtime_buffer_period
                FROM organization_configurations
                WHERE ho_id = ?
            )

            SELECT 
                h.user_id as employee_id,
                emp.code,
                emp.device_employee_id,
                emp.name,
                h.attandance_date,
                emp.status as employee_status,
                emp.tag_updated_at,
                emp.status as employee_status,
                emp.tag_updated_at,
                DAY(h.attandance_date) as DAY,
                h.tag,
                des.name as designation,
                d.name as department,
                DATE_FORMAT(h.first_punch, '%I:%i %p') as PunchIn,
                CASE WHEN h.first_punch = h.last_punch THEN null ELSE DATE_FORMAT(h.last_punch, '%I:%i %p') END as PunchOut,
                CASE 
                    WHEN h.first_punch > h.from_time THEN 
                        TIME_TO_SEC(TIMEDIFF(h.first_punch, h.from_time)) 
                    ELSE NULL 
                END AS LateIn,
                CASE 
                    WHEN h.last_punch < h.to_time THEN 
                        TIME_TO_SEC(TIMEDIFF(h.to_time, h.last_punch)) 
                    ELSE NULL 
                END AS EarlyOut,
                (case 
                    when flag = 'Week Off' then flag
                    when tag = 'Full Day' or tag = 'Half Day' or tag = 'Absent' then tag else 
                        (case 
                            when tag = 'overwrite' and h.overwrite >= h.full_day_hrs then 'Full Day' 
                            when tag = 'overwrite' and h.overwrite >= h.half_day_hrs then 'Half Day' 
                        else 
                            (case 
                                when h.working_hrs >= h.full_day_hrs then 'Full Day' 
                                when h.working_hrs >= h.half_day_hrs then 'Half Day' 
                                when flag IS NOT NULL then flag
                            else 'Absent' end) 
                        end) 
                end) status,
                CONCAT(
                    IF(FLOOR(TIME_TO_SEC(h.working_hrs) / 3600) > 0, CONCAT(FLOOR(TIME_TO_SEC(h.working_hrs) / 3600), 'h '), ''),
                    IF(MOD(FLOOR(TIME_TO_SEC(h.working_hrs) / 60), 60) > 0, CONCAT(MOD(FLOOR(TIME_TO_SEC(h.working_hrs) / 60), 60), 'm'), '')
                ) AS working_hrs,
                CONCAT(
                    IF(FLOOR(TIME_TO_SEC(h.break_hrs) / 3600) > 0, CONCAT(FLOOR(TIME_TO_SEC(h.break_hrs) / 3600), 'h '), ''),
                    IF(MOD(FLOOR(TIME_TO_SEC(h.break_hrs) / 60), 60) > 0, CONCAT(MOD(FLOOR(TIME_TO_SEC(h.break_hrs) / 60), 60), 'm'), '')
                ) AS break_hrs,
                CASE WHEN MOD(h.p_cnt, 2) <> 0 THEN 'Problem' END AS error_state,
                CASE 
                    WHEN TIME_TO_SEC(h.working_hrs) > (TIME_TO_SEC(h.full_day_hrs) + TIME_TO_SEC(h.overtime_buffer_period)) 
                        AND (TIME_TO_SEC(h.working_hrs) - (TIME_TO_SEC(h.full_day_hrs) + TIME_TO_SEC(h.overtime_buffer_period))) > 3600 
                    THEN 
                        TIME_TO_SEC(h.working_hrs) - (TIME_TO_SEC(h.full_day_hrs) + TIME_TO_SEC(h.overtime_buffer_period)) 
                    ELSE NULL 
                END AS overtime
            FROM
            (SELECT 
                user_id,
                attandance_date,
                CASE WHEN emp_co.full_day_hrs IS NULL THEN o_co.full_day_hrs ELSE emp_co.full_day_hrs END AS full_day_hrs,
                CASE WHEN emp_co.half_day_hrs IS NULL THEN o_co.half_day_hrs ELSE emp_co.half_day_hrs END AS half_day_hrs,
                CASE WHEN emp_co.overtime_buffer_period IS NULL THEN o_co.overtime_buffer_period ELSE emp_co.overtime_buffer_period END AS overtime_buffer_period,
                MAX(CASE WHEN tag = 1 THEN 'Normal' WHEN tag = 2 THEN 'Full Day' WHEN tag = 3 THEN 'Half Day' WHEN tag = 4 THEN 'Absent' WHEN tag = 5 THEN 'Overwrite' END) AS tag,
                (CASE WHEN flag = 5 THEN 'Leave' WHEN flag = 6 then 'Absent' WHEN flag = 7 then 'Absent' WHEN flag = 9 THEN 'Week Off' END) AS flag,
                MIN(punch_min_datetime) AS first_punch,
                MAX(punch_max_datetime) AS last_punch,
                MIN(from_time) AS from_time,
                MAX(to_time) AS to_time,
                MAX(CASE WHEN tag = 5 THEN overwrite WHEN tag = 2 THEN (CASE WHEN emp_co.full_day_hrs IS NULL THEN o_co.full_day_hrs ELSE emp_co.full_day_hrs END) WHEN tag = 3 THEN (CASE WHEN emp_co.half_day_hrs IS NULL THEN o_co.half_day_hrs ELSE emp_co.half_day_hrs END) WHEN tag = 4 THEN 0 ELSE working_hrs END) AS working_hrs,    
                MAX(break_hrs) AS break_hrs,
                SUM(p_cnt) AS p_cnt,
                MAX(j.overwrite) AS overwrite 
            FROM journal_entries j 
            LEFT JOIN emp_wise_config emp_co ON j.ho_id = emp_co.ho_id AND j.user_id = emp_co.employee_id 
            JOIN org_wise_config o_co ON j.ho_id = o_co.ho_id  
            WHERE
                j.ho_id = ? AND j.attandance_date >= ? AND j.attandance_date <= ? 
                AND j.tag > 0 
            GROUP BY 1, 2) h 
            JOIN employees emp ON emp.id = h.user_id AND emp.status > 0 AND emp.ho_id = ? ${department_condition}
            LEFT JOIN departments d ON emp.department_id = d.id AND d.ho_id = ?
            LEFT JOIN designations des ON des.id = emp.designation_id AND des.ho_id = ?
            JOIN employee_branches eb ON eb.employee_id = h.user_id AND eb.status = 1 ${user_organizations_condition}
            ${whereCondition}
            GROUP BY 1, 5
        `;

        try {
            const [row] = await db2.query(query, parameters);
            return row;
        } catch (err) {
            throw err;
        }

    }

    static async isPayrollLockedQuery(ho_id, from_date, to_date, salary_type = 1) {

        let dateRangeCond = `AND (
                (? BETWEEN date_from AND date_to OR ? BETWEEN date_from AND date_to) 
                OR 
                (date_from BETWEEN ? AND ? OR date_to BETWEEN ? AND ?)
            )`;

        let queryParams = [ho_id, from_date, to_date, from_date, to_date, from_date, to_date, salary_type];

        if( from_date === to_date ) {
            dateRangeCond = `AND (
                ? BETWEEN date_from AND date_to
                OR 
                (date_from <= ? AND date_to >= ?)
              )`;

            queryParams =  [ho_id, from_date, from_date, from_date, salary_type]
        }

        const query =  `
            SELECT is_locked, id, DATE_FORMAT(created, '%Y-%m-%d') as created
            FROM payrolls
            WHERE ho_id = ?
                ${dateRangeCond}
                AND is_locked = 1
                AND salary_type = ?
            `;
        const connection = await db2.getConnection();    

        const [row] = await connection.query(query, queryParams); 

        return row;
    }

