        let getPayrollLockRows = await this.isPayrollLockedQuery(ho_id, start_date, end_date, 2);

        let payrollId = getPayrollLockRows.length > 0 ? getPayrollLockRows[0]?.id : 0

        let query = '';
        let parameters = [];


        if(payrollId > 0){
            //freeze config
            // console.log('in freeze query');

            query = `
            SELECT 
                pd.ho_id,
                pd.employee_id as user_id,
                pd.bank_details,
                emp.code,
                emp.name,
                emp.device_employee_id,
                emp.status as employee_status,
                emp.tag_updated_at,
                es.bank_details,
                pd.department_name as department,
                pd.designation_name as designation,
                pd.org_names,
                pd.total_working_hrs_minutes,
                pd.over_time_minutes,
                COALESCE(SUM(pd.total_working_hrs_minutes), 0) - COALESCE(SUM(pd.over_time_minutes), 0) AS standard_working_hours,
                pd.ot_amt as overtime_pay,
                pd.salary as hourly_rate,
                pd.daily_wage as gross_wage,
                pd.extras_amt as extra_amount,
                pd.penalties_amt as penalty_extra,
                pd.loan_adv_amt as advance_amount,
                0 as loan_amount,
                pd.selected_days,
                pd.total_days_in_month,
                ${payrollId} as payroll_id
            from payroll_details pd
            JOIN employees emp ON emp.id = pd.employee_id and emp.ho_id = ?
            LEFT JOIN employee_salaries es ON es.emp_id = pd.employee_id and es.ho_id = ?
            where
                pd.ho_id = ? AND pd.payroll_id = ? AND pd.status = 1 AND pd.salary_type = 2
            GROUP BY pd.ho_id, pd.user_id, emp.name, emp.code
            `;

            parameters = [ho_id, ho_id, ho_id, payrollId];

            const connection = await db2.getConnection();      
            try {
                const [row] = await connection.query(query, parameters);
                return row;
            } catch (err) {
                console.log('err',err)
                throw err;
            } finally {
                await connection.destroy();
            }            

        }else{

            var user_organizations_condition = '';

            if(user_organizations !== '') {
                user_organizations_condition = `and eb.branch_id in (${user_organizations})`;
            }

            var department_condition = ""
            if (user_type !== 1 && department_ids !== null) {
                if (department_ids) {
                  department_condition = ` AND e.department_id IN (${department_ids})`;
                } else {
                  department_condition = ` AND e.department_id IS NULL`;
                }
            }

            query = `
                WITH emp_wise_config AS (
                    SELECT 
                        employee_id,
                        ho_id,
                        MAX(CASE WHEN key_name = 'hourly_overtime_buffer_period' THEN key_value END) AS hourly_overtime_buffer_period,
                        MAX(CASE WHEN key_name = 'hourly_overtime_hourly_pay' THEN key_value END) AS hourly_overtime_hourly_pay,
                        MAX(CASE WHEN key_name = 'hourly_overtime_working_hour' THEN key_value END) AS hourly_overtime_working_hour
                    FROM employee_payroll_configurations
                    WHERE ho_id = ${ho_id} AND status = 1
                    GROUP BY 1, 2
                ),

                org_wise_config AS (
                    SELECT 
                        ho_id,
                        MAX(CASE WHEN key_name = 'hourly_overtime_buffer_period' THEN key_value END) AS hourly_overtime_buffer_period,
                        MAX(CASE WHEN key_name = 'hourly_overtime_hourly_pay' THEN key_value END) AS hourly_overtime_hourly_pay,
                        MAX(CASE WHEN key_name = 'hourly_overtime_working_hour' THEN key_value END) AS hourly_overtime_working_hour
                    FROM organization_configurations
                    WHERE ho_id = ${ho_id} AND status = 1
                    GROUP BY 1
                ),

                salary_data as (
                    SELECT
                        b.ho_id,
                        b.user_id,
                        b.bank_details,
                        emp.code,
                        emp.name,
                        emp.device_employee_id,
                        tag,
                        SUM(working_hrs) as total_working_hrs_minutes,
                        SUM(hourly_overtime_working_hour) as hourly_overtime_working_minutes,
                        SUM(over_time_minutes) as over_time_minutes,
                        COALESCE(SUM(working_hrs), 0) - COALESCE(SUM(over_time_minutes), 0) AS standard_working_hours,
                        SUM(overtime_pay) as overtime_pay,
                        SUM(hourly_pay) as hourly_pay,
                        MAX(salary) as hourly_rate,
                        MAX(hourly_overtime_working_hour) as hourly_overtime_working_hour,
                        MAX(hourly_overtime_buffer_period) as hourly_overtime_buffer_period,
                        MAX(hourly_overtime_hourly_pay) as hourly_overtime_hourly_pay,
                        SUM(eligible_for_overtime) as eligible_for_overtime
                    FROM (
                        SELECT
                            ho_id,
                            attandance_date,
                            DATE_FORMAT(attandance_date, "%d") AS day,
                            DATE_FORMAT(attandance_date, "%M") AS month,
                            user_id,
                            tag,
                            FLOOR(TIME_TO_SEC(working_hrs) / 60) as working_hrs,
                            FLOOR(TIME_TO_SEC(hourly_overtime_working_hour) / 60) as hourly_overtime_working_hour,
                            FLOOR(TIME_TO_SEC(hourly_overtime_buffer_period) / 60) as hourly_overtime_buffer_period,
                            hourly_overtime_hourly_pay,
                            CASE 
                                WHEN TIME_TO_SEC(working_hrs) / 60 > TIME_TO_SEC(hourly_overtime_working_hour) / 60
                                    AND COALESCE(is_approved_ot, 0) != 1
                                THEN 1
                                ELSE 0
                            END AS eligible_for_overtime,
                            CASE 
                                WHEN FLOOR(TIME_TO_SEC(working_hrs) / 60) > FLOOR(TIME_TO_SEC(hourly_overtime_working_hour) / 60) 
                                then (
                                        FLOOR(
                                            (
                                                FLOOR(TIME_TO_SEC(working_hrs) / 60) 
                                                - FLOOR(TIME_TO_SEC(hourly_overtime_working_hour) / 60)
                                            ) 
                                            / FLOOR(TIME_TO_SEC(hourly_overtime_buffer_period) / 60)
                                        ) 
                                        * FLOOR(TIME_TO_SEC(hourly_overtime_buffer_period) / 60)
                                    ) 
                                else 0 
                            end as over_time_minutes,
                            CASE 
                                WHEN FLOOR(TIME_TO_SEC(working_hrs) / 60) > FLOOR(TIME_TO_SEC(hourly_overtime_working_hour) / 60) and is_approved_ot = 1
                                then (
                                        FLOOR(
                                            (
                                                FLOOR(TIME_TO_SEC(working_hrs) / 60) 
                                                - FLOOR(TIME_TO_SEC(hourly_overtime_working_hour) / 60)
                                            ) 
                                            / FLOOR(TIME_TO_SEC(hourly_overtime_buffer_period) / 60)
                                        ) 
                                        * FLOOR(TIME_TO_SEC(hourly_overtime_buffer_period) / 60)
                                    ) * (hourly_overtime_hourly_pay / 60) 
                                else 0 
                            end as overtime_pay,
                            CASE 
                                WHEN FLOOR(TIME_TO_SEC(working_hrs) / 60) <= FLOOR(TIME_TO_SEC(hourly_overtime_working_hour) / 60) 
                                THEN (FLOOR(TIME_TO_SEC(working_hrs) / 60) * (salary / 60))
                                WHEN FLOOR(TIME_TO_SEC(working_hrs) / 60) > FLOOR(TIME_TO_SEC(hourly_overtime_working_hour) / 60) and TIME_TO_SEC(hourly_overtime_working_hour) > 0
                                THEN (FLOOR(TIME_TO_SEC(hourly_overtime_working_hour) / 60) * (salary / 60))
                                ELSE (FLOOR(TIME_TO_SEC(working_hrs) / 60) * (salary / 60)) 
                            END as hourly_pay,
                            salary,
                            bank_details,
                            CASE WHEN MOD(p_cnt, 2) <> 0 THEN 'Problem' END AS error_state
                        FROM (
                            SELECT 
                                j.ho_id,
                                j.attandance_date,
                                j.user_id,
                                j.is_approved_ot, 
                                COALESCE(
                                    ep1.hourly_overtime_working_hour,
                                    oc1.hourly_overtime_working_hour
                                ) AS hourly_overtime_working_hour,
                                COALESCE(
                                    ep1.hourly_overtime_hourly_pay,
                                    oc1.hourly_overtime_hourly_pay
                                ) AS hourly_overtime_hourly_pay,
                                COALESCE(
                                    ep1.hourly_overtime_buffer_period,
                                    oc1.hourly_overtime_buffer_period
                                ) AS hourly_overtime_buffer_period,
                                (case when tag = 5 then 'Overwrite' end) as tag,
                                MAX(CASE 
                                    WHEN tag = 5 THEN overwrite 
                                    ELSE working_hrs 
                                END) AS working_hrs,    
                                MAX(break_hrs) AS break_hrs,
                                SUM(p_cnt) AS p_cnt,
                                j.overwrite,
                                es.salary,
                                es.bank_details
                            FROM journal_entries j
                            LEFT JOIN emp_wise_config ep1 ON j.user_id = ep1.employee_id AND j.ho_id = ep1.ho_id
                            JOIN org_wise_config oc1 ON j.ho_id = oc1.ho_id
                            JOIN employee_salaries es on es.ho_id = ${ho_id} and es.emp_id = j.user_id and es.salary_type = 2
                            WHERE j.ho_id = ${ho_id} AND j.attandance_date >= ? AND j.attandance_date <= ?
                            and tag > 0 
                                GROUP BY 1,2,3
                        ) a
                    ) b
                    join employees emp on b.user_id = emp.id
                    WHERE b.ho_id = ${ho_id}  and emp.ho_id = ${ho_id} 
                    GROUP BY b.ho_id, b.user_id
                ),

                extra_col as
                (
                SELECT
                    ho_id,
                    employee_id, 
                    sum(case when adjustment_type = 1 and  type = 1 then amount else 0 end) as extra_amount,
                    sum(case when adjustment_type = 3 and  type = 2 then amount else 0 end) as penalty_extra
                FROM payroll_extras
                WHERE 
                    ho_id = ${ho_id}
                    and status = 1 and type IN (1,2)
                    and adjustment_type in (1,3)
                    and adjustment_date >= ?
                    and adjustment_date <= ?
                GROUP BY ho_id,employee_id
                ),

                loan_details as
                (
                SELECT 
                    ho_id,
                    employee_id,
                    SUM(case when extra_amount < remaining_amount and extra_amount is not null and extra_amount > 0 then extra_amount when installment_amt > 0 and remaining_amount > installment_amt then installment_amt else remaining_amount end) as final_amount
                FROM
                (
                SELECT 
                    lap.ho_id,
                    lap.employee_id,
                    lap.id,
                    lap.installment_amt,
                    lap.type,
                    MAX(lap.credit) as main_amount,
                    SUM(lap2.debit) as deducted_amount,
                    ifnull(MAX(lap.credit),0) - ifnull(SUM(lap2.debit),0) as remaining_amount,
                    SUM(pe.amount) as extra_amount
                FROM 
                    loan_advance_payments lap 
                    left join loan_advance_payments lap2 on lap.id = lap2.reference_id and lap2.status = 1
                    left join payroll_extras pe on pe.ho_id = ${ho_id} and lap.id = pe.loan_id AND pe.status = 1 and pe.type = 2 and pe.adjustment_type = 2
                WHERE
                    lap.ho_id = ${ho_id} AND
                    lap.transaction_date <= ? AND
                    lap.status = 1 AND
                    lap.reference_id = 0
                    AND lap.type = 1
                    AND pe.id is null
                Group by 1,2,3,4
                    ) a
                    GROUP BY 1,2
                ),

                advance_details as
                (
                SELECT 
                    ho_id,
                    employee_id,
                    SUM(case when extra_amount < remaining_amount and extra_amount is not null and extra_amount > 0 then extra_amount when installment_amt > 0 and remaining_amount > installment_amt then installment_amt else remaining_amount end) as final_amount_advance
                FROM
                (
                SELECT 
                    lap.ho_id,
                    lap.employee_id,
                    lap.id,
                    lap.installment_amt,
                    lap.type,
                    MAX(lap.credit) as main_amount,
                    SUM(lap2.debit) as deducted_amount,
                    ifnull(MAX(lap.credit),0) - ifnull(SUM(lap2.debit),0) as remaining_amount,
                    (
                        SELECT SUM(pe.amount)
                        FROM payroll_extras pe
                        JOIN loan_advance_payments lap_status_check 
                            ON pe.loan_id = lap_status_check.id 
                            AND lap_status_check.status = 1
                        WHERE pe.ho_id = lap.ho_id
                            AND pe.loan_id = lap.id
                            AND pe.status = 1 
                            AND pe.type = 2 
                            AND pe.adjustment_type = 2
                    ) AS extra_amount
                FROM 
                    loan_advance_payments lap 
                    left join loan_advance_payments lap2 on lap.id = lap2.reference_id and lap2.status = 1
                    LEFT JOIN (
                        SELECT loan_id
                        FROM payroll_extras 
                        WHERE ho_id = ${ho_id} AND status = 1 AND type = 2 AND adjustment_type = 2
                        GROUP BY loan_id
                    ) extra ON lap.id = extra.loan_id
                WHERE
                    lap.ho_id = ${ho_id} AND
                    lap.transaction_date <= ? AND
                    lap.status = 1 AND
                    lap.reference_id = 0
                    AND (extra.loan_id > 0 OR lap.type = 2)
                Group by 1,2,3,4
                    ) a
                WHERE 
                    extra_amount IS NULL 
                    OR extra_amount > 0
                GROUP BY 1,2
                )

                SELECT
                    sd.ho_id,
                    sd.user_id,
                    sd.bank_details,
                    sd.eligible_for_overtime,
                    emp.code,
                    emp.name,
                    emp.device_employee_id,
                    emp.status as employee_status,
                    emp.tag_updated_at,
                    es.bank_details,
                    d.name as department,
                    des.name as designation,
                    org_names.org_names,
                    SUM(total_working_hrs_minutes) as total_working_hrs_minutes,
                    SUM(standard_working_hours) as standard_working_hours,
                    MAX(hourly_overtime_working_hour) as hourly_overtime_working_hour,
                    MAX(hourly_overtime_buffer_period) as hourly_overtime_buffer_period,
                    MAX(hourly_overtime_hourly_pay) as hourly_overtime_hourly_pay,
                    SUM(over_time_minutes) as over_time_minutes,
                    SUM(overtime_pay) as overtime_pay,
                    SUM(hourly_pay) as hourly_pay,
                    MAX(hourly_rate) as hourly_rate,
                    extra.extra_amount,
                    extra.penalty_extra,
                    ld.final_amount as loan_amount,
                    ad.final_amount_advance as advance_amount,
                    (COALESCE(hourly_rate, 0) / 60) * COALESCE(standard_working_hours, 0) AS gross_wage,
                    DATEDIFF(?, ?) + 1 AS selected_days,
                    DAY(LAST_DAY(?)) AS total_days_in_month,
                    0 AS payroll_id
                FROM
                    salary_data sd
                    JOIN employees emp on emp.id = sd.user_id and emp.ho_id = ${ho_id} and emp.status > 0 ${department_condition}
                    LEFT JOIN extra_col extra on extra.ho_id = sd.ho_id and sd.user_id = extra.employee_id
                    LEFT JOIN loan_details ld on ld.ho_id = sd.ho_id and ld.employee_id = sd.user_id
                    LEFT JOIN advance_details ad on ad.ho_id = sd.ho_id and ad.employee_id = sd.user_id
                    LEFT JOIN departments d on emp.department_id = d.id
                    LEFT JOIN designations des on des.id = emp.designation_id
                    LEFT JOIN employee_salaries es on es.ho_id = sd.ho_id and es.emp_id = emp.id
                    JOIN (
                        SELECT eb.employee_id, GROUP_CONCAT(DISTINCT org.name ORDER BY org.name ASC SEPARATOR ', ') AS org_names
                        FROM employee_branches eb
                        LEFT JOIN organizations org ON org.id = eb.branch_id
                        WHERE
                        eb.ho_id = ${ho_id} and eb.status = 1 ${user_organizations_condition} 
                        GROUP BY eb.employee_id
                    ) org_names ON org_names.employee_id = emp.id
                GROUP BY sd.ho_id, sd.user_id, emp.name, emp.code;
            `;

            parameters = [start_date, end_date, start_date, end_date, end_date, end_date, end_date, start_date, start_date];

            const connection = await db2.getConnection();      
            try {
                const [row] = await connection.query(query, parameters);

                return row;
            } catch (err) {
                throw err;
            } finally {
                await connection.destroy();
            }

        }
    }
}

