import React, { useEffect, useState, useRef } from 'react';
import { Card, CardBody, Col, Container, Input, Label, Row, Button, Form, FormFeedback, Alert, Spinner } from 'reactstrap';
import logo2 from "../../assets/images/loginLogo.svg";
import GoogleIcon from "../../assets/images/svgIcons/google.svg";
import appleIcon from "../../assets/images/svgIcons/apple.svg";

//redux
import { useSelector, useDispatch } from "react-redux";

import { Link, useLocation } from "react-router-dom";

// Formik validation
import * as Yup from "yup";
import { useFormik } from "formik";


import { loginPassword, socialLogin, resetLoginFlag, apiError } from "../../store/actions";


import withRouter from '../../Components/Common/withRouter';

import { toast, ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import ContactUs from '../../Components/Common/ContactUs';
import GoogleSignIn from './GoogleSignIn';
import { useMutation } from '@tanstack/react-query';
import { VerifyEmailOrMobile } from '../../helpers/awsapibackend_helper';
import ReCAPTCHA from 'react-google-recaptcha';
import { Loginvector } from '../../Components/Common/AllSvgIcons';

const Login = (props) => {
    const dispatch = useDispatch();
    const location = useLocation();
    const [userLogin, setUserLogin] = useState([]);
    const [recaptchaValue, setRecaptchaValue] = useState(null);
    const [wrongEmailCount, setWrongEmailCount] = useState(0);

    const onCaptchaChange = (value) => {
        setRecaptchaValue(value);
    };

    const { user, errorMsg, loading, error, oAuthErrorMsg, oAuthError, oAuthLoading } = useSelector(state => ({
        user: state.Account.user,
        errorMsg: state.Login.errorMsg,
        loading: state.Login.loading,
        error: state.Login.error,
        oAuthErrorMsg: state.Login.oAuthErrorMsg,
        oAuthError: state.Login.oAuthError,
        oAuthLoading: state.Login.oAuthLoading
    }));

    const {mutate: verifyEmailMobileMutate, isPending} = useMutation({
        mutationFn: VerifyEmailOrMobile,
        onSuccess: (data, variables) => {
            if (data?.statusCode === 200) {
                setWrongEmailCount(0);
                sessionStorage.setItem('wrongEmailCount', 0);
                setRecaptchaValue(null);

                const updatedValues = {
                    ...variables,
                    token: data?.token
                };
                dispatch(loginPassword(updatedValues, props.router.navigate));
            }else{
                if(wrongEmailCount > 1){
                    setWrongEmailCount(0);
                    sessionStorage.setItem('wrongEmailCount', 0);
                    setRecaptchaValue(null);
                }else{
                    setWrongEmailCount((prev) => prev + 1);
                    sessionStorage.setItem('wrongEmailCount', wrongEmailCount + 1);
                    setRecaptchaValue(null);
                }

                toast.error(data?.message);
            }
        },
        onError: (error) => {
            toast.error(error);
        }
      });

    useEffect(() => {
        if (user && user) {
            setUserLogin({
                email: user.user.email
            });
        }
    }, [user]);

    const validation = useFormik({
        // enableReinitialize : use this flag when initial values needs to be changed
        enableReinitialize: true,

        initialValues: {
            email: userLogin.email || location.state || ''
        },
        validationSchema: Yup.object({
            email: Yup.string()
            .required("Please enter a valid email ID or mobile number.")
            .test(
                'email-or-phone',
                'Please enter a valid email ID or a 10-digit mobile number.',
                function (value) {
                    const phoneRegex = /^(\+91)?\d{10}$/;

                    const emailRegex = /^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i;

                    if (value.includes('@')) {
                        return emailRegex.test(value);
                    }

                    // Otherwise, validate as a phone number
                    return phoneRegex.test(value);
                }
            )
        }),
        onSubmit: (values) => {
            const phoneRegex = /^(\+91)?\d{10}$/;

            if(phoneRegex.test(values.email)){
                verifyEmailMobileMutate({
                    "phone_code": values.email.slice(0,3),
                    "mobile_no": values.email.slice(3),
                });
            }else{
                verifyEmailMobileMutate({
                    "email": values.email
                });
            }
        }
    });


    const handleEmailChange = (e) => {
        const value = e.target.value;

        if (/^\d{0,10}$/.test(value)) {
            validation.setFieldValue('email', value.length === 10 ? `+91${value}` : value);
        }
        else {
            validation.setFieldValue('email', value);
        }

    }

    const signIn = (res, type) => {
        if (type === "google" && res) {
            const postData = {
                name: res.profileObj.name,
                email: res.profileObj.email,
                token: res.tokenObj.access_token,
                idToken: res.tokenId,
            };
            dispatch(socialLogin(postData, props.router.navigate, type));
        } else if (type === "facebook" && res) {
            const postData = {
                name: res.name,
                email: res.email,
                token: res.accessToken,
                idToken: res.tokenId,
            };
            dispatch(socialLogin(postData, props.router.navigate, type));
        }
    };

    //handleGoogleLoginResponse
    const googleResponse = response => {
        signIn(response, "google");
    };

    useEffect(() => {
        if (error) {
            setTimeout(() => {
                dispatch(resetLoginFlag());
            }, 3000);
        }
    }, [dispatch, error]);

    let formikRef = useRef(null);
    useEffect(() => {
        if (formikRef.current) {
            formikRef.current.focus();
        }

        if(sessionStorage.getItem('is_fl')) {
            toast.success("You've been logged out! please log back in");
            sessionStorage.removeItem('is_fl');
        }
    }, [formikRef]);


    document.title = "Petpooja Payroll";
    return (
        <React.Fragment>
            <Container fluid className='ps-login-bgcl'>
                <Row className='pcbg-cl'>
                    <Col sm={12} className='ps-image-set'><img src={logo2} alt="" className='login-logo' /></Col>
                    <Col sm={9} md={11} lg={10} xl={10} className='m-auto'>
                        <Row >
                            <Col md={6} lg={7} xl={6}  className='ps-login-leftsvg mx-auto'>
                                <Loginvector/>
                                <div className='ps-welcm-text'>
                                    <h2>Welcome!</h2>
                                    <p>Managing Employee activities & attendance made <br /> simple with Petpooja</p>
                                </div>
                            </Col>
                            <Col sm={10} md={6} lg={5} xl={4} className=' mx-auto' >
                                <div className="p-4 pt-3 login-form ">
                                    <div className='d-flex justify-content-end mb-3'>
                                        <div className="progress-bar">
                                            <div className="progress" style={{ width: "30px" }}></div>
                                        </div>
                                        <div className="progress-bar ms-2">
                                            <div className="progress" style={{ width: "0px" }}></div>
                                        </div>
                                    </div>
                                    <div className='px-2'>
                                        <h5 className='fw-600'>Login to Dashboard</h5>
                                        <p className='text-mute mt-3'>Hello there, Let’s get started.</p>
                                    </div>
                                    <div className="p-2 mt-4">
                                        <Form
                                            onSubmit={(e) => {
                                                e.preventDefault();
                                                validation.handleSubmit();
                                                return false;
                                            }}
                                            action="#">

                                            <div className="mb-3">
                                                <Label htmlFor="email" className="form-label ">Email ID Or Mobile Number</Label>
                                                <Input
                                                    name="email"
                                                    className="form-control"
                                                    placeholder="Enter Email ID Or Mobile Number"
                                                    type="text"
                                                    onChange={(e)=>{handleEmailChange(e)}}
                                                    // onBlur={validation.handleBlur}
                                                    value={validation.values.email || ""}
                                                    innerRef={formikRef}
                                                    invalid={
                                                        validation.touched.email && validation.errors.email ? true : false
                                                    }
                                                />
                                                {validation.touched.email && validation.errors.email ? (
                                                    <FormFeedback type="invalid">{validation.errors.email}</FormFeedback>
                                                ) : null}

                                                <div className="mt-1" style={{ textAlign: 'right' }}>
                                                    <Link to="/forgot-password" className="ext-primary text-decoration-underline text ps-for-text"> Forgot Password? </Link>
                                                </div>
                                            </div>

                                            {wrongEmailCount > 1 && <div className="recaptcha-container mt-2">
                                                <ReCAPTCHA
                                                    sitekey={process.env.REACT_APP_CAPTCHA_SITE_KEY}
                                                    onChange={onCaptchaChange}
                                                />
                                            </div>}

                                            <div className="my-2">
                                                <Button disabled={(wrongEmailCount > 1 && !recaptchaValue) ? true : isPending ? true : false} className="ps-btn md-btn primary-btn w-100" type="submit">
                                                    {isPending ? <Spinner size="sm" className='me-2'> Loading... </Spinner> : null}
                                                    Next
                                                </Button>
                                            </div>

                                            <div className="mt-1 text-center">
                                                <div className="signin-other-title">
                                                    <h5 className="fs-13 title text-muted mb-2">Or</h5>
                                                </div>
                                            </div>
                                            <div className='mb-2'>
                                                <GoogleSignIn error={oAuthError} loading={oAuthLoading} />
                                            </div>
                                        </Form>
                                    </div>

                                    <div className='ps-term-cond'>
                                        <p><Link to="https://www.petpooja.com/payroll/terms-and-conditions" target={"_blank"}> Terms & Condition</Link> | <Link to="https://www.petpooja.com/payroll/privacy-policy" target={"_blank"}> Privacy Policy </Link></p>
                                    </div>

                                    <div className="mt-2 text-center d-none">
                                        <p className="fw-400 mb-0 tx-black">{"Don't have an account?"}  <Link to="/register" className="fw-semibold text-primary text-decoration-underline pl-10 fs-5 text"> Signup </Link> </p>
                                    </div>
                                </div>
                                <ContactUs />
                            </Col>
                        </Row>

                    </Col>
                </Row>
            </Container>
        </React.Fragment>

    );
};

export default withRouter(Login)

