            dispatch(socialLogin(postData, props.router.navigate, type));
        }
    };

    //handleGoogleLoginResponse
    const googleResponse = response => {
        signIn(response, "google");
    };

    useEffect(() => {
        if (error) {
            setTimeout(() => {
                dispatch(resetLoginFlag());
            }, 3000);
        }
    }, [dispatch, error]);

    let formikRef = useRef(null);
    useEffect(() => {
        if (formikRef.current) {
            formikRef.current.focus();
        }

        if(sessionStorage.getItem('is_fl')) {
            toast.success("You've been logged out! please log back in");
            sessionStorage.removeItem('is_fl');
        }
    }, [formikRef]);


    document.title = "Petpooja Payroll";
    return (
        <React.Fragment>
            <Container fluid className='ps-login-bgcl'>
                <Row className='pcbg-cl'>
                    <Col sm={12} className='ps-image-set'><img src={logo2} alt="" className='login-logo' /></Col>
                    <Col sm={9} md={11} lg={10} xl={10} className='m-auto'>
                        <Row >
                            <Col md={6} lg={7} xl={6}  className='ps-login-leftsvg mx-auto'>
                                <Loginvector/>
                                <div className='ps-welcm-text'>
                                    <h2>Welcome!</h2>
                                    <p>Managing Employee activities & attendance made <br /> simple with Petpooja</p>
                                </div>
                            </Col>
                            <Col sm={10} md={6} lg={5} xl={4} className=' mx-auto' >
                                <div className="p-4 pt-3 login-form ">
                                    <div className='d-flex justify-content-end mb-3'>
                                        <div className="progress-bar">
                                            <div className="progress" style={{ width: "30px" }}></div>
                                        </div>
                                        <div className="progress-bar ms-2">
                                            <div className="progress" style={{ width: "0px" }}></div>
                                        </div>
                                    </div>
                                    <div className='px-2'>
                                        <h5 className='fw-600'>Login to Dashboard</h5>
                                        <p className='text-mute mt-3'>Hello there, Let’s get started.</p>
                                    </div>
                                    <div className="p-2 mt-4">
                                        <Form
                                            onSubmit={(e) => {
                                                e.preventDefault();
                                                validation.handleSubmit();
                                                return false;
                                            }}
                                            action="#">

                                            <div className="mb-3">
                                                <Label htmlFor="email" className="form-label ">Email ID Or Mobile Number</Label>
                                                <Input
                                                    name="email"
                                                    className="form-control"
                                                    placeholder="Enter Email ID Or Mobile Number"
                                                    type="text"
                                                    onChange={(e)=>{handleEmailChange(e)}}
                                                    // onBlur={validation.handleBlur}
                                                    value={validation.values.email || ""}
                                                    innerRef={formikRef}
                                                    invalid={
                                                        validation.touched.email && validation.errors.email ? true : false
                                                    }
                                                />
                                                {validation.touched.email && validation.errors.email ? (
                                                    <FormFeedback type="invalid">{validation.errors.email}</FormFeedback>
                                                ) : null}

                                                <div className="mt-1" style={{ textAlign: 'right' }}>
                                                    <Link to="/forgot-password" className="ext-primary text-decoration-underline text ps-for-text"> Forgot Password? </Link>
                                                </div>
                                            </div>

                                            {wrongEmailCount > 1 && <div className="recaptcha-container mt-2">
                                                <ReCAPTCHA
                                                    sitekey={process.env.REACT_APP_CAPTCHA_SITE_KEY}
                                                    onChange={onCaptchaChange}
                                                />
                                            </div>}

                                            <div className="my-2">
                                                <Button disabled={(wrongEmailCount > 1 && !recaptchaValue) ? true : isPending ? true : false} className="ps-btn md-btn primary-btn w-100" type="submit">
                                                    {isPending ? <Spinner size="sm" className='me-2'> Loading... </Spinner> : null}
                                                    Next
                                                </Button>
                                            </div>

                                            <div className="mt-1 text-center">
                                                <div className="signin-other-title">
                                                    <h5 className="fs-13 title text-muted mb-2">Or</h5>
                                                </div>
                                            </div>
                                            <div className='mb-2'>
                                                <GoogleSignIn error={oAuthError} loading={oAuthLoading} />
                                            </div>
                                        </Form>
                                    </div>

                                    <div className='ps-term-cond'>
                                        <p><Link to="https://www.petpooja.com/payroll/terms-and-conditions" target={"_blank"}> Terms & Condition</Link> | <Link to="https://www.petpooja.com/payroll/privacy-policy" target={"_blank"}> Privacy Policy </Link></p>
                                    </div>

                                    <div className="mt-2 text-center d-none">
                                        <p className="fw-400 mb-0 tx-black">{"Don't have an account?"}  <Link to="/register" className="fw-semibold text-primary text-decoration-underline pl-10 fs-5 text"> Signup </Link> </p>
                                    </div>
                                </div>
                                <ContactUs />
                            </Col>
                        </Row>

                    </Col>
                </Row>
            </Container>
        </React.Fragment>

    );
};

export default withRouter(Login)

