// Auto voice test
import express from "express"
import cors from "cors";
import path from "path";
import dotenv from "dotenv";
import cookieParser from "cookie-parser";
import authRoutes from "./api/auth.route.js";
import organizationRoutes from "./api/organization.route.js";
import reportsRoutes from "./api/reports.route.js";
import shiftsRoutes from "./api/shifts.route.js";
import employeesShiftsRoutes from "./api/employeesShifts.route.js";
import notificationsRoutes from "./api/notifications.route.js"
import https from "http";
import fs from "fs";
import useragent from "express-useragent";
import employeesRoutes from "./api/employees.route.js"


// load env file as per staging , local and live start
var get_env_path = process.env.NODE_ENV;
if (get_env_path !== undefined && get_env_path != "") {
  var env_path = ".env." + process.env.NODE_ENV;
} else {
  var env_path = ".env";
}
dotenv.config({ path: env_path, override: true });
// load env file as per staging , local and live end

const app = express();
app.use(cors());
app.use(cookieParser());
app.use(express.json({ limit: "5mb" })); // Set limit for JSON payload
app.use(useragent.express());
app.use(function (req, res, next) {
  res.header("Access-Control-Allow-Origin", "*");
  res.header("X-Frame-Options", "DENY");
  res.header(
      "Access-Control-Allow-Methods",
      "GET, POST, OPTIONS, PUT, PATCH, DELETE"
  );
  res.header(
      "Access-Control-Allow-Headers",
      "x-access-token, Origin, X-Requested-With, Content-Type, Accept"
  );
  next();
});

const port = process.env.PORT || 3000;
const __dirname = path.resolve();

var build_folder = (process.env.IS_MT !== undefined && process.env.IS_MT === '1') ? "build_mt" : "build";

app.use(express.static(path.join(__dirname, "../", build_folder)));
app.use("/api/auth", authRoutes);
app.use("/api/organizations/", organizationRoutes)
app.use("/api/reports/", reportsRoutes)
app.use("/api/shifts/", shiftsRoutes)
app.use("/api/employees_shifts/", employeesShiftsRoutes)
app.use("/api/notification/", notificationsRoutes)
app.use("/api/employees/", employeesRoutes)


app.get("*", (req, res) => {
    res.sendFile(path.join(__dirname, "../"+build_folder, "index.html"));
});

if(process.env.NODE_ENV !== undefined && process.env.NODE_ENV === 'production') {
  var key = fs.readFileSync('/etc/ssl/petpooja/petpooja.key');
  var cert = fs.readFileSync('/etc/ssl/petpooja/STAR_petpooja_com.crt');
  var options = {
    key: key,
    cert: cert
  };

  var server = https.createServer(options, app);

  server.listen(port, () => {
    console.log(`Server listening on port ${port}`);
  });
} else {
  app.listen(port, () => {
    console.log(`App listening on port ${port}`);
  });
}

