+  // Optimized DOM observer to apply colors when tags are added
+  useEffect(() => {
+    if (!showSpreadsheet) return;
+    
+    // Debounce function to avoid excessive color applications
+    let colorUpdateTimeout = null;
+    const debouncedColorUpdate = () => {
+      if (colorUpdateTimeout) clearTimeout(colorUpdateTimeout);
+      colorUpdateTimeout = setTimeout(() => {
+        applyShiftColors();
+      }, 100);
+    };
+    
+    // Optimized mutation detection
+    const observer = new MutationObserver((mutations) => {
+      let shouldUpdate = false;
+      
+      // Check for .jss_dropdown_tag elements more efficiently
+      for (let i = 0; i < mutations.length; i++) {
+        const mutation = mutations[i];
+        
+        // Most efficient case - direct class changes on a tag
+        if (mutation.type === 'attributes' && 
+            mutation.attributeName === 'class' && 
+            mutation.target.classList?.contains('jss_dropdown_tag')) {
+          shouldUpdate = true;
+          break;
+        }
+        
+        // Check added nodes (avoid unnecessary Array.from for performance)
+        if (mutation.addedNodes.length) {
+          for (let j = 0; j < mutation.addedNodes.length; j++) {
+            const node = mutation.addedNodes[j];
+            
+            // Only process Element nodes
+            if (node.nodeType === 1) { // Node.ELEMENT_NODE === 1
+              if (node.classList?.contains('jss_dropdown_tag') || 
+                  (node.querySelector && node.querySelector('.jss_dropdown_tag'))) {
+                shouldUpdate = true;
+                break;
+              }
+            }
+          }
+        }
+        
+        if (shouldUpdate) break;
+      }
+      
+      if (shouldUpdate) {
+        debouncedColorUpdate();
+      }
+    });
+    
+    // Configure the observer with more precise targeting
+    const config = {
+      childList: true,   // Watch for children being added/removed
+      subtree: true,     // Watch all descendants
+      attributes: true,  // Watch attributes for class changes
+      attributeFilter: ['class'], // Only watch class attribute changes
+      characterData: false
+    };
+    
+    // Target specific spreadsheet container for better performance
+    const targetNode = document.querySelector('.jss_content') || document.body;
+    observer.observe(targetNode, config);
+    
+    // Apply colors when component mounts
+    const initialColorTimeout = setTimeout(() => {
+      applyShiftColors();
+    }, 300); // Delay to ensure DOM is fully rendered
+    
+    // Reapply colors on window resize (for responsive layouts)
+    const handleResize = () => {
+      debouncedColorUpdate();
+    };
+    window.addEventListener('resize', handleResize);
+    
+    // Cleanup function
+    return () => {
+      observer.disconnect();
+      if (colorUpdateTimeout) clearTimeout(colorUpdateTimeout);
+      clearTimeout(initialColorTimeout);
+      window.removeEventListener('resize', handleResize);
+    };
