        {/* Input for Shift Name */}
        <div className={`ps-form-group ${formik.errors[`shift_name_0`] ? 'inp-warning' : ''} ps-warn-col p-3`}>
            <label htmlFor="shiftname">Shift Name</label>
            <input
                name={`shift_name_0`}
                placeholder="Write Shift Name Here"
                type="text"
                className={`form-control ps-rounded-7 ${formik.errors[`shift_name_0`] ? 'warning' : ''}`}
                value={formik.values[`shift_name_0`] || ''}
                onChange={formik.handleChange}
            />
            {formik.errors[`shift_name_0`] && <p>{formik.errors[`shift_name_0`]}</p>}
        </div>

        {/* Common Time Pickers with Checkbox */}
        {renderCommonTimePickers(0)}

        {/* Day-wise Time Settings (only shown when checkbox is unchecked) */}
        {renderShiftDayRows(0)}

        <div className="ps-form-group p-3">
            <label>Choose Shift Color</label>
            <Library1Dropdown
                value={selectedColor[0] || 'no color'}
                options={colorOptions}
                onChange={(e) => {
                    setSelectedColor(prev => ({
                        ...prev,
                        0: e.value
                    }));
                    formik.setFieldValue(`shift_color_0`, e.value);
                }}
                optionLabel="label"
                placeholder="Select Color"
                className="w-100 ps-check-drop"
                itemTemplate={(option) => (
                    <div className="d-flex align-items-center">
                        <div style={{
                            backgroundColor: option.value,
                            width: '20px',
                            height: '20px',
                            borderRadius: '4px',
                            marginRight: '8px',
                            border: '1px solid #ccc',
                            flexShrink: 0
                        }}></div>
                        <span>{option.label}</span>
                    </div>
                )}
                valueTemplate={(option) => {
                    if (!option) return null;
                    return (
                        <div className="d-flex align-items-center">
                            <div style={{
                                backgroundColor: option.value,
                                width: '20px',
                                height: '20px',
                                borderRadius: '4px',
                                marginRight: '8px',
                                border: '1px solid #ccc',
                                flexShrink: 0,
                                display: 'inline-block'
                            }}></div>
                            <span>{option.label}</span>
                        </div>
                    );
                }}
            />
        </div>

        <div className="p-3 ps-form-group">
            <label htmlFor="from">Remark </label>
            <textarea
                disabled={isReadOnly}
                className="form-control"
                rows="5"
                placeholder="Write your remarks or comments here..."
                id={`shift_description_${0}`}
                name={`shift_description_${0}`}
                value={formik.values[`shift_description_${0}`] || ''}
                onChange={formik.handleChange}
            />
        </div>
    </>
)}

