import { call, put, takeEvery, takeLatest, all, fork } from "redux-saga/effects"
import { toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

// employees Redux States
import {
    GET_SHIFTWISE_EMPLOYEES, GET_SHIFT_DESIG_GROUP, SAVE_EMPLOYEES_SHIFT_MAPPING,GET_EMPLOYEE_DESIGNATIONS,SAVE_STAFF_REQUIREMENT_REQUEST,
    SAVE_STAFF_REQUIREMENT_SUCCESS,
    SAVE_STAFF_REQUIREMENT_FAILURE,
    GET_STAFF_REQUIREMENT,
    ASSIGN_SHIFT_REQUEST, ASSIGN_SHIFT_SUCCESS, ASSIGN_SHIFT_FAILURE,
    GET_EMP_WITH_SHIFT_FAILURE, GET_EMP_WITH_SHIFT_SUCCESS, GET_EMP_WITH_SHIFT_REQUEST,
    GET_EMP_WITH_WEEKOFF_FAILURE, GET_EMP_WITH_WEEKOFF_SUCCESS, GET_EMP_WITH_WEEKOFF_REQUEST,
    SAVE_EMPLOYEE_WEEKOFF_REQUEST, SAVE_EMPLOYEE_WEEKOFF_SUCCESS, SAVE_EMPLOYEE_WEEKOFF_FAILURE,
  } from "./actionTypes";

import {
    empShiftsApiResponseSuccess,
    empShiftsApiResponseError,
    saveStaffRequirementSuccess,
    saveStaffRequirementFailure,
} from "./actions";  

// import { 
//     getShiftwiseEmployees as getShiftEmployeesApi,
//     saveEmployeeShiftMapping as saveEmployeeShiftMappingApi,
//     getShiftDesigGroup as getShiftDesigGroupApi
// } from "../../helpers/nodebackend_helper";

import {
    getShiftwiseEmployees as getShiftEmployeesApi,
    saveEmployeeShiftMapping as saveEmployeeShiftMappingApi,
    getShiftDesigGroup as getShiftDesigGroupApi,
    getEmployeeDesignations as getEmployeeDesignationsApi,
    saveStaffRequirement as saveStaffRequirementAPI,
    getStaffRequirement as getStaffRequirementAPI,
    assignShiftApi,
    saveEmployeeWeekoffApi,
    getEmployees as getEmployeesApi,
} from "../../helpers/awsapibackend_helper"

function* getShiftEmployees({ payload: day }) {
    try {
        if (process.env.REACT_APP_API_URL) {
            const response = yield call(getShiftEmployeesApi, {
                //'org_id' : sessionStorage.getItem('organization_id'),
                'request_day' : day
            });
            yield put(empShiftsApiResponseSuccess(GET_SHIFTWISE_EMPLOYEES, response.data));
        }
    } catch (error) {
        yield put(empShiftsApiResponseError(GET_SHIFTWISE_EMPLOYEES, error));
    }
}

function* getShiftDesigGroupEmp({ payload : day }) {
    try {
        if (process.env.REACT_APP_API_URL) {
            const response = yield call(getShiftDesigGroupApi, {
                //'org_id' : sessionStorage.getItem('organization_id'),
                'request_day' : day
            });
            yield put(empShiftsApiResponseSuccess(GET_SHIFT_DESIG_GROUP, response.data));
        }
    } catch (error) {
        yield put(empShiftsApiResponseError(GET_SHIFT_DESIG_GROUP, error));
    }
}

function* saveEmployeeShiftMapping({ payload: {day, additions, deletions, addWeekoff, removeWeekoff, assignedShifts, defaultShiftId, employeeNames} }) {
    try {

        const obj = JSON.parse(sessionStorage.getItem("authUser"));

        if (process.env.REACT_APP_API_URL) {
            const response = yield call(saveEmployeeShiftMappingApi, {
                //'org_id' : sessionStorage.getItem('organization_id'),
                'request_day' : day,
                'ad' : additions,
                'del' : deletions,
                'add_weekoff' : addWeekoff,
                'remove_weekoff' : removeWeekoff,
                'assigned_shifts' : assignedShifts,
                'default_shift_id' : defaultShiftId,
                'employee_names' : employeeNames,
                'username' : obj.data.username,
            });

            sessionStorage.setItem('isResetShiftOldList',1)
            if(response.status === 0) {
                yield put(empShiftsApiResponseSuccess(SAVE_EMPLOYEES_SHIFT_MAPPING, response.data));
                toast.error(response.message);
            } else {
                yield put(empShiftsApiResponseSuccess(SAVE_EMPLOYEES_SHIFT_MAPPING, response.data));
            }
            // toast.success("Shift Assignment Saved Successfully"  );
        }
    } catch (error) {
        yield put(empShiftsApiResponseError(SAVE_EMPLOYEES_SHIFT_MAPPING, error));
        toast.error("Problem Saving Data"  );
    }
}

function* getEmployeeDesignations() {
    try {
        if (process.env.REACT_APP_AWS_API_URL) {
            const response = yield call(getEmployeeDesignationsApi, {
                //'org_id' : sessionStorage.getItem('organization_id'),
                'method' : 'employee_designations'
            });
            yield put(empShiftsApiResponseSuccess(GET_EMPLOYEE_DESIGNATIONS, response.data));
        }
    } catch (error) {
        yield put(empShiftsApiResponseError(GET_EMPLOYEE_DESIGNATIONS, error));
    }
}

function* saveStaffRequirement({ payload: data }) {
    try {
        if (process.env.REACT_APP_API_URL) {

            //data.org_id = sessionStorage.getItem('organization_id');

            const response = yield call(saveStaffRequirementAPI, data);

            yield put(saveStaffRequirementSuccess(SAVE_STAFF_REQUIREMENT_SUCCESS, response.data));
            toast.success("Staff Requirement Saved Successfully"  );
        }
    } catch (error) {
        yield put(saveStaffRequirementFailure(SAVE_STAFF_REQUIREMENT_FAILURE, error));
        toast.error("Problem Saving Data"  );
    }
}

function* getStaffRequirement({ payload: {day, shiftId} }) {
    try {
        if (process.env.REACT_APP_API_URL) {

            const response = yield call(getStaffRequirementAPI, {
                //'org_id' : sessionStorage.getItem('organization_id'),
                'day' : day,
                'shift_id' : shiftId
            });

            yield put(empShiftsApiResponseSuccess(GET_STAFF_REQUIREMENT, response.data));
        }
    } catch (error) {
        yield put(empShiftsApiResponseError(GET_STAFF_REQUIREMENT, error));
    }
}

function* assignShift({ payload: {employeeIds, shiftIds, shiftNames} }) {

    try {
        if (process.env.REACT_APP_API_URL) {

            const obj = JSON.parse(sessionStorage.getItem("authUser"));

            const response = yield call(assignShiftApi, {
                'employeeIds' : employeeIds,
                'shiftIds' : shiftIds,
                'shiftNames' : shiftNames,
                'username' : obj.data.username,
            });

            if(response.status === 1) {
                sessionStorage.setItem('isResetEmployeeWithWeekoffList',1)
                sessionStorage.setItem('isResetShiftNewList',1)
                yield put({ type: ASSIGN_SHIFT_SUCCESS, payload: { data: response.data, employeeIds, shiftIds, shiftNames } });
                toast.success("Shift Assignment Saved Successfully");
            } else {
                yield put({ type: ASSIGN_SHIFT_FAILURE, error: response.message });
                toast.error(response.message);
            } 




        }   
    } catch (error) {
        yield put({ type: ASSIGN_SHIFT_FAILURE, error: error.message });
        toast.error("Problem Saving Data");

    }
}

function* getEmployeeWithShifts() {
    try {
        if (process.env.REACT_APP_AWS_API_URL) {
            const response = yield call(getEmployeesApi, {
                'method':'employee_list_with_shift'
            });
            yield put({ type: GET_EMP_WITH_SHIFT_SUCCESS, payload: response});
        }
    } catch (error) {
        yield put({ type: GET_EMP_WITH_SHIFT_FAILURE, error: error.message });
    }
}

function* getEmployeeWithWeekoffs() {
    try {
        if (process.env.REACT_APP_AWS_API_URL) {
            const response = yield call(getEmployeesApi, {
                'method':'employee_list_with_weekoff'
            });
            yield put({ type: GET_EMP_WITH_WEEKOFF_SUCCESS, payload: response});
        }
    } catch (error) {
        yield put({ type: GET_EMP_WITH_WEEKOFF_FAILURE, error: error.message });
    }
}

function* saveEmployeeWeekoff({ payload: {employeeId, employeeName, shiftIds, day, occasionalWeekoffDays} }) {

    try {
        if (process.env.REACT_APP_API_URL) {
            const obj = JSON.parse(sessionStorage.getItem("authUser"));

            const response = yield call(saveEmployeeWeekoffApi, {
                'employee_id' : employeeId,
                'employee_name' : employeeName,
                'shift_ids' : shiftIds,
                'day' : day,
                'occasional_weekoff_days':occasionalWeekoffDays,
                'username' : obj.data.username,
            });

            yield put({ type: SAVE_EMPLOYEE_WEEKOFF_SUCCESS, payload: { employeeId, day, occasionalWeekoffDays } });
            toast.success("Employee Weekoff Saved Successfully");

        }   
    } catch (error) {
        //console.log(error);
        yield put({ type: SAVE_EMPLOYEE_WEEKOFF_FAILURE, error: error.message });
        toast.error("Problem Saving Data");

    }
}

export function* watchGetComgetEmployees() {
    yield takeEvery(GET_SHIFTWISE_EMPLOYEES, getShiftEmployees);
    yield takeEvery(SAVE_EMPLOYEES_SHIFT_MAPPING, saveEmployeeShiftMapping);
    yield takeEvery(GET_SHIFT_DESIG_GROUP, getShiftDesigGroupEmp);
    yield takeEvery(GET_EMPLOYEE_DESIGNATIONS, getEmployeeDesignations);
    yield takeEvery(SAVE_STAFF_REQUIREMENT_REQUEST, saveStaffRequirement);
    yield takeEvery(GET_STAFF_REQUIREMENT, getStaffRequirement);
    yield takeLatest(ASSIGN_SHIFT_REQUEST, assignShift);  
    yield takeLatest(GET_EMP_WITH_SHIFT_REQUEST, getEmployeeWithShifts);  
    yield takeLatest(GET_EMP_WITH_WEEKOFF_REQUEST, getEmployeeWithWeekoffs);
    yield takeLatest(SAVE_EMPLOYEE_WEEKOFF_REQUEST, saveEmployeeWeekoff);

}


function* employeesShiftsSaga() {
    yield all([
        fork(watchGetComgetEmployees)
    ]);
}

  export default employeesShiftsSaga;

