          let ssdt = '0';

          if (getShiftModeData.length > 0) {
            for (const row of getShiftModeData) {
                if (row.key_name === 'shift_module_mode') {
                  smm = row.key_value;
                } else if (row.key_name === 'shift_switch_decision_taken') {
